// components/CustomerMap.js
import React, { useEffect, useRef } from 'react';
import { Loader } from '@googlemaps/js-api-loader';

/* global google */

const CustomerMap = ({ customers, route, onOptimizeRoute }) => {
  const mapRef = useRef(null);

  useEffect(() => {
    const loader = new Loader({
      apiKey: process.env.REACT_APP_GOOGLE_MAPS_API_KEY,
      version: "weekly",
    });

    loader.load().then(() => {
      const map = new google.maps.Map(mapRef.current, {
        center: { lat: 0, lng: 0 },
        zoom: 2,
      });

      const bounds = new google.maps.LatLngBounds();

      customers.forEach((customer, index) => {
        if (customer.address && customer.address.lat && customer.address.lng) {
          const position = new google.maps.LatLng(customer.address.lat, customer.address.lng);
          const marker = new google.maps.Marker({
            position: position,
            map: map,
            title: customer.name,
            label: (index + 1).toString(),
          });

          bounds.extend(position);
        }
      });

      map.fitBounds(bounds);
    });
  }, [customers]);

  return (
    <div>
      <div ref={mapRef} style={{ width: '100%', height: '400px' }}></div>
      <button 
        onClick={onOptimizeRoute}
        className="mt-2 bg-primary text-white px-4 py-2 rounded hover:bg-opacity-90"
      >
        Optimize Route
      </button>
    </div>
  );
};

export default CustomerMap;
