import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { PlusIcon } from '@heroicons/react/24/outline';
import Header from '../components/Header';

const Jobs = () => {
  const [job, setJob] = useState({
    title: '',
    description: '',
    customer: '',
    price: '',
    status: 'Pending',
    recurrence: 'One-time',
    scheduledDay: 'Monday', // New field
    estimatedDuration: '',
    location: {
      address: '',
      coordinates: []
    },
    cost: '',
    assignedTo: '',
    notes: ''
  });
  const [jobs, setJobs] = useState([]);
  const [customers, setCustomers] = useState([]);
  const [employees, setEmployees] = useState([]);
  const [services, setServices] = useState([]);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [loading, setLoading] = useState(false);
  const [showForm, setShowForm] = useState(false);

  useEffect(() => {
    fetchCustomers();
    fetchJobs();
    fetchEmployees();
    fetchServices();
  }, []);

  // ... (keep the existing fetch functions)

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (name.includes('.')) {
      const [parent, child] = name.split('.');
      setJob(prevJob => ({
        ...prevJob,
        [parent]: {
          ...prevJob[parent],
          [child]: value
        }
      }));
    } else {
      setJob({ ...job, [name]: value });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    setLoading(true);

    try {
      const response = await axios.post('http://localhost:5000/api/jobs', job, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setSuccess('Job created successfully!');
      setJob({
        title: '',
        description: '',
        customer: '',
        price: '',
        status: 'Pending',
        recurrence: 'One-time',
        scheduledDay: 'Monday',
        estimatedDuration: '',
        location: {
          address: '',
          coordinates: []
        },
        cost: '',
        assignedTo: '',
        notes: ''
      });
      fetchJobs();
      setShowForm(false);
    } catch (error) {
      console.error('Error creating job:', error.response?.data || error.message);
      setError(error.response?.data?.message || 'Failed to create job. Please try again.');
    }
    setLoading(false);
  };

  return (
    <div className="min-h-screen bg-background text-text">
      <Header />
      <main className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        {/* ... (keep the existing JSX) */}
        
        {showForm && (
          <div className="bg-surface shadow rounded-lg p-6 mb-6">
            <h2 className="text-xl font-semibold mb-4">Create New Job</h2>
            <form onSubmit={handleSubmit} className="space-y-4">
              {/* ... (keep the existing form fields) */}
              
              <div>
                <label htmlFor="scheduledDay" className="block text-sm font-medium">Scheduled Day</label>
                <select
                  id="scheduledDay"
                  name="scheduledDay"
                  value={job.scheduledDay}
                  onChange={handleChange}
                  className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary focus:ring focus:ring-primary focus:ring-opacity-50 text-black"
                  required
                >
                  {['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday', 'Sunday'].map(day => (
                    <option key={day} value={day}>{day}</option>
                  ))}
                </select>
              </div>
              
              {/* ... (keep the rest of the form) */}
            </form>
          </div>
        )}

        {/* Job List */}
        <div className="bg-surface shadow rounded-lg overflow-hidden">
          <table className="min-w-full divide-y divide-gray-700">
            <thead className="bg-gray-800">
              <tr>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-400 uppercase tracking-wider">Job</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-400 uppercase tracking-wider">Customer</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-400 uppercase tracking-wider">Price</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-400 uppercase tracking-wider">Status</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-400 uppercase tracking-wider">Recurrence</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-400 uppercase tracking-wider">Scheduled Day</th>
              </tr>
            </thead>
            <tbody className="bg-gray-900 divide-y divide-gray-700">
              {jobs.map((job) => (
                <tr key={job._id}>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <div className="text-sm font-medium text-white">{job.title}</div>
                    <div className="text-sm text-gray-400">{job.description}</div>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <div className="text-sm text-white">{job.customer?.name || 'Unknown'}</div>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <div className="text-sm text-white">${job.price}</div>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <span className={`px-2 inline-flex text-xs leading-5 font-semibold rounded-full 
                      ${job.status === 'Completed' ? 'bg-green-100 text-green-800' : 
                        job.status === 'In Progress' ? 'bg-yellow-100 text-yellow-800' : 
                        job.status === 'Canceled' ? 'bg-red-100 text-red-800' : 
                        job.status === 'Scheduled' ? 'bg-blue-100 text-blue-800' :
                        'bg-gray-100 text-gray-800'}`}>
                      {job.status}
                    </span>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <div className="text-sm text-white">{job.recurrence}</div>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <div className="text-sm text-white">{job.scheduledDay}</div>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </main>
    </div>
  );
};

export default Jobs;
