import React, { useState } from 'react';
import axios from 'axios';
import InputMask from 'react-input-mask';
import GooglePlacesAutocomplete from 'react-google-places-autocomplete';
import { PlusIcon, ArrowUpOnSquareIcon, InformationCircleIcon } from '@heroicons/react/24/outline';
import Header from '../components/Header';

const stateAbbreviations = {
  Pennsylvania: 'PA',
  NewYork: 'NY',
  // Add more state mappings here...
};

const Customers = () => {
  const [customer, setCustomer] = useState({
    name: '',
    email: '',
    phone: '',
    address: {
      street: '',
      city: '',
      state: '',
      zipCode: ''
    },
    yardSize: '',
    notes: '',
    status: 'Active'
  });
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [loading, setLoading] = useState(false);
  const [csvFile, setCsvFile] = useState(null);
  const [showCsvFormat, setShowCsvFormat] = useState(false);

  const handleAddressChange = (address) => {
    // Handle Google Places API address selection
    const addressComponents = address.label.split(', ');
    const street = addressComponents[0];
    const city = addressComponents[1];
    const stateFull = addressComponents[2];
    const state = stateAbbreviations[stateFull] || stateFull; // Convert to abbreviation if possible
    const zipCode = addressComponents[3] || '';

    setCustomer((prev) => ({
      ...prev,
      address: {
        street,
        city,
        state,
        zipCode
      }
    }));
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (name.includes('.')) {
      const [parent, child] = name.split('.');
      setCustomer((prev) => ({
        ...prev,
        [parent]: {
          ...prev[parent],
          [child]: value
        }
      }));
    } else {
      setCustomer((prev) => ({ ...prev, [name]: value }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    setLoading(true);

    // Validate required fields
    if (!customer.address.street || !customer.address.city || !customer.address.state || !customer.address.zipCode) {
      setError('All address fields are required.');
      setLoading(false);
      return;
    }

    try {
      await axios.post('http://localhost:5000/api/customers', customer, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setSuccess('Customer added successfully!');
      setCustomer({
        name: '',
        email: '',
        phone: '',
        address: { street: '', city: '', state: '', zipCode: '' },
        yardSize: '',
        notes: '',
        status: 'Active'
      });
    } catch (error) {
      console.error('Error creating customer:', error);
      setError('Failed to add customer. Please try again.');
    }
    setLoading(false);
  };

  const handleCsvUpload = async (e) => {
    e.preventDefault();
    if (!csvFile) {
      setError('Please select a CSV file to upload.');
      return;
    }

    const formData = new FormData();
    formData.append('file', csvFile);

    setLoading(true);
    try {
      await axios.post('http://localhost:5000/api/customers/import', formData, {
        headers: { 
          'Content-Type': 'multipart/form-data',
          Authorization: `Bearer ${localStorage.getItem('token')}` 
        }
      });
      setSuccess('Customers imported successfully!');
      setCsvFile(null);
    } catch (error) {
      console.error('Error importing customers:', error);
      setError('Failed to import customers. Please try again.');
    }
    setLoading(false);
  };

  return (
    <div className="min-h-screen bg-background text-text">
      <Header />
      <main className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        <div className="px-4 py-6 sm:px-0">
          <div className="flex justify-between items-center mb-6">
            <h1 className="text-3xl font-semibold">Add Customers</h1>
          </div>

          {error && <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded relative mb-4">{error}</div>}
          {success && <div className="bg-green-100 border border-green-400 text-green-700 px-4 py-3 rounded relative mb-4">{success}</div>}

          <div className="bg-surface overflow-hidden shadow rounded-lg p-6 mb-6">
            <h2 className="text-xl font-semibold mb-4">Add New Customer</h2>
            <form onSubmit={handleSubmit} className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <label htmlFor="name" className="block text-sm font-medium">Name *</label>
                <input
                  type="text"
                  id="name"
                  name="name"
                  value={customer.name}
                  onChange={handleChange}
                  className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary focus:ring focus:ring-primary focus:ring-opacity-50"
                  required
                />
              </div>
              <div>
                <label htmlFor="email" className="block text-sm font-medium">Email *</label>
                <input
                  type="email"
                  id="email"
                  name="email"
                  value={customer.email}
                  onChange={handleChange}
                  className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary focus:ring focus:ring-primary focus:ring-opacity-50"
                  required
                />
              </div>
              <div>
                <label htmlFor="phone" className="block text-sm font-medium">Phone</label>
                <InputMask
                  mask="(999) 999-9999"
                  value={customer.phone}
                  onChange={handleChange}
                >
                  {(inputProps) => (
                    <input
                      {...inputProps}
                      type="tel"
                      id="phone"
                      name="phone"
                      className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary focus:ring focus:ring-primary focus:ring-opacity-50"
                    />
                  )}
                </InputMask>
              </div>
              <div>
                <label htmlFor="address" className="block text-sm font-medium">Address *</label>
                <GooglePlacesAutocomplete
                  selectProps={{
                    value: customer.address.street,
                    onChange: handleAddressChange,
                    placeholder: "Search for an address...",
                  }}
                />
              </div>
              <div className="md:col-span-2">
                <label htmlFor="notes" className="block text-sm font-medium">Notes</label>
                <textarea
                  id="notes"
                  name="notes"
                  value={customer.notes}
                  onChange={handleChange}
                  rows="3"
                  className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary focus:ring focus:ring-primary focus:ring-opacity-50"
                ></textarea>
              </div>
              <div>
                <label htmlFor="status" className="block text-sm font-medium">Status</label>
                <select
                  id="status"
                  name="status"
                  value={customer.status}
                  onChange={handleChange}
                  className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary focus:ring focus:ring-primary focus:ring-opacity-50"
                >
                  <option value="Active">Active</option>
                  <option value="Inactive">Inactive</option>
                  <option value="Discontinued">Discontinued</option>
                </select>
              </div>
              <div className="md:col-span-2">
                <button
                  type="submit"
                  className="w-full bg-primary text-white px-4 py-2 rounded-md text-sm font-medium hover:bg-opacity-90 flex items-center justify-center"
                  disabled={loading}
                >
                  <PlusIcon className="h-5 w-5 mr-2" />
                  {loading ? 'Adding...' : 'Add Customer'}
                </button>
              </div>
            </form>
          </div>

          {/* CSV Upload Form */}
          <div className="bg-surface overflow-hidden shadow rounded-lg p-6">
            <h2 className="text-xl font-semibold mb-4">Import Customers from CSV</h2>
            <form onSubmit={handleCsvUpload} className="space-y-4">
              <div>
                <label htmlFor="csvFile" className="block text-sm font-medium">Select CSV File</label>
                <input
                  type="file"
                  id="csvFile"
                  accept=".csv"
                  onChange={(e) => setCsvFile(e.target.files[0])}
                  className="mt-1 block w-full"
                />
              </div>
              <button
                type="submit"
                className="w-full bg-secondary text-white px-4 py-2 rounded-md text-sm font-medium hover:bg-opacity-90 flex items-center justify-center"
                disabled={loading || !csvFile}
              >
                <ArrowUpOnSquareIcon className="h-5 w-5 mr-2" />
                {loading ? 'Importing...' : 'Import Customers'}
              </button>
            </form>
          </div>
        </div>
      </main>
    </div>
