// frontend/src/pages/Profile.js
import React, { useEffect, useState } from 'react';
import axiosInstance from '../utils/axiosInstance'; // Adjust path as necessary
import { useAuth } from '../contexts/AuthContext'; // Assuming you have an AuthContext

const Profile = () => {
  const [profile, setProfile] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const { user } = useAuth(); // Assuming AuthContext provides user info

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const response = await axiosInstance.get('/api/profile');
        setProfile(response.data);
      } catch (err) {
        console.error('Error fetching profile:', err);
        setError(err.response?.data?.error || 'Failed to load profile.');
      } finally {
        setLoading(false);
      }
    };
    fetchProfile();
  }, []);

  if (loading) {
    return <p className="text-center mt-8">Loading profile...</p>;
  }

  if (error) {
    return <p className="text-center text-red-500 mt-8">{error}</p>;
  }

  return (
    <div className="max-w-4xl mx-auto p-8">
      <h1 className="text-3xl font-bold mb-6">Your Profile</h1>
      <div className="bg-white shadow-md rounded-lg p-6">
        <h2 className="text-2xl font-semibold mb-4">Basic Information</h2>
        <p><strong>Name:</strong> {profile.user.name}</p>
        <p><strong>Email:</strong> {profile.user.email}</p>
        <p><strong>Bio:</strong> {profile.bio || 'N/A'}</p>

        <h2 className="text-2xl font-semibold mt-6 mb-4">Address</h2>
        <p><strong>Street:</strong> {profile.address.street || 'N/A'}</p>
        <p><strong>City:</strong> {profile.address.city || 'N/A'}</p>
        <p><strong>State:</strong> {profile.address.state || 'N/A'}</p>
        <p><strong>Zip Code:</strong> {profile.address.zipCode || 'N/A'}</p>

        <h2 className="text-2xl font-semibold mt-6 mb-4">Subscription</h2>
        <p><strong>Tier:</strong> {profile.subscriptionTier}</p>
        <p><strong>Active:</strong> {profile.subscriptionActive ? 'Yes' : 'No'}</p>
      </div>
    </div>
  );
};

export default Profile;
