// src/pages/Jobs.js
import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { TextField, Button, Paper, Typography } from '@mui/material';

const Jobs = () => {
  const [job, setJob] = useState({
    title: '',
    description: '',
    deadline: '',
    customer: ''
  });

  const handleChange = (e) => {
    setJob({ ...job, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // TODO: Implement job creation API call
    console.log('Job to create:', job);
  };

  return (
    <div className="min-h-screen bg-background text-text">
      <main className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        <div className="px-4 py-6 sm:px-0">
          <h1 className="text-2xl font-semibold mb-6">Jobs</h1>
          <Paper elevation={3} className="p-6 mb-6">
            <Typography variant="h5" gutterBottom>Create New Job</Typography>
            <form onSubmit={handleSubmit} className="space-y-4">
              <TextField
                fullWidth
                label="Job Title"
                name="title"
                value={job.title}
                onChange={handleChange}
                required
              />
              <TextField
                fullWidth
                label="Description"
                name="description"
                value={job.description}
                onChange={handleChange}
                multiline
                rows={4}
              />
              <TextField
                fullWidth
                label="Deadline"
                name="deadline"
                type="date"
                value={job.deadline}
                onChange={handleChange}
                InputLabelProps={{ shrink: true }}
              />
              <TextField
                fullWidth
                label="Customer"
                name="customer"
                value={job.customer}
                onChange={handleChange}
              />
              <Button type="submit" variant="contained" color="primary">
                Create Job
              </Button>
            </form>
          </Paper>
          <p className="mt-4">Additional job management functionality coming soon!</p>
          <Link to="/dashboard" className="mt-4 inline-block text-primary hover:text-primary-dark">
            Back to Dashboard
          </Link>
        </div>
      </main>
    </div>
  );
};

export default Jobs;
