// src/components/ProfileTabs/BadgesTab.js
import React from 'react';

const BadgesTab = ({ badges }) => {
  return (
    <div className="space-y-6">
      <h2 className="text-2xl font-semibold mb-4">Your Achievements</h2>
      {badges && badges.length > 0 ? (
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-4">
          {badges.map((badge, index) => (
            <div key={index} className="bg-surface p-4 rounded flex flex-col items-center">
              <img
                src={badge.iconUrl || '/badges/default-badge.png'}
                alt={badge.name}
                className="h-16 w-16 mb-2"
              />
              <h3 className="font-semibold">{badge.name}</h3>
              <p className="text-sm text-gray-500 text-center">{badge.description}</p>
              <p className="text-xs text-gray-400 mt-1">
                Earned on: {new Date(badge.dateEarned).toLocaleDateString()}
              </p>
            </div>
          ))}
        </div>
      ) : (
        <p className="text-gray-500">You haven't earned any achievements yet.</p>
      )}
    </div>
  );
};

export default BadgesTab;
