import React, { useState, useEffect } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { Link, useNavigate } from 'react-router-dom';
import {
  PlusIcon,
  UsersIcon,
  MapIcon,
  DocumentIcon,
  ArrowPathIcon,
  EyeIcon,
  EyeSlashIcon,
  ChevronLeftIcon,
  ChevronRightIcon,
} from '@heroicons/react/24/outline';
import Header from '../components/Header';
import { Line, Bar, Doughnut } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  ArcElement,
  Title,
  Tooltip,
  Legend,
  TimeScale,
  Filler,
} from 'chart.js';
import 'chartjs-adapter-date-fns';
import { fetchJobs, fetchCustomers, fetchEmployees, fetchAnalytics } from '../api/dashboardApi';
import TrialTimer from '../components/TrialTimer';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  ArcElement,
  Title,
  Tooltip,
  Legend,
  TimeScale,
  Filler
);

// Define a color palette
const colors = {
  primary: '#6366f1',
  secondary: '#8b5cf6',
  tertiary: '#ec4899',
  quaternary: '#14b8a6',
  background: '#1f2937',
  cardBg: '#374151',
  text: '#f3f4f6',
  muted: '#9ca3af',
};

const Dashboard = () => {
  const { user } = useAuth();
  const navigate = useNavigate();
  const [jobCount, setJobCount] = useState(0);
  const [customerCount, setCustomerCount] = useState(0);
  const [employeeCount, setEmployeeCount] = useState(0);
  const [analyticsData, setAnalyticsData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [globalTimeRange, setGlobalTimeRange] = useState('30');
  const [customDateRange, setCustomDateRange] = useState({ start: '', end: '' });
  const [showInsights, setShowInsights] = useState(true);
  const [currentPage, setCurrentPage] = useState(0);

  useEffect(() => {
    fetchDashboardData(globalTimeRange, customDateRange);
    const interval = setInterval(() => {
      fetchDashboardData(globalTimeRange, customDateRange);
    }, 600000); // Refresh every 10 minutes
    return () => clearInterval(interval);
  }, [globalTimeRange, customDateRange]);

  const fetchDashboardData = async (timeRange, customRange = null) => {
    setLoading(true);
    setError(null);
    try {
      const [jobs, customers, employees, analytics] = await Promise.all([
        fetchJobs(),
        fetchCustomers(),
        fetchEmployees(),
        fetchAnalytics(timeRange, customRange),
      ]);
      setJobCount(jobs.length);
      setCustomerCount(customers.length);
      setEmployeeCount(employees.length);
      setAnalyticsData(analytics);
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
      if (error.response && error.response.status === 403) {
        navigate('/trial-ended');
      } else {
        setError('Failed to load dashboard data. Please try again.');
      }
    } finally {
      setLoading(false);
    }
  };

  const handleTimeRangeChange = (e) => {
    const selectedRange = e.target.value;
    setGlobalTimeRange(selectedRange);
    if (selectedRange !== 'custom') {
      setCustomDateRange({ start: '', end: '' });
    }
  };

  const handleCustomDateChange = (e) => {
    setCustomDateRange({ ...customDateRange, [e.target.name]: e.target.value });
  };

  const applyCustomDateRange = () => {
    if (customDateRange.start && customDateRange.end) {
      fetchDashboardData('custom', customDateRange);
    }
  };

  const charts = [
    {
      title: "Revenue Over Time",
      component: (
        <Line
          data={{
            labels: analyticsData?.revenue?.map((item) => item._id) || [],
            datasets: [{
              label: 'Total Revenue ($)',
              data: analyticsData?.revenue?.map((item) => item.totalRevenue) || [],
              fill: true,
              backgroundColor: `${colors.primary}33`,
              borderColor: colors.primary,
              tension: 0.4,
            }],
          }}
          options={{
            responsive: true,
            maintainAspectRatio: false,
            plugins: {
              legend: { display: false },
              tooltip: {
                backgroundColor: colors.cardBg,
                titleColor: colors.text,
                bodyColor: colors.text,
                borderColor: colors.primary,
                borderWidth: 1,
              },
            },
            scales: {
              x: { 
                grid: { color: `${colors.muted}33` },
                ticks: { color: colors.muted },
              },
              y: { 
                grid: { color: `${colors.muted}33` },
                ticks: { color: colors.muted },
                beginAtZero: true,
              },
            },
          }}
        />
      ),
    },
    {
      title: "Customer Growth",
      component: (
        <Bar
          data={{
            labels: analyticsData?.customerGrowth?.map((item) => item._id) || [],
            datasets: [{
              label: 'New Customers',
              data: analyticsData?.customerGrowth?.map((item) => item.newCustomers) || [],
              backgroundColor: colors.secondary,
            }],
          }}
          options={{
            responsive: true,
            maintainAspectRatio: false,
            plugins: {
              legend: { display: false },
              tooltip: {
                backgroundColor: colors.cardBg,
                titleColor: colors.text,
                bodyColor: colors.text,
                borderColor: colors.secondary,
                borderWidth: 1,
              },
            },
            scales: {
              x: { 
                grid: { color: `${colors.muted}33` },
                ticks: { color: colors.muted },
              },
              y: { 
                grid: { color: `${colors.muted}33` },
                ticks: { color: colors.muted },
                beginAtZero: true,
              },
            },
          }}
        />
      ),
    },
    {
      title: "Job Status Distribution",
      component: (
        <Doughnut
          data={{
            labels: analyticsData?.jobStatusDistribution?.map((item) => item._id) || [],
            datasets: [{
              label: 'Job Status',
              data: analyticsData?.jobStatusDistribution?.map((item) => item.count) || [],
              backgroundColor: [colors.primary, colors.secondary, colors.tertiary, colors.quaternary],
            }],
          }}
          options={{
            responsive: true,
            maintainAspectRatio: false,
            plugins: {
              legend: { 
                position: 'right',
                labels: { color: colors.text },
              },
              tooltip: {
                backgroundColor: colors.cardBg,
                titleColor: colors.text,
                bodyColor: colors.text,
                borderColor: colors.primary,
                borderWidth: 1,
              },
            },
          }}
        />
      ),
    },
    {
      title: "Top Performing Employees",
      component: (
        <Bar
          data={{
            labels: analyticsData?.topPerformingEmployees?.map((item) => item.name) || [],
            datasets: [{
              label: 'Jobs Completed',
              data: analyticsData?.topPerformingEmployees?.map((item) => item.jobCount) || [],
              backgroundColor: colors.tertiary,
            }],
          }}
          options={{
            responsive: true,
            maintainAspectRatio: false,
            indexAxis: 'y',
            plugins: {
              legend: { display: false },
              tooltip: {
                backgroundColor: colors.cardBg,
                titleColor: colors.text,
                bodyColor: colors.text,
                borderColor: colors.tertiary,
                borderWidth: 1,
              },
            },
            scales: {
              x: { 
                grid: { color: `${colors.muted}33` },
                ticks: { color: colors.muted },
                beginAtZero: true,
              },
              y: { 
                grid: { color: `${colors.muted}33` },
                ticks: { color: colors.muted },
              },
            },
          }}
        />
      ),
    },
  ];

  const nextPage = () => {
    setCurrentPage((prevPage) => (prevPage + 1) % Math.ceil(charts.length / 3));
  };

  const prevPage = () => {
    setCurrentPage((prevPage) => (prevPage - 1 + Math.ceil(charts.length / 3)) % Math.ceil(charts.length / 3));
  };

  const renderChartCarousel = () => {
    const startIndex = currentPage * 3;
    const displayedCharts = charts.slice(startIndex, startIndex + 3);

    return (
      <div className="relative">
        <div className="flex justify-between items-center mb-4">
          <h2 className="text-2xl font-semibold text-white">Dashboard Insights</h2>
          <div className="flex space-x-2">
            <button
              onClick={prevPage}
              className={`bg-${colors.cardBg} hover:bg-opacity-80 text-white p-2 rounded-full transition duration-300`}
            >
              <ChevronLeftIcon className="h-6 w-6" />
            </button>
            <button
              onClick={nextPage}
              className={`bg-${colors.cardBg} hover:bg-opacity-80 text-white p-2 rounded-full transition duration-300`}
            >
              <ChevronRightIcon className="h-6 w-6" />
            </button>
          </div>
        </div>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {displayedCharts.map((chart, index) => (
            <div key={index} className={`bg-${colors.cardBg} p-4 rounded-lg shadow-lg`}>
              <h3 className="text-lg font-semibold mb-2 text-white">{chart.title}</h3>
              <div style={{ height: '300px' }}>{chart.component}</div>
            </div>
          ))}
        </div>
      </div>
    );
  };

  return (
    <div className={`min-h-screen bg-${colors.background} text-${colors.text}`}>
      <Header />

      <main className="max-w-7xl mx-auto py-6 px-4 sm:px-6 lg:px-8">
        {user?.subscriptionTier === 'Free' && (
          <div className="flex flex-col sm:flex-row items-center justify-between mb-6">
            <TrialTimer trialEndDate={user.trialEndDate} />
            <Link
              to="/pricing"
              className={`mt-4 sm:mt-0 bg-${colors.primary} text-white px-4 py-2 rounded-md text-sm font-medium hover:bg-opacity-90 transition duration-300`}
            >
              Upgrade Now
            </Link>
          </div>
        )}

        <div className="flex justify-between items-center mb-6">
          <h1 className="text-3xl font-bold text-white">Welcome, {user?.name || 'User'}</h1>
          <div className="flex items-center">
            <select
              value={globalTimeRange}
              onChange={handleTimeRangeChange}
              className={`mr-2 p-2 border rounded text-${colors.text} bg-${colors.cardBg}`}
            >
              <option value="7">Past 7 days</option>
              <option value="30">Past 30 days</option>
              <option value="90">Past 90 days</option>
              <option value="all">All time</option>
              <option value="custom">Custom range</option>
            </select>
            {globalTimeRange === 'custom' && (
              <>
                <input
                  type="date"
                  name="start"
                  value={customDateRange.start}
                  onChange={handleCustomDateChange}
                  className={`mr-2 p-2 border rounded text-${colors.text} bg-${colors.cardBg}`}
                />
                <input
                  type="date"
                  name="end"
                  value={customDateRange.end}
                  onChange={handleCustomDateChange}
                  className={`mr-2 p-2 border rounded text-${colors.text} bg-${colors.cardBg}`}
                />
                <button
                  onClick={applyCustomDateRange}
                  className={`bg-${colors.primary} text-white px-4 py-2 rounded-md text-sm font-medium hover:bg-opacity-90 transition duration-300`}
                >
                  Apply
                </button>
              </>
            )}
            <button
              onClick={() => fetchDashboardData(globalTimeRange, globalTimeRange === 'custom' ? customDateRange : null)}
              className={`ml-4 bg-${colors.quaternary} text-white px-4 py-2 rounded-md text-sm font-medium hover:bg-opacity-90 transition duration-300 flex items-center`}
            >
              <ArrowPathIcon className="h-5 w-5 mr-2" />
              Refresh Dashboard
            </button>
          </div>
        </div>

        {error && (
          <div className="bg-red-100 border-l-4 border-red-500 text-red-700 p-4 mb-6" role="alert">
            <p>{error}</p>
          </div>
        )}

        <div className="grid grid-cols-1 gap-5 sm:grid-cols-2 lg:grid-cols-3 mb-8">
          <OverviewCard title="Jobs" count={jobCount} link="/manage-jobs" color={colors.primary} />
          <OverviewCard title="Customers" count={customerCount} link="/manage-customers" color={colors.secondary} />
          <OverviewCard title="Employees" count={employeeCount} link="/manage-employees" color={colors.tertiary} />
        </div>

        <div className="mb-8">
          <h2 className="text-2xl font-semibold mb-4 text-white">Quick Actions</h2>
          <div className="grid grid-cols-2 gap-4 sm:grid-cols-3 lg:grid-cols-5">
            <QuickActionButton to="/jobs" icon={PlusIcon} text="Create New Job" color={colors.primary} />
            <QuickActionButton to="/customers" icon={PlusIcon} text="Add New Customer" color={colors.secondary} />
            <QuickActionButton to="/add-employee" icon={UsersIcon} text="Add New Employee" color={colors.tertiary} />
            <QuickActionButton to="/generate-report" icon={DocumentIcon} text="Generate Report" color={colors.quaternary} />
            <QuickActionButton to="/build-routes" icon={MapIcon} text="Build Routes" color={colors.primary} />
          </div>
        </div>

        <div className="flex justify-between items-center mb-4">
          <h2 className="text-2xl font-semibold text-white">Dashboard Insights</h2>
          <button 
            onClick={() => setShowInsights(!showInsights)} 
            className={`text-${colors.text} hover:text-${colors.muted} transition duration-300`}
          >
            {showInsights ? <EyeSlashIcon className="h-6 w-6" /> : <EyeIcon className="h-6 w-6" />}
          </button>
        </div>

        {showInsights && (
          <>
            {loading ? (
              <div className="text-center py-12 text-white">Loading dashboard data...</div>
            ) : (
              renderChartCarousel()
            )}
          </>
        )}
      </main>
    </div>
  );
};

const OverviewCard = ({ title, count, link, color }) => (
  <div className={`bg-${colors.cardBg} overflow-hidden shadow rounded-lg transition duration-300 hover:shadow-lg`}>
    <div className="px-4 py-5 sm:p-6">
      <h3 className="text-lg leading-6 font-medium text-white">{title}</h3>
      <div className={`mt-2 text-3xl font-semibold text-${color}`}>{count}</div>
      <div className="mt-4">
        <Link 
          to={link} 
          className={`text-${color} hover:text-opacity-80 transition duration-300`}
        >
          View all {title.toLowerCase()}
        </Link>
      </div>
    </div>
  </div>
);

const QuickActionButton = ({ to, icon: Icon, text, color }) => (
  <Link
    to={to}
    className={`bg-${color} text-white px-4 py-2 rounded-md text-sm font-medium hover:bg-opacity-90 transition duration-300 flex items-center justify-center`}
  >
    <Icon className="h-5 w-5 mr-2" />
    {text}
  </Link>
);

export default Dashboard;
