// src/pages/Profile.js
import React, { useState, useEffect, useCallback } from 'react';
import { useAuth } from '../contexts/AuthContext';
import axios from 'axios';
import Header from '../components/Header';
import {
  fetchJobs,
  fetchCustomers,
  fetchRoutes,
  fetchCompletedJobs,
  fetchServices,
  fetchBusinessInfo,
} from '../api/dashboardApi';

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {
  faUser,
  faBriefcase,
  faUsers,
  faTasks,
  faRoute,
  faTrophy,
  faExclamationTriangle,
  faChevronDown,
  faChevronUp,
} from '@fortawesome/free-solid-svg-icons';

import InputMask from 'react-input-mask';
import Select from 'react-select';
import { Autocomplete, useJsApiLoader } from '@react-google-maps/api';

// Full list of U.S. states
const stateOptions = [
  { value: 'AL', label: 'Alabama' },
  // ... (include all states as in your Customers.js)
  { value: 'WY', label: 'Wyoming' },
];

const API_URL = 'http://localhost:5000/api';

const Profile = () => {
  const { user } = useAuth();
  const [activeTab, setActiveTab] = useState('overview');
  const [services, setServices] = useState([]);
  const [newService, setNewService] = useState({ name: '', description: '', defaultPrice: '' });
  const [businessInfo, setBusinessInfo] = useState({
    name: '',
    logoUrl: '', // Ensure this exists to avoid errors
    address: {
      street: '',
      city: '',
      state: '',
      zipCode: '',
    },
    phone: '',
    email: '',
    website: ''
  });
  
  // Access logoUrl with optional chaining and default value
  const logoUrl = businessInfo?.logoUrl || 'default-logo-url';

  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [logoFile, setLogoFile] = useState(null);

  // States for password change
  const [passwordData, setPasswordData] = useState({
    currentPassword: '',
    newPassword: '',
    confirmNewPassword: '',
  });

  // States for levels and achievements
  const [profileCompletion, setProfileCompletion] = useState(0);
  const [achievements, setAchievements] = useState([]);
  const [level, setLevel] = useState(1);
  const [nextLevelRequirement, setNextLevelRequirement] = useState('');
  const [customersCount, setCustomersCount] = useState(0);
  const [jobsCount, setJobsCount] = useState(0);
  const [routesCount, setRoutesCount] = useState(0);
  const [jobsCompletedCount, setJobsCompletedCount] = useState(0);

  // Fields required for profile completion
  const requiredFields = [
    'name',
    'address.street',
    'address.city',
    'address.state',
    'address.zipCode',
    'phone',
    'email',
    'website',
  ];

  const [missingFields, setMissingFields] = useState([]);

  // State for collapsing the missing fields section
  const [isMissingFieldsCollapsed, setIsMissingFieldsCollapsed] = useState(false);

  // For Google Maps Places Autocomplete
  const [autocomplete, setAutocomplete] = useState(null);
  const { isLoaded } = useJsApiLoader({
    googleMapsApiKey: process.env.REACT_APP_GOOGLE_MAPS_API_KEY,
    libraries: ['places'],
  });

  // Achievement icons mapping
  const achievementIcons = {
    'Welcome Aboard!': faUser,
    'Business Profile Completed': faBriefcase,
    '5 Customers Added': faUsers,
    '5 Jobs Created': faTasks,
    'First Route Built': faRoute,
    '50 Jobs Completed': faTrophy,
    '100 Jobs Completed': faTrophy,
    '20 Customers Added': faUsers,
    '200 Jobs Completed': faTrophy,
  };

  // Calculate profile completion
  const calculateProfileCompletion = useCallback(() => {
    let completion = 0;
    const totalFields = requiredFields.length + 1; // Including logo as an additional field
    const missing = [];
  
    requiredFields.forEach((field) => {
      const fieldParts = field.split('.');
      let value = businessInfo;
      fieldParts.forEach((part) => {
        value = value && value[part];
      });
  
      if (value && value.trim() !== '') {
        completion += 100 / totalFields;
      } else {
        missing.push(field);
      }
    });
  
    // Ensure logoUrl or logoFile is present
    if (businessInfo.logoUrl || logoFile) {
      completion += 100 / totalFields;
    } else {
      missing.push('logo');
    }
  
    setProfileCompletion(Math.round(completion));
    setMissingFields(missing);
  }, [businessInfo, logoFile]);
  
  // Update achievements based on current level
  const updateAchievements = useCallback((currentLevel) => {
    const newAchievements = [];

    for (let i = 1; i <= currentLevel; i++) {
      let achievement = '';
      switch (i) {
        case 1:
          achievement = 'Welcome Aboard!';
          break;
        case 2:
          achievement = 'Business Profile Completed';
          break;
        case 3:
          achievement = '5 Customers Added';
          break;
        case 4:
          achievement = '5 Jobs Created';
          break;
        case 5:
          achievement = 'First Route Built';
          break;
        case 6:
          achievement = '50 Jobs Completed';
          break;
        case 7:
          achievement = '100 Jobs Completed';
          break;
        case 8:
          achievement = '20 Customers Added';
          break;
        case 9:
          achievement = '200 Jobs Completed';
          break;
        default:
          achievement = `Level ${i} Achieved`;
          break;
      }
      newAchievements.push(achievement);
    }

    setAchievements(newAchievements);
  }, []);

  // Calculate level based on user activities
  const calculateLevel = useCallback(() => {
    let currentLevel = 1;
    let requirement = '';

    if (profileCompletion === 100) {
      currentLevel = 2;
      requirement = 'Add 5 customers to reach Level 3.';
    } else {
      requirement = 'Complete your business profile to reach Level 2.';
    }

    if (customersCount >= 5) {
      currentLevel = 3;
      requirement = 'Create 5 jobs to reach Level 4.';
    }

    if (jobsCount >= 5) {
      currentLevel = 4;
      requirement = 'Build a route to reach Level 5.';
    }

    if (routesCount >= 1) {
      currentLevel = 5;
      requirement = 'Complete 50 jobs to reach Level 6.';
    }

    if (jobsCompletedCount >= 50) {
      currentLevel = 6;
      requirement = 'Complete 100 jobs to reach Level 7.';
    }

    if (jobsCompletedCount >= 100) {
      currentLevel = 7;
      requirement = 'Add 20 customers to reach Level 8.';
    }

    if (customersCount >= 20) {
      currentLevel = 8;
      requirement = 'Complete 200 jobs to reach Level 9.';
    }

    if (jobsCompletedCount >= 200) {
      currentLevel = 9;
      requirement = 'You have reached the maximum level!';
    }

    setLevel(currentLevel);
    setNextLevelRequirement(requirement);
    updateAchievements(currentLevel);
  }, [
    profileCompletion,
    customersCount,
    jobsCount,
    routesCount,
    jobsCompletedCount,
    updateAchievements,
  ]);

  // Now use the useEffect hooks
  useEffect(() => {
    fetchServicesData();
    fetchBusinessInfoData();
    fetchUserActivities();
  }, []);

  useEffect(() => {
    console.log('Business Info:', businessInfo);
    console.log('Logo File:', logoFile);
    console.log('Profile Completion:', profileCompletion);
    console.log('Missing Fields:', missingFields);
    calculateProfileCompletion();
}, [businessInfo, calculateProfileCompletion]);


  useEffect(() => {
    calculateLevel();
  }, [
    profileCompletion,
    customersCount,
    jobsCount,
    routesCount,
    jobsCompletedCount,
    calculateLevel,
  ]);

  // Fetch services
  const fetchServicesData = async () => {
    try {
      const data = await fetchServices();
      setServices(data);
    } catch (error) {
      console.error('Error fetching services:', error);
      setError('Failed to fetch services. Please try again.');
    }
  };

  // Fetch business info
  const fetchBusinessInfoData = async () => {
    try {
      const response = await fetchBusinessInfo();
      const data = response.data;
  
      // Ensure the address structure is correctly handled
      const addressParts = data?.address?.split(',') || ['', '', '', ''];
      const [street, city, state, zipCode] = addressParts;
  
      setBusinessInfo({
        name: data.name || '',
        logoUrl: data.logoUrl || '', // Ensure safe access to logoUrl
        address: {
          street: street || '',
          city: city || '',
          state: state || '',
          zipCode: zipCode || '',
        },
        phone: data.phone || '',
        email: data.email || '',
        website: data.website || '',
      });
    } catch (error) {
      console.error('Error fetching business info:', error);
      setError('Failed to fetch business information. Please try again.');
    }
  };

  // Fetch user activities (customers, jobs, routes, completed jobs)
  const fetchUserActivities = async () => {
    try {
      // Fetch customers count
      const customers = await fetchCustomers();
      setCustomersCount(customers.length);

      // Fetch jobs count
      const jobs = await fetchJobs();
      setJobsCount(jobs.length);

      // Fetch routes count
      const routes = await fetchRoutes();
      setRoutesCount(routes.length);

      // Fetch completed jobs count
      const completedJobs = await fetchCompletedJobs();
      setJobsCompletedCount(completedJobs.length);
    } catch (error) {
      console.error('Error fetching user activities:', error);
      setError('Failed to fetch user activities. Please try again.');
    }
  };

  // Handle service input changes
  const handleServiceChange = (e) => {
    const { name, value } = e.target;
    setNewService({ ...newService, [name]: value });
  };

  // Submit new service
  const handleServiceSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');

    try {
      await axios.post(`${API_URL}/services`, newService, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
      });
      setSuccess('Service added successfully!');
      setNewService({ name: '', description: '', defaultPrice: '' });
      fetchServicesData();
    } catch (error) {
      console.error('Error adding service:', error);
      setError('Failed to add service. Please try again.');
    }
  };

  // Delete service
  const handleServiceDelete = async (serviceId) => {
    try {
      await axios.delete(`${API_URL}/services/${serviceId}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
      });
      setSuccess('Service deleted successfully!');
      fetchServicesData();
    } catch (error) {
      console.error('Error deleting service:', error);
      setError('Failed to delete service. Please try again.');
    }
  };

  // Handle business info input changes
  const handleBusinessInfoChange = (e) => {
    const { name, value, type } = e.target;
    if (name.includes('.')) {
      const [parent, child] = name.split('.');
      setBusinessInfo((prev) => ({
        ...prev,
        [parent]: {
          ...prev[parent],
          [child]: value.trim(),  // Trim spaces here
        },
      }));
    } else {
      setBusinessInfo((prev) => ({ ...prev, [name]: value.trim() })); // Trim spaces here
    }
  };
  

  // Handle logo file change
  const handleLogoChange = (e) => {
    setLogoFile(e.target.files[0]);
  };

  // Submit business info
  const handleBusinessInfoSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');
  
    try {
      const formData = new FormData();
      formData.append('name', businessInfo.name);
      formData.append('address', `${businessInfo.address.street}, ${businessInfo.address.city}, ${businessInfo.address.state}, ${businessInfo.address.zipCode}`);
      formData.append('phone', businessInfo.phone);
      formData.append('email', businessInfo.email);
      formData.append('website', businessInfo.website);
      
      if (logoFile) {
        formData.append('logo', logoFile); // Only append if logoFile exists
      }
  
      const response = await axios.post(`${API_URL}/business-info`, formData, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`,
          'Content-Type': 'multipart/form-data',
        },
      });
  
      setBusinessInfo(response.data); // Update with the new data
      setSuccess('Business information updated successfully!');
    } catch (error) {
      console.error('Error updating business info:', error);
      setError('Failed to update business information. Please try again.');
    }
  };
  


  // Password change handler
  const handlePasswordChangeSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');

    if (passwordData.newPassword !== passwordData.confirmNewPassword) {
      setError('New passwords do not match.');
      return;
    }

    try {
      await axios.post(
        `${API_URL}/auth/change-password`,
        {
          currentPassword: passwordData.currentPassword,
          newPassword: passwordData.newPassword,
        },
        {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
        }
      );
      setSuccess('Password changed successfully!');
      setPasswordData({ currentPassword: '', newPassword: '', confirmNewPassword: '' });
    } catch (error) {
      console.error('Error changing password:', error);
      setError('Failed to change password. Please try again.');
    }
  };

  // Tab navigation handler
  const handleTabChange = (tab) => {
    setActiveTab(tab);
    setError('');
    setSuccess('');
  };

  const handlePlaceChanged = () => {
    if (autocomplete !== null) {
      const place = autocomplete.getPlace();

      // Extract address components
      const addressComponents = place.address_components;
      const streetNumber = addressComponents.find((component) =>
        component.types.includes('street_number')
      )?.long_name || '';
      const route = addressComponents.find((component) =>
        component.types.includes('route')
      )?.long_name || '';
      const city = addressComponents.find((component) =>
        component.types.includes('locality') || component.types.includes('sublocality')
      )?.long_name || '';
      const state = addressComponents.find((component) =>
        component.types.includes('administrative_area_level_1')
      )?.short_name || '';
      const zipCode = addressComponents.find((component) =>
        component.types.includes('postal_code')
      )?.long_name || '';

      setBusinessInfo((prev) => ({
        ...prev,
        address: {
          street: `${streetNumber} ${route}`.trim(),
          city,
          state,
          zipCode,
        },
      }));
    } else {
      console.log('Autocomplete is not loaded yet!');
    }
  };

  const onLoadAutocomplete = (autocompleteInstance) => {
    setAutocomplete(autocompleteInstance);
  };

  // Handle address select (for PlacesAutocomplete)
  const handleAddressSelect = (address) => {
    setBusinessInfo((prevInfo) => ({
      ...prevInfo,
      street: address,
    }));
  };

  const customSelectStyles = {
    control: (provided) => ({
      ...provided,
      backgroundColor: 'white',
      color: 'black',
    }),
    singleValue: (provided) => ({
      ...provided,
      color: 'black',
    }),
    input: (provided) => ({
      ...provided,
      color: 'black',
    }),
    menu: (provided) => ({
      ...provided,
      color: 'black',
    }),
  };

  return (
    <div className="min-h-screen bg-background text-text">
      <Header />
      <main className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        <div className="px-4 py-6 sm:px-0">
          <h1 className="text-3xl font-semibold mb-6">Profile</h1>
  
          {/* Level and Alerts */}
          <div className="mb-6">
            <h2 className="text-xl font-semibold mb-2">Level {level}</h2>
            <p className="mt-2 text-sm">{nextLevelRequirement}</p>
          </div>
  
          {error && (
            <div
              className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4"
              role="alert"
            >
              {error}
            </div>
          )}
          {success && (
            <div
              className="bg-green-100 border border-green-400 text-green-700 px-4 py-3 rounded mb-4"
              role="alert"
            >
              {success}
            </div>
          )}
  
          {/* Tabs */}
          <div className="mb-6">
            <nav className="flex space-x-4">
              <button
                onClick={() => handleTabChange('overview')}
                className={`px-3 py-2 font-medium ${
                  activeTab === 'overview'
                    ? 'text-primary border-b-2 border-primary'
                    : 'text-gray-500'
                }`}
              >
                Overview
              </button>
              <button
                onClick={() => handleTabChange('business')}
                className={`px-3 py-2 font-medium ${
                  activeTab === 'business'
                    ? 'text-primary border-b-2 border-primary'
                    : 'text-gray-500'
                }`}
              >
                Business Info
              </button>
              <button
                onClick={() => handleTabChange('services')}
                className={`px-3 py-2 font-medium ${
                  activeTab === 'services'
                    ? 'text-primary border-b-2 border-primary'
                    : 'text-gray-500'
                }`}
              >
                Services
              </button>
              <button
                onClick={() => handleTabChange('security')}
                className={`px-3 py-2 font-medium ${
                  activeTab === 'security'
                    ? 'text-primary border-b-2 border-primary'
                    : 'text-gray-500'
                }`}
              >
                Security
              </button>
              <button
                onClick={() => handleTabChange('achievements')}
                className={`px-3 py-2 font-medium ${
                  activeTab === 'achievements'
                    ? 'text-primary border-b-2 border-primary'
                    : 'text-gray-500'
                }`}
              >
                Achievements
              </button>
            </nav>
          </div>
  
          {/* Tab Content */}
          {activeTab === 'overview' && (
            <div>
              {/* User Information Section */}
              <div className="bg-surface shadow overflow-hidden sm:rounded-lg p-6 mb-6">
                <h2 className="text-2xl font-semibold mb-4">User Information</h2>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div>
                    <h3 className="text-lg font-medium text-black">Name</h3>
                    <p className="mt-1 text-sm text-black">{user?.name}</p>
                  </div>
                  <div>
                    <h3 className="text-lg font-medium text-black">Email</h3>
                    <p className="mt-1 text-sm text-black">{user?.email}</p>
                  </div>
                </div>
              </div>
  
              {/* Profile Completion Section */}
              <div className="bg-surface shadow overflow-hidden sm:rounded-lg p-6 mb-6">
                <h2 className="text-2xl font-semibold mb-4">Profile Completion</h2>
                <div className="w-full bg-gray-200 rounded-full h-4 mb-4">
                  <div
                    className="bg-primary h-4 rounded-full"
                    style={{ width: `${profileCompletion}%` }}
                  ></div>
                </div>
                <p className="text-sm text-black">
                  Your profile is {profileCompletion}% complete.
                </p>
                {missingFields.length > 0 && (
                  <div className="mt-4">
                    <h3 className="text-md font-medium text-red-600 flex items-center">
                      <FontAwesomeIcon icon={faExclamationTriangle} className="mr-2" />
                      Missing Information:
                    </h3>
                    <ul className="list-disc list-inside text-red-600">
                      {missingFields.map((field) => (
                        <li key={field} className="capitalize">
                          {field.replace(/([A-Z])/g, ' $1')}
                        </li>
                      ))}
                    </ul>
                    <p className="mt-2 text-sm text-black">
                      Please complete the missing fields in your business profile to reach 100%.
                    </p>
                  </div>
                )}
              </div>
            </div>
          )}
  
          {activeTab === 'achievements' && (
            <div>
              {/* Achievements Section */}
              <div className="bg-surface shadow overflow-hidden sm:rounded-lg p-6">
                <h2 className="text-2xl font-semibold mb-4">Achievements</h2>
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                  {achievements.map((achievement, index) => (
                    <div
                      key={index}
                      className="flex items-center space-x-4 p-4 bg-gray-100 rounded-md"
                    >
                      {/* Icon */}
                      <div className="w-12 h-12 bg-primary text-white flex items-center justify-center rounded-full">
                        <FontAwesomeIcon
                          icon={achievementIcons[achievement] || faTrophy}
                          size="2x"
                        />
                      </div>
                      <div>
                        <h3 className="text-lg font-medium text-black">{achievement}</h3>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            </div>
          )}
  
          {activeTab === 'business' && (
            <div>
              {/* Business Information Form */}
              <div className="bg-surface shadow overflow-hidden sm:rounded-lg p-6 mb-6">
                <h2 className="text-2xl font-semibold mb-4">Business Information</h2>
                <form onSubmit={handleBusinessInfoSubmit}>
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    {/* Business Name */}
                    <div>
                      <label htmlFor="name" className="block text-sm font-medium text-black">
                        Business Name
                      </label>
                      <input
                        type="text"
                        id="name"
                        name="name"
                        value={businessInfo.name}
                        onChange={handleBusinessInfoChange}
                        className={`mt-1 block w-full rounded-md border ${
                          !businessInfo.name ? 'border-red-500' : 'border-gray-300'
                        } shadow-sm text-black`}
                        required
                      />
                    </div>
                    {/* Logo */}
                    <div>
                      <label htmlFor="logo" className="block text-sm font-medium text-black">
                        Logo
                      </label>
                      <input
                        type="file"
                        id="logo"
                        name="logo"
                        onChange={handleLogoChange}
                        className="mt-1 block w-full text-black"
                        accept="image/*"
                      />
                    </div>
                    {/* Street Address with Autocomplete */}
                    <div className="md:col-span-2">
                      <label htmlFor="address.street" className="block text-sm font-medium text-black">
                        Address
                      </label>
                      {isLoaded && (
                        <Autocomplete onLoad={onLoadAutocomplete} onPlaceChanged={handlePlaceChanged}>
                          <input
                            type="text"
                            id="address.street"
                            name="address.street"
                            value={businessInfo.address.street}
                            onChange={handleBusinessInfoChange}
                            className={`mt-1 block w-full rounded-md border ${
                              !businessInfo.address.street ? 'border-red-500' : 'border-gray-300'
                            } shadow-sm text-black`}
                            required
                          />
                        </Autocomplete>
                      )}
                      {!isLoaded && (
                        <input
                          type="text"
                          id="address.street"
                          name="address.street"
                          value={businessInfo.address.street}
                          onChange={handleBusinessInfoChange}
                          className={`mt-1 block w-full rounded-md border ${
                            !businessInfo.address.street ? 'border-red-500' : 'border-gray-300'
                          } shadow-sm text-black`}
                          required
                        />
                      )}
                    </div>
                    {/* City */}
                    <div>
                      <label htmlFor="address.city" className="block text-sm font-medium text-black">
                        City
                      </label>
                      <input
                        type="text"
                        id="address.city"
                        name="address.city"
                        value={businessInfo.address.city}
                        onChange={handleBusinessInfoChange}
                        className={`mt-1 block w-full rounded-md border ${
                          !businessInfo.address.city ? 'border-red-500' : 'border-gray-300'
                        } shadow-sm text-black`}
                        required
                      />
                    </div>
                    {/* State */}
                    <div>
                      <label htmlFor="address.state" className="block text-sm font-medium text-black">
                        State
                      </label>
                      <Select
                        id="address.state"
                        name="address.state"
                        options={stateOptions}
                        value={stateOptions.find(
                          (option) => option.value === businessInfo.address.state
                        )}
                        onChange={(selectedOption) =>
                          handleBusinessInfoChange({
                            target: { name: 'address.state', value: selectedOption.value },
                          })
                        }
                        styles={customSelectStyles}
                        className="mt-1"
                        required
                      />
                    </div>
                    {/* Zip Code */}
                    <div>
                      <label htmlFor="address.zipCode" className="block text-sm font-medium text-black">
                        Zip Code
                      </label>
                      <input
                        type="text"
                        id="address.zipCode"
                        name="address.zipCode"
                        value={businessInfo.address.zipCode}
                        onChange={handleBusinessInfoChange}
                        className={`mt-1 block w-full rounded-md border ${
                          !businessInfo.address.zipCode ? 'border-red-500' : 'border-gray-300'
                        } shadow-sm text-black`}
                        required
                      />
                    </div>
                    {/* Phone */}
                    <div>
                      <label htmlFor="phone" className="block text-sm font-medium text-black">
                        Phone
                      </label>
                      <InputMask mask="(999) 999-9999" value={businessInfo.phone} onChange={handleBusinessInfoChange}>
                        {(inputProps) => (
                          <input
                            {...inputProps}
                            type="tel"
                            id="phone"
                            name="phone"
                            className={`mt-1 block w-full rounded-md border ${
                              !businessInfo.phone ? 'border-red-500' : 'border-gray-300'
                            } shadow-sm text-black`}
                            required
                          />
                        )}
                      </InputMask>
                    </div>
                    {/* Email */}
                    <div>
                      <label htmlFor="email" className="block text-sm font-medium text-black">
                        Email
                      </label>
                      <input
                        type="email"
                        id="email"
                        name="email"
                        value={businessInfo.email}
                        onChange={handleBusinessInfoChange}
                        className={`mt-1 block w-full rounded-md border ${
                          !businessInfo.email ? 'border-red-500' : 'border-gray-300'
                        } shadow-sm text-black`}
                        required
                      />
                    </div>
                    {/* Website */}
                    <div>
                      <label htmlFor="website" className="block text-sm font-medium text-black">
                        Website
                      </label>
                      <input
                        type="url"
                        id="website"
                        name="website"
                        value={businessInfo.website}
                        onChange={handleBusinessInfoChange}
                        className={`mt-1 block w-full rounded-md border ${
                          !businessInfo.website ? 'border-red-500' : 'border-gray-300'
                        } shadow-sm text-black`}
                        required
                      />
                    </div>
                  </div>
                  <button
                    type="submit"
                    className="mt-4 bg-primary text-white px-4 py-2 rounded-md text-sm font-medium"
                  >
                    Update Business Information
                  </button>
                </form>
              </div>
            </div>
          )}
  
          {activeTab === 'services' && (
            <div>
              {/* Services Section */}
              <div className="bg-surface shadow overflow-hidden sm:rounded-lg p-6">
                <h2 className="text-2xl font-semibold mb-4">Services</h2>
                <form onSubmit={handleServiceSubmit} className="mb-6">
                  <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                    <div>
                      <label htmlFor="name" className="block text-sm font-medium">
                        Service Name
                      </label>
                      <input
                        type="text"
                        id="name"
                        name="name"
                        value={newService.name}
                        onChange={handleServiceChange}
                        className="mt-1 block w-full rounded-md border-gray-800 shadow-sm"
                        required
                      />
                    </div>
                    <div>
                      <label htmlFor="description" className="block text-sm font-medium">
                        Description
                      </label>
                      <input
                        type="text"
                        id="description"
                        name="description"
                        value={newService.description}
                        onChange={handleServiceChange}
                        className="mt-1 block w-full rounded-md border-gray-800 shadow-sm"
                        required
                      />
                    </div>
                    <div>
                      <label htmlFor="defaultPrice" className="block text-sm font-medium">
                        Default Price
                      </label>
                      <input
                        type="number"
                        id="defaultPrice"
                        name="defaultPrice"
                        value={newService.defaultPrice}
                        onChange={handleServiceChange}
                        className="mt-1 block w-full rounded-md border-gray-800 shadow-sm"
                        required
                      />
                    </div>
                  </div>
                  <button
                    type="submit"
                    className="mt-4 bg-primary text-white px-4 py-2 rounded-md text-sm font-medium"
                  >
                    Add Service
                  </button>
                </form>
  
                <div className="overflow-x-auto">
                  <table className="min-w-full divide-y divide-gray-200">
                    <thead className="bg-gray-50">
                      <tr>
                        <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                          Name
                        </th>
                        <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                          Description
                        </th>
                        <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                          Default Price
                        </th>
                        <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                          Actions
                        </th>
                      </tr>
                    </thead>
                    <tbody className="bg-white divide-y divide-gray-200">
                      {services.map((service) => (
                        <tr key={service._id}>
                          <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">
                            {service.name}
                          </td>
                          <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                            {service.description}
                          </td>
                          <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                            ${service.defaultPrice}
                          </td>
                          <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                            <button
                              onClick={() => handleServiceDelete(service._id)}
                              className="text-red-600 hover:text-red-900"
                            >
                              Delete
                            </button>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              </div>
            </div>
          )}
  
          {activeTab === 'security' && (
            <div>
              {/* Password Change Section */}
              <div className="bg-surface shadow overflow-hidden sm:rounded-lg p-6 mb-6">
                <h2 className="text-2xl font-semibold mb-4">Change Password</h2>
                <form onSubmit={handlePasswordChangeSubmit}>
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <div>
                      <label htmlFor="currentPassword" className="block text-sm font-medium">
                        Current Password
                      </label>
                      <input
                        type="password"
                        id="currentPassword"
                        name="currentPassword"
                        value={passwordData.currentPassword}
                        onChange={(e) =>
                          setPasswordData({ ...passwordData, currentPassword: e.target.value })
                        }
                        className="mt-1 block w-full rounded-md border-gray-800 shadow-sm"
                        required
                      />
                    </div>
                    <div>
                      <label htmlFor="newPassword" className="block text-sm font-medium">
                        New Password
                      </label>
                      <input
                        type="password"
                        id="newPassword"
                        name="newPassword"
                        value={passwordData.newPassword}
                        onChange={(e) =>
                          setPasswordData({ ...passwordData, newPassword: e.target.value })
                        }
                        className="mt-1 block w-full rounded-md border-gray-800 shadow-sm"
                        required
                      />
                    </div>
                    <div>
                      <label htmlFor="confirmNewPassword" className="block text-sm font-medium">
                        Confirm New Password
                      </label>
                      <input
                        type="password"
                        id="confirmNewPassword"
                        name="confirmNewPassword"
                        value={passwordData.confirmNewPassword}
                        onChange={(e) =>
                          setPasswordData({ ...passwordData, confirmNewPassword: e.target.value })
                        }
                        className="mt-1 block w-full rounded-md border-gray-800 shadow-sm"
                        required
                      />
                    </div>
                  </div>
                  <button
                    type="submit"
                    className="mt-4 bg-primary text-white px-4 py-2 rounded-md text-sm font-medium"
                  >
                    Change Password
                  </button>
                </form>
              </div>
            </div>
          )}
        </div>
      </main>
    </div>
  );  
};

export default Profile;
