// src/pages/ManageJobs.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import { PencilIcon, TrashIcon, CheckIcon, XIcon } from '@heroicons/react/solid';

const ManageJobs = () => {
  const [jobs, setJobs] = useState([]);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [editingJob, setEditingJob] = useState(null);

  useEffect(() => {
    fetchJobs();
  }, []);

  const fetchJobs = async () => {
    setLoading(true);
    try {
      const response = await axios.get('http://localhost:5000/api/jobs', {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setJobs(response.data);
    } catch (error) {
      console.error('Error fetching jobs:', error);
      setError('Failed to fetch jobs. Please try again.');
    }
    setLoading(false);
  };

  const handleDeleteJob = async (jobId) => {
    if (window.confirm('Are you sure you want to delete this job?')) {
      try {
        await axios.delete(`http://localhost:5000/api/jobs/${jobId}`, {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        });
        setJobs(jobs.filter(job => job._id !== jobId));
      } catch (error) {
        console.error('Error deleting job:', error);
        setError('Failed to delete job. Please try again.');
      }
    }
  };

  const handleEditJob = (job) => {
    setEditingJob({ ...job });
  };

  const handleUpdateJob = async () => {
    try {
      const response = await axios.put(`http://localhost:5000/api/jobs/${editingJob._id}`, editingJob, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setJobs(jobs.map(job => job._id === editingJob._id ? response.data : job));
      setEditingJob(null);
    } catch (error) {
      console.error('Error updating job:', error);
      setError('Failed to update job. Please try again.');
    }
  };

  const handleInputChange = (e) => {
    setEditingJob({ ...editingJob, [e.target.name]: e.target.value });
  };

  return (
    <div className="min-h-screen bg-gray-100 text-gray-900">
      <main className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        <div className="px-4 py-6 sm:px-0">
          <h1 className="text-3xl font-bold mb-6 text-blue-600">Manage Jobs</h1>
          {error && <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">{error}</div>}
          {loading ? (
            <div className="text-center">
              <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-blue-500 mx-auto"></div>
              <p className="mt-4">Loading jobs...</p>
            </div>
          ) : jobs.length > 0 ? (
            <div className="bg-white shadow overflow-hidden sm:rounded-md">
              <ul className="divide-y divide-gray-200">
                {jobs.map((job) => (
                  <li key={job._id}>
                    <div className="px-4 py-4 sm:px-6">
                      {editingJob && editingJob._id === job._id ? (
                        <div className="space-y-3">
                          <input
                            type="text"
                            name="title"
                            value={editingJob.title}
                            onChange={handleInputChange}
                            className="w-full p-2 border rounded focus:ring-blue-500 focus:border-blue-500"
                          />
                          <textarea
                            name="description"
                            value={editingJob.description}
                            onChange={handleInputChange}
                            className="w-full p-2 border rounded focus:ring-blue-500 focus:border-blue-500"
                          />
                          <input
                            type="date"
                            name="scheduledDate"
                            value={editingJob.scheduledDate.split('T')[0]}
                            onChange={handleInputChange}
                            className="w-full p-2 border rounded focus:ring-blue-500 focus:border-blue-500"
                          />
                          <input
                            type="number"
                            name="price"
                            value={editingJob.price}
                            onChange={handleInputChange}
                            className="w-full p-2 border rounded focus:ring-blue-500 focus:border-blue-500"
                          />
                          <select
                            name="status"
                            value={editingJob.status}
                            onChange={handleInputChange}
                            className="w-full p-2 border rounded focus:ring-blue-500 focus:border-blue-500"
                          >
                            <option value="Scheduled">Scheduled</option>
                            <option value="In Progress">In Progress</option>
                            <option value="Completed">Completed</option>
                            <option value="Cancelled">Cancelled</option>
                          </select>
                          <div className="flex justify-end space-x-2">
                            <button onClick={handleUpdateJob} className="bg-green-500 text-white p-2 rounded hover:bg-green-600">
                              <CheckIcon className="h-5 w-5" />
                            </button>
                            <button onClick={() => setEditingJob(null)} className="bg-red-500 text-white p-2 rounded hover:bg-red-600">
                              <XIcon className="h-5 w-5" />
                            </button>
                          </div>
                        </div>
                      ) : (
                        <div className="flex items-center justify-between">
                          <div>
                            <h3 className="text-lg font-semibold text-blue-600">{job.title}</h3>
                            <p className="mt-1 text-sm text-gray-600">{job.description}</p>
                            <p className="mt-1 text-sm text-gray-600">Customer: {job.customer.name}</p>
                            <p className="mt-1 text-sm text-gray-600">Date: {new Date(job.scheduledDate).toLocaleDateString()}</p>
                            <p className="mt-1 text-sm text-gray-600">Price: ${job.price}</p>
                            <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${
                              job.status === 'Completed' ? 'bg-green-100 text-green-800' :
                              job.status === 'In Progress' ? 'bg-yellow-100 text-yellow-800' :
                              job.status === 'Cancelled' ? 'bg-red-100 text-red-800' :
                              'bg-blue-100 text-blue-800'
                            }`}>
                              {job.status}
                            </span>
                          </div>
                          <div className="flex space-x-2">
                            <button
                              onClick={() => handleEditJob(job)}
                              className="text-indigo-600 hover:text-indigo-900"
                            >
                              <PencilIcon className="h-5 w-5" />
                            </button>
                            <button
                              onClick={() => handleDeleteJob(job._id)}
                              className="text-red-600 hover:text-red-900"
                            >
                              <TrashIcon className="h-5 w-5" />
                            </button>
                          </div>
                        </div>
                      )}
                    </div>
                  </li>
                ))}
              </ul>
            </div>
          ) : (
            <p className="text-center text-gray-500">No jobs found.</p>
          )}
          <Link to="/dashboard" className="mt-6 inline-block bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 transition duration-200">
            Back to Dashboard
          </Link>
        </div>
      </main>
    </div>
  );
};

export default ManageJobs;
