import React, { useState, useEffect } from 'react';
import { fetchRoutes, updateRoute, saveSchedule } from './api';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import Modal from 'react-modal';

Modal.setAppElement('#root'); // Set the app element for accessibility

const RouteManager = () => {
  const [routes, setRoutes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedRoute, setSelectedRoute] = useState(null);
  const [recurringOptions, setRecurringOptions] = useState({
    frequency: 'weekly',
    interval: 1,
    daysOfWeek: [],
  });
  const [isEditModalOpen, setIsEditModalOpen] = useState(false);
  const [editRouteData, setEditRouteData] = useState({ name: '', customers: [] });

  useEffect(() => {
    const loadInitialData = async () => {
      try {
        const fetchedRoutes = await fetchRoutes();
        setRoutes(fetchedRoutes);
      } catch (err) {
        setError('Failed to load routes');
        console.error(err);
      } finally {
        setLoading(false);
      }
    };
    loadInitialData();
  }, []);

  const handleUpdateRoute = async () => {
    try {
      const updatedRoute = await updateRoute(editRouteData._id, editRouteData);
      setRoutes(routes.map(route => route._id === editRouteData._id ? updatedRoute : route));
      closeModal();
    } catch (err) {
      setError('Failed to update route');
      console.error(err);
    }
  };

  const handleRecurringOptionChange = (option, value) => {
    setRecurringOptions(prev => ({ ...prev, [option]: value }));
  };

  const saveScheduleForRoute = async () => {
    if (selectedRoute) {
      await saveSchedule(selectedRoute._id, recurringOptions);
      // Update the route in state
      setRoutes(routes.map(route =>
        route._id === selectedRoute._id ? { ...route, schedule: recurringOptions } : route
      ));
    }
  };

  const openModal = (route) => {
    setEditRouteData(route);
    setIsEditModalOpen(true);
  };

  const closeModal = () => {
    setIsEditModalOpen(false);
    setEditRouteData({ name: '', customers: [] });
  };

  const onDragEnd = (result) => {
    const { source, destination } = result;

    if (!destination) return;

    const sourceRoute = routes.find(route => route._id === source.droppableId);
    const destRoute = routes.find(route => route._id === destination.droppableId);
    const [movedCustomer] = sourceRoute.customers.splice(source.index, 1);
    destRoute.customers.splice(destination.index, 0, movedCustomer);

    setRoutes([...routes]);
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <DragDropContext onDragEnd={onDragEnd}>
      <div className="min-h-screen bg-background text-text">
        <nav className="bg-surface">
          <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
            <div className="flex items-center justify-between h-16">
              <div className="flex items-center">
                <span className="font-bold text-xl">Route Manager</span>
              </div>
            </div>
          </div>
        </nav>
        <main className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
          <div className="px-4 py-6 sm:px-0">
            <div className="grid grid-cols-1 gap-5 sm:grid-cols-2 lg:grid-cols-2">
              <div className="bg-surface overflow-hidden shadow rounded-lg p-6">
                <h3 className="text-lg leading-6 font-medium">Routes</h3>
                <ul className="mt-4 space-y-4">
                  {routes.map(route => (
                    <Droppable droppableId={route._id} key={route._id}>
                      {(provided) => (
                        <li ref={provided.innerRef} {...provided.droppableProps} className="bg-white p-4 rounded shadow text-gray-800">
                          <h4 className="font-semibold">{route.name}</h4>
                          <p>Customers: {route.customers.length}</p>
                          <p>Schedule: {route.schedule?.frequency} on {getDayName(route.schedule?.dayOfWeek)}</p>
                          <div className="flex space-x-2 mt-2">
                            <button onClick={() => openModal(route)} className="bg-blue-500 text-white px-2 py-1 rounded hover:bg-blue-600">Edit</button>
                          </div>
                          {route.customers.map((customer, index) => (
                            <Draggable key={customer._id} draggableId={customer._id} index={index}>
                              {(provided) => (
                                <div
                                  ref={provided.innerRef}
                                  {...provided.draggableProps}
                                  {...provided.dragHandleProps}
                                  className="bg-white p-2 mb-2 rounded shadow text-gray-800"
                                >
                                  {customer.name} - {customer.address?.street}, {customer.address?.city}, {customer.address?.state} {customer.address?.zipCode}
                                </div>
                              )}
                            </Draggable>
                          ))}
                          {provided.placeholder}
                        </li>
                      )}
                    </Droppable>
                  ))}
                </ul>
              </div>
              <div className="bg-surface overflow-hidden shadow rounded-lg p-6">
                <h3 className="text-lg leading-6 font-medium">Recurring Schedule</h3>
                {selectedRoute ? (
                  <div className="mt-4 text-gray-800">
                    <select value={recurringOptions.frequency} onChange={(e) => handleRecurringOptionChange('frequency', e.target.value)} className="w-full p-2 rounded mb-2 border text-gray-800">
                      <option value="weekly" className="text-gray-800">Weekly</option>
                      <option value="biweekly" className="text-gray-800">Bi-weekly</option>
                      <option value="monthly" className="text-gray-800">Monthly</option>
                    </select>
                    <input type="number" value={recurringOptions.interval} onChange={(e) => handleRecurringOptionChange('interval', e.target.value)} placeholder="Interval" className="w-full p-2 rounded mb-2 border text-gray-800"/>
                    <button onClick={saveScheduleForRoute} className="mt-4 bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600">Save Schedule</button>
                  </div>
                ) : (
                  <p>Please select a route to view and set its schedule.</p>
                )}
              </div>
            </div>
          </div>
        </main>

        <Modal
          isOpen={isEditModalOpen}
          onRequestClose={closeModal}
          contentLabel="Edit Route"
          className="Modal"
          overlayClassName="Overlay"
        >
          <h2>Edit Route</h2>
          <input
            type="text"
            value={editRouteData.name}
            onChange={(e) => setEditRouteData({ ...editRouteData, name: e.target.value })}
            placeholder="Route Name"
            className="w-full p-2 rounded mb-2 border text-gray-800"
          />
          <button onClick={handleUpdateRoute} className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600">Save</button>
          <button onClick={closeModal} className="bg-gray-500 text-white px-4 py-2 rounded hover:bg-gray-600">Cancel</button>
        </Modal>
      </div>
    </DragDropContext>
  );
};

function getDayName(dayNumber) {
  const days = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'];
  return days[dayNumber];
}

export default RouteManager;
