// src/components/ProfileTabs/SettingsTab.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const SettingsTab = () => {
  const [preferences, setPreferences] = useState({
    emailNotifications: false,
    smsNotifications: false,
  });

  useEffect(() => {
    const fetchPreferences = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get('/api/profile/preferences', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setPreferences(response.data);
      } catch (error) {
        console.error('Error fetching preferences:', error);
      }
    };
    fetchPreferences();
  }, []);

  const handleChange = (e) => {
    setPreferences({ ...preferences, [e.target.name]: e.target.checked });
  };

  const handleSaveSettings = async () => {
    try {
      const token = localStorage.getItem('token');
      await axios.put('/api/profile/preferences', preferences, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert('Settings updated successfully!');
    } catch (error) {
      console.error('Error updating settings:', error);
      alert('Failed to update settings.');
    }
  };

  return (
    <div className="bg-background text-text">
      <h2 className="text-2xl font-semibold mb-4">Account Settings</h2>
      <div className="mb-6">
        <label className="flex items-center">
          <input
            type="checkbox"
            name="emailNotifications"
            checked={preferences.emailNotifications}
            onChange={handleChange}
            className="form-checkbox text-primary bg-surface border-gray-600"
          />
          <span className="ml-2">Receive updates via Email</span>
        </label>
      </div>
      <div className="mb-6">
        <label className="flex items-center">
          <input
            type="checkbox"
            name="smsNotifications"
            checked={preferences.smsNotifications}
            onChange={handleChange}
            className="form-checkbox text-primary bg-surface border-gray-600"
          />
          <span className="ml-2">Receive updates via SMS</span>
        </label>
      </div>
      <button
        onClick={handleSaveSettings}
        className="bg-primary text-text px-4 py-2 rounded hover:bg-primary-dark"
      >
        Save Settings
      </button>
    </div>
  );
};

export default SettingsTab;
