import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import { PencilIcon, TrashIcon } from '@heroicons/react/24/solid';
import Header from '../components/Header';

const ManageCustomers = () => {
  const [customers, setCustomers] = useState([]);
  const [filteredCustomers, setFilteredCustomers] = useState([]);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [editingCustomer, setEditingCustomer] = useState(null);
  const [filter, setFilter] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const [customersPerPage] = useState(10);
  const [activeTab, setActiveTab] = useState('list');

  // New state variables for notifications
  const [message, setMessage] = useState('');
  const [selectedCustomers, setSelectedCustomers] = useState([]);
  const [notificationType, setNotificationType] = useState('email');
  const [sending, setSending] = useState(false);
  const [result, setResult] = useState(null);

  useEffect(() => {
    fetchCustomers();
  }, []);

  useEffect(() => {
    if (filter === '') {
      setFilteredCustomers(customers);
    } else {
      setFilteredCustomers(customers.filter(customer => 
        customer.name?.toLowerCase().includes(filter.toLowerCase()) ||
        customer.email?.toLowerCase().includes(filter.toLowerCase()) ||
        customer.phone?.toLowerCase().includes(filter.toLowerCase()) ||
        customer.address?.street?.toLowerCase().includes(filter.toLowerCase()) ||
        customer.address?.city?.toLowerCase().includes(filter.toLowerCase()) ||
        customer.address?.state?.toLowerCase().includes(filter.toLowerCase()) ||
        customer.address?.zipCode?.toLowerCase().includes(filter.toLowerCase()) ||
        customer.notes?.toLowerCase().includes(filter.toLowerCase())
      ));
    }
  }, [filter, customers]);
  

  const fetchCustomers = async () => {
    setLoading(true);
    try {
      const response = await axios.get('http://localhost:5000/api/customers', {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setCustomers(response.data);
      setFilteredCustomers(response.data);
    } catch (error) {
      console.error('Error fetching customers:', error);
      setError('Failed to fetch customers. Please try again.');
    }
    setLoading(false);
  };

  const handleDeleteCustomer = async (customerId) => {
    if (window.confirm('Are you sure you want to delete this customer?')) {
      try {
        await axios.delete(`http://localhost:5000/api/customers/${customerId}`, {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        });
        setCustomers(customers.filter(customer => customer._id !== customerId));
      } catch (error) {
        console.error('Error deleting customer:', error);
        setError('Failed to delete customer. Please try again.');
      }
    }
  };

  const handleEditCustomer = (customer) => {
    setEditingCustomer({ ...customer });
  };

  const handleUpdateCustomer = async () => {
    try {
      const response = await axios.put(`http://localhost:5000/api/customers/${editingCustomer._id}`, editingCustomer, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setCustomers(customers.map(customer => customer._id === editingCustomer._id ? response.data : customer));
      setEditingCustomer(null);
    } catch (error) {
      console.error('Error updating customer:', error);
      setError('Failed to update customer. Please try again.');
    }
  };
  
  const handleStatusChange = async (customerId, newStatus) => {
    try {
      await axios.put(`http://localhost:5000/api/customers/${customerId}/status`, 
        { status: newStatus },
        { headers: { Authorization: `Bearer ${localStorage.getItem('token')}` } }
      );
      setCustomers(customers.map(customer => 
        customer._id === customerId ? {...customer, status: newStatus} : customer
      ));
    } catch (error) {
      console.error('Error updating customer status:', error);
      setError('Failed to update customer status. Please try again.');
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    if (name.includes('.')) {
      const [objectKey, nestedKey] = name.split('.');
      setEditingCustomer(prev => ({
        ...prev,
        [objectKey]: {
          ...prev[objectKey],
          [nestedKey]: value
        }
      }));
    } else {
      setEditingCustomer(prev => ({ ...prev, [name]: value }));
    }
  };

  // Pagination Logic
  const indexOfLastCustomer = currentPage * customersPerPage;
  const indexOfFirstCustomer = indexOfLastCustomer - customersPerPage;
  const currentCustomers = filteredCustomers.slice(indexOfFirstCustomer, indexOfLastCustomer);

  const paginate = (pageNumber) => setCurrentPage(pageNumber);

  // New functions for notifications
  const handleSelectCustomer = (customerId) => {
    setSelectedCustomers(prev => 
      prev.includes(customerId) 
        ? prev.filter(id => id !== customerId)
        : [...prev, customerId]
    );
  };

  const handleSelectAll = () => {
    setSelectedCustomers(
      selectedCustomers.length === customers.length 
        ? [] 
        : customers.map(customer => customer._id)
    );
  };

  const handleSendNotification = async () => {
    if (selectedCustomers.length === 0 || !message) {
      setResult({ success: false, message: 'Please select customers and enter a message.' });
      return;
    }

    setSending(true);
    try {
      const response = await axios.post('http://localhost:5000/api/notifications', {
        customerIds: selectedCustomers,
        message,
        type: notificationType
      }, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setResult({ success: true, message: 'Notifications sent successfully!' });
    } catch (error) {
      setResult({ success: false, message: 'Failed to send notifications. Please try again.' });
    } finally {
      setSending(false);
    }
  };

  return (
    <div className="min-h-screen bg-background text-gray-800">
      <Header />
      <main className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        <div className="px-4 py-6 sm:px-0">
          <h1 className="text-2xl font-bold mb-4">Manage Customers</h1>
          {error && <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">{error}</div>}
          
          <div className="mb-4">
            <nav className="flex space-x-4" aria-label="Tabs">
              <button
                onClick={() => setActiveTab('list')}
                className={`${
                  activeTab === 'list'
                    ? 'bg-primary text-white'
                    : 'text-text hover:text-white hover:bg-primary'
                } px-3 py-2 font-medium text-sm rounded-md`}
              >
                Customer List
              </button>
              <button
                onClick={() => setActiveTab('notifications')}
                className={`${
                  activeTab === 'notifications'
                    ? 'bg-primary text-white'
                    : 'text-text hover:text-white hover:bg-primary'
                } px-3 py-2 font-medium text-sm rounded-md`}
              >
                Send Notifications
              </button>
            </nav>
          </div>

          {activeTab === 'list' ? (
            <>
              <div className="mb-4">
                <input
                  type="text"
                  placeholder="Filter customers..."
                  value={filter}
                  onChange={(e) => setFilter(e.target.value)}
                  className="border rounded p-2 w-full md:w-1/3 bg-background text-text"
                />
              </div>

              {loading ? (
                <div className="text-center">
                  <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-primary mx-auto"></div>
                  <p className="mt-4">Loading customers...</p>
                </div>
              ) : filteredCustomers.length > 0 ? (
                <div className="bg-surface shadow overflow-hidden sm:rounded-lg">
                  <table className="min-w-full divide-y divide-gray-500">
                    <thead className="bg-gray-900">
                      <tr>
                        <th className="px-6 py-3 text-left text-xs font-medium text-text uppercase tracking-wider">Name</th>
                        <th className="px-6 py-3 text-left text-xs font-medium text-text uppercase tracking-wider">Email</th>
                        <th className="px-6 py-3 text-left text-xs font-medium text-text uppercase tracking-wider">Phone</th>
                        <th className="px-6 py-3 text-left text-xs font-medium text-text uppercase tracking-wider">Address</th>
                        <th className="px-6 py-3 text-left text-xs font-medium text-text uppercase tracking-wider">Notes</th>
                        <th className="px-6 py-3 text-left text-xs font-medium text-text uppercase tracking-wider">Status</th>
                        <th className="px-6 py-3 text-left text-xs font-medium text-text uppercase tracking-wider">Actions</th>
                      </tr>
                    </thead>
                    <tbody className="bg-gray-900 divide-y divide-gray-200">
                      {currentCustomers.map((customer) => (
                        <tr key={customer._id}>
                          <td className="px-6 py-4 whitespace-nowrap">
                            <div className="text-sm font-medium text-text">{customer.name}</div>
                          </td>
                          <td className="px-6 py-4 whitespace-nowrap">
                            <div className="text-sm text-text">{customer.email}</div>
                          </td>
                          <td className="px-6 py-4 whitespace-nowrap">
                            <div className="text-sm text-text">{customer.phone}</div>
                          </td>
                          <td className="px-6 py-4 whitespace-nowrap">
                            <div className="text-sm text-text">
                              {customer.address && (
                                <>
                                  {customer.address.street}, {customer.address.city}, {customer.address.state} {customer.address.zipCode}
                                </>
                              )}
                            </div>
                          </td>
                          <td className="px-6 py-4 whitespace-nowrap">
                            <div className="text-sm text-text">{customer.notes}</div>
                          </td>
                          <td className="px-6 py-4 whitespace-nowrap">
                            <select
                              value={customer.status}
                              onChange={(e) => handleStatusChange(customer._id, e.target.value)}
                              className="text-sm text-white border rounded p-1 bg-primary"
                            >
                              <option value="Active">Active</option>
                              <option value="Inactive">Inactive</option>
                              <option value="Discontinued">Discontinued</option>
                            </select>
                          </td>
                          <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                            <button
                              onClick={() => handleEditCustomer(customer)}
                              className="text-indigo-600 hover:text-indigo-900 mr-2"
                            >
                              <PencilIcon className="h-5 w-5 inline" />
                            </button>
                            <button
                              onClick={() => handleDeleteCustomer(customer._id)}
                              className="text-red-600 hover:text-red-900"
                            >
                              <TrashIcon className="h-5 w-5 inline" />
                            </button>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                  <div className="px-6 py-4 bg-gray-900">
                    <Pagination
                      customersPerPage={customersPerPage}
                      totalCustomers={filteredCustomers.length}
                      paginate={paginate}
                    />
                  </div>
                </div>
              ) : (
                <p className="text-center text-gray-500">No customers found.</p>
              )}
            </>
          ) : (
            <div className="bg-gray-900 shadow-md rounded-lg p-6">
              <h2 className="text-2xl font-bold mb-4 text-white">Send Notifications</h2>
              
              <div className="mb-4">
                <label className="block text-white mb-2">Notification Type</label>
                <div className="flex space-x-4">
                  <button
                    className={`px-4 py-2 rounded ${notificationType === 'email' ? 'bg-primary text-white' : 'bg-gray-700 text-gray-300'}`}
                    onClick={() => setNotificationType('email')}
                  >
                    Email
                  </button>
                  <button
                    className={`px-4 py-2 rounded ${notificationType === 'sms' ? 'bg-primary text-white' : 'bg-gray-700 text-gray-300'}`}
                    onClick={() => setNotificationType('sms')}
                  >
                    SMS
                  </button>
                </div>
              </div>

              <div className="mb-4">
                <label htmlFor="message" className="block text-white mb-2">Message</label>
                <textarea
                  id="message"
                  value={message}
                  onChange={(e) => setMessage(e.target.value)}
                  className="w-full px-3 py-2 text-gray-800 border rounded-lg focus:outline-none focus:border-primary"
                  rows="4"
                  placeholder="Enter your message here..."
                ></textarea>
              </div>

              <div className="mb-4">
                <div className="flex justify-between items-center mb-2">
                  <label className="text-white">Select Customers</label>
                  <button
                    onClick={handleSelectAll}
                    className="text-sm text-primary hover:underline"
                  >
                    {selectedCustomers.length === customers.length ? 'Deselect All' : 'Select All'}
                  </button>
                </div>
                <div className="max-h-60 overflow-y-auto bg-gray-800 rounded-lg p-2">
                  {customers.map(customer => (
                    <div key={customer._id} className="flex items-center mb-2">
                      <input
                        type="checkbox"
                        id={customer._id}
                        checked={selectedCustomers.includes(customer._id)}
                        onChange={() => handleSelectCustomer(customer._id)}
                        className="mr-2"
                      />
                      <label htmlFor={customer._id} className="text-white cursor-pointer">
                        {customer.name} ({notificationType === 'email' ? customer.email : customer.phone})
                      </label>
                    </div>
                  ))}
                </div>
              </div>

              <button
                onClick={handleSendNotification}
                disabled={sending}
                className="w-full bg-primary text-white py-2 px-4 rounded-lg hover:bg-primary-dark transition duration-300"
              >
                {sending ? 'Sending...' : 'Send Notification'}
              </button>

              {result && (
                <div className={`mt-4 p-3 rounded ${result.success ? 'bg-green-600' : 'bg-red-600'} text-white`}>
                  {result.message}
                </div>
              )}
            </div>
          )}

          <Link to="/dashboard" className="mt-6 inline-block bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 transition duration-200">
            Back to Dashboard
          </Link>
        </div>
      </main>

      {/* Edit Customer Modal */}
      {editingCustomer && (
        <div className="fixed z-10 inset-0 overflow-y-auto" aria-labelledby="modal-title" role="dialog" aria-modal="true">
          <div className="flex items-end justify-center min-h-screen pt-4 px-4 pb-20 text-center sm:block sm:p-0">
            <div className="fixed inset-0 bg-gray-500 bg-opacity-75 transition-opacity" aria-hidden="true"></div>
            <span className="hidden sm:inline-block sm:align-middle sm:h-screen" aria-hidden="true">&#8203;</span>
            <div className="inline-block align-bottom bg-white rounded-lg text-left overflow-hidden shadow-xl transform transition-all sm:my-8 sm:align-middle sm:max-w-lg sm:w-full">
              <div className="bg-white px-4 pt-5 pb-4 sm:p-6 sm:pb-4">
                <h3 className="text-lg leading-6 font-medium text-gray-900" id="modal-title">
                  Edit Customer
                </h3>
                <div className="mt-2">
                  <input
                    type="text"
                    name="name"
                    value={editingCustomer.name}
                    onChange={handleInputChange}
                    className="w-full p-2 border rounded focus:ring-blue-500 focus:border-blue-500 mb-2"
                    placeholder="Customer Name"
                  />
                  <input
                    type="email"
                    name="email"
                    value={editingCustomer.email}
                    onChange={handleInputChange}
                    className="w-full p-2 border rounded focus:ring-blue-500 focus:border-blue-500 mb-2"
                    placeholder="Email"
                  />
                  <input
                    type="tel"
                    name="phone"
                    value={editingCustomer.phone}
                    onChange={handleInputChange}
                    className="w-full p-2 border rounded focus:ring-blue-500 focus:border-blue-500 mb-2"
                    placeholder="Phone"
                  />
                  <input
                    type="text"
                    name="address.street"
                    value={editingCustomer.address?.street || ''}
                    onChange={handleInputChange}
                    className="w-full p-2 border rounded focus:ring-blue-500 focus:border-blue-500 mb-2"
                    placeholder="Street"
                  />
                  <input
                    type="text"
                    name="address.city"
                    value={editingCustomer.address?.city || ''}
                    onChange={handleInputChange}
                    className="w-full p-2 border rounded focus:ring-blue-500 focus:border-blue-500 mb-2"
                    placeholder="City"
                  />
                  <input
                    type="text"
                    name="address.state"
                    value={editingCustomer.address?.state || ''}
                    onChange={handleInputChange}
                    className="w-full p-2 border rounded focus:ring-blue-500 focus:border-blue-500 mb-2"
                    placeholder="State"
                  />
                  <input
                    type="text"
                    name="address.zipCode"
                    value={editingCustomer.address?.zipCode || ''}
                    onChange={handleInputChange}
                    className="w-full p-2 border rounded focus:ring-blue-500 focus:border-blue-500 mb-2"
                    placeholder="Zip Code"
                  />
                  <input
                    type="text"
                    name="notes"
                    value={editingCustomer.notes || ''}
                    onChange={handleInputChange}
                    className="w-full p-2 border rounded focus:ring-blue-500 focus:border-blue-500 mb-2"
                    placeholder="Notes"
                  />
                </div>
              </div>
              <div className="bg-gray-50 px-4 py-3 sm:px-6 sm:flex sm:flex-row-reverse">
                <button
                  type="button"
                  className="w-full inline-flex justify-center rounded-md border border-transparent shadow-sm px-4 py-2 bg-blue-600 text-base font-medium text-white hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 sm:ml-3 sm:w-auto sm:text-sm"
                  onClick={handleUpdateCustomer}
                >
                  Save
                </button>
                <button
                  type="button"
                  className="mt-3 w-full inline-flex justify-center rounded-md border border-gray-300 shadow-sm px-4 py-2 bg-white text-base font-medium text-gray-700 hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 sm:mt-0 sm:ml-3 sm:w-auto sm:text-sm"
                  onClick={() => setEditingCustomer(null)}
                >
                  Cancel
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

const Pagination = ({ customersPerPage, totalCustomers, paginate }) => {
  const pageNumbers = [];

  for (let i = 1; i <= Math.ceil(totalCustomers / customersPerPage); i++) {
    pageNumbers.push(i);
  }

  return (
    <nav className="mt-4">
      <ul className="pagination flex justify-center">
        {pageNumbers.map(number => (
          <li key={number} className="page-item">
            <a onClick={() => paginate(number)} href="#!" className="page-link mx-1 px-3 py-1 border rounded text-blue-600 hover:bg-blue-600 hover:text-white transition duration-200">
              {number}
            </a>
          </li>
        ))}
      </ul>
    </nav>
  );
};

export default ManageCustomers;
