import React, { useState, useEffect } from 'react';
import FullCalendar from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';
import timeGridPlugin from '@fullcalendar/timegrid';
import interactionPlugin from '@fullcalendar/interaction';
import { fetchCalendarEvents, updateCalendarEvent } from './api';

const CalendarView = ({ routes }) => {
  const [events, setEvents] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    loadEvents();
  }, []);

  const loadEvents = async () => {
    try {
      const calendarEvents = await fetchCalendarEvents();
      const routeEvents = routes.flatMap(route => 
        route.customers.map(customer => ({
          title: `${route.name}: ${customer.name}`,
          start: customer.scheduledDate, // Assuming customer has a scheduledDate property
          end: customer.scheduledEndDate, // Assuming customer has a scheduledEndDate property
          color: '#4CAF50', // Green color for route events
          extendedProps: { type: 'route', routeId: route._id, customerId: customer._id }
        }))
      );
      setEvents([...calendarEvents, ...routeEvents]);
    } catch (error) {
      console.error('Error loading events:', error);
      setError('Failed to load calendar events. Please try refreshing the page.');
    }
  };

  const handleEventDrop = async (info) => {
    const { event } = info;
    try {
      const updatedEvent = await updateCalendarEvent({
        id: event.id,
        start: event.start,
        end: event.end,
      });
      setEvents(prevEvents => 
        prevEvents.map(e => e.id === updatedEvent.id ? updatedEvent : e)
      );
    } catch (error) {
      console.error('Error updating event:', error);
      info.revert();
    }
  };

  const handleEventClick = (info) => {
    const { event } = info;
    alert(`Event: ${event.title}\nStart: ${event.start}\nEnd: ${event.end}`);
  };

  if (error) return <div className="text-red-500">{error}</div>;

  return (
    <div className="calendar-container">
      <FullCalendar
        plugins={[dayGridPlugin, timeGridPlugin, interactionPlugin]}
        initialView="dayGridMonth"
        headerToolbar={{
          left: 'prev,next today',
          center: 'title',
          right: 'dayGridMonth,timeGridWeek,timeGridDay'
        }}
        editable={true}
        droppable={true}
        events={events}
        eventDrop={handleEventDrop}
        eventClick={handleEventClick}
      />
    </div>
  );
};

export default CalendarView;
