// src/pages/Profile.js
import React, { useState, useEffect, useCallback } from 'react';
import { useAuth } from '../contexts/AuthContext';
import axios from 'axios';
import Header from '../components/Header';
import { fetchJobs, fetchCustomers, fetchRoutes, fetchCompletedJobs } from '../api/dashboardApi';


const API_URL = 'http://localhost:5000/api';

const Profile = () => {
  const { user } = useAuth();
  const [activeTab, setActiveTab] = useState('overview');
  const [services, setServices] = useState([]);
  const [newService, setNewService] = useState({ name: '', description: '', defaultPrice: '' });
  const [businessInfo, setBusinessInfo] = useState({
    name: '',
    logo: '',
    address: '',
    phone: '',
    email: '',
    website: '',
  });
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [logoFile, setLogoFile] = useState(null);

  // States for password change
  const [passwordData, setPasswordData] = useState({
    currentPassword: '',
    newPassword: '',
    confirmNewPassword: '',
  });

  // States for levels and achievements
  const [profileCompletion, setProfileCompletion] = useState(0);
  const [achievements, setAchievements] = useState([]);
  const [level, setLevel] = useState(1);
  const [nextLevelRequirement, setNextLevelRequirement] = useState('');
  const [customersCount, setCustomersCount] = useState(0);
  const [jobsCount, setJobsCount] = useState(0);
  const [routesCount, setRoutesCount] = useState(0);
  const [jobsCompletedCount, setJobsCompletedCount] = useState(0);

  // **Define the functions before useEffect hooks**

  // Calculate profile completion
  const calculateProfileCompletion = useCallback(() => {
    let completion = 0;
    const fields = ['name', 'logo', 'address', 'phone', 'email', 'website'];
    fields.forEach((field) => {
      if (businessInfo[field]) completion += 100 / fields.length;
    });
    setProfileCompletion(Math.round(completion));
  }, [businessInfo]);

  // Update achievements based on current level
  const updateAchievements = useCallback((currentLevel) => {
    const newAchievements = [];

    for (let i = 1; i <= currentLevel; i++) {
      let achievement = '';
      switch (i) {
        case 1:
          achievement = 'Welcome Aboard!';
          break;
        case 2:
          achievement = 'Business Profile Completed';
          break;
        case 3:
          achievement = '5 Customers Added';
          break;
        case 4:
          achievement = '5 Jobs Created';
          break;
        case 5:
          achievement = 'First Route Built';
          break;
        case 6:
          achievement = '50 Jobs Completed';
          break;
        case 7:
          achievement = '100 Jobs Completed';
          break;
        case 8:
          achievement = '20 Customers Added';
          break;
        case 9:
          achievement = '200 Jobs Completed';
          break;
        default:
          achievement = `Level ${i} Achieved`;
          break;
      }
      newAchievements.push(achievement);
    }

    setAchievements(newAchievements);
  }, []);

  // Calculate level based on user activities
  const calculateLevel = useCallback(() => {
    let currentLevel = 1;
    let requirement = '';

    if (profileCompletion === 100) {
      currentLevel = 2;
      requirement = 'Add 5 customers to reach Level 3.';
    } else {
      requirement = 'Complete your business profile to reach Level 2.';
    }

    if (customersCount >= 5) {
      currentLevel = 3;
      requirement = 'Create 5 jobs to reach Level 4.';
    }

    if (jobsCount >= 5) {
      currentLevel = 4;
      requirement = 'Build a route to reach Level 5.';
    }

    if (routesCount >= 1) {
      currentLevel = 5;
      requirement = 'Complete 50 jobs to reach Level 6.';
    }

    if (jobsCompletedCount >= 50) {
      currentLevel = 6;
      requirement = 'Complete 100 jobs to reach Level 7.';
    }

    if (jobsCompletedCount >= 100) {
      currentLevel = 7;
      requirement = 'Add 20 customers to reach Level 8.';
    }

    if (customersCount >= 20) {
      currentLevel = 8;
      requirement = 'Complete 200 jobs to reach Level 9.';
    }

    if (jobsCompletedCount >= 200) {
      currentLevel = 9;
      requirement = 'You have reached the maximum level!';
    }

    setLevel(currentLevel);
    setNextLevelRequirement(requirement);
    updateAchievements(currentLevel);
  }, [
    profileCompletion,
    customersCount,
    jobsCount,
    routesCount,
    jobsCompletedCount,
    updateAchievements,
  ]);

  // **Now use the useEffect hooks**

  useEffect(() => {
    fetchServices();
    fetchBusinessInfo();
    fetchUserActivities();
  }, []);

  useEffect(() => {
    calculateProfileCompletion();
  }, [calculateProfileCompletion]);

  useEffect(() => {
    calculateLevel();
  }, [calculateLevel]);

  // Fetch services
  const fetchServices = async () => {
    try {
      const response = await axios.get('/api/services', {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
      });
      setServices(response.data);
    } catch (error) {
      console.error('Error fetching services:', error);
      setError('Failed to fetch services. Please try again.');
    }
  };

  // Fetch business info
  const fetchBusinessInfo = async () => {
    try {
      const response = await axios.get('/api/business-info', {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
      });
      setBusinessInfo(response.data);
    } catch (error) {
      console.error('Error fetching business info:', error);
      setError('Failed to fetch business information. Please try again.');
    }
  };

  // Fetch user activities (customers, jobs, routes, completed jobs)
  const fetchUserActivities = async () => {
    try {
      // Fetch customers count
      const customers = await fetchCustomers();
      setCustomersCount(customers.length);

      // Fetch jobs count
      const jobs = await fetchJobs();
      setJobsCount(jobs.length);

      // Fetch routes count
      const routes = await fetchRoutes();
      setRoutesCount(routes.length);

      // Fetch completed jobs count
      const completedJobs = await fetchCompletedJobs();
      setJobsCompletedCount(completedJobs.length);
    } catch (error) {
      console.error('Error fetching user activities:', error);
      setError('Failed to fetch user activities. Please try again.');
    }
  };

  // Handle service input changes
  const handleServiceChange = (e) => {
    const { name, value } = e.target;
    setNewService({ ...newService, [name]: value });
  };

  // Submit new service
  const handleServiceSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');

    try {
      await axios.post('/api/services', newService, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
      });
      setSuccess('Service added successfully!');
      setNewService({ name: '', description: '', defaultPrice: '' });
      fetchServices();
    } catch (error) {
      console.error('Error adding service:', error);
      setError('Failed to add service. Please try again.');
    }
  };

  // Delete service
  const handleServiceDelete = async (serviceId) => {
    try {
      await axios.delete(`/api/services/${serviceId}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
      });
      setSuccess('Service deleted successfully!');
      fetchServices();
    } catch (error) {
      console.error('Error deleting service:', error);
      setError('Failed to delete service. Please try again.');
    }
  };

  // Handle business info input changes
  const handleBusinessInfoChange = (e) => {
    const { name, value } = e.target;
    setBusinessInfo({ ...businessInfo, [name]: value });
  };

  // Handle logo file change
  const handleLogoChange = (e) => {
    setLogoFile(e.target.files[0]);
  };

  // Submit business info
  const handleBusinessInfoSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');

    const formData = new FormData();
    Object.keys(businessInfo).forEach((key) => {
      if (businessInfo[key]) formData.append(key, businessInfo[key]);
    });
    if (logoFile) formData.append('logo', logoFile);

    try {
      const response = await axios.post('/api/business-info', formData, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`,
          'Content-Type': 'multipart/form-data',
        },
      });
      setBusinessInfo(response.data);
      setSuccess('Business information updated successfully!');
    } catch (error) {
      console.error('Error updating business info:', error);
      setError('Failed to update business information. Please try again.');
    }
  };

  // Password change handler
  const handlePasswordChangeSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');

    if (passwordData.newPassword !== passwordData.confirmNewPassword) {
      setError('New passwords do not match.');
      return;
    }

    try {
      await axios.post(
        '/api/auth/change-password',
        {
          currentPassword: passwordData.currentPassword,
          newPassword: passwordData.newPassword,
        },
        {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
        }
      );
      setSuccess('Password changed successfully!');
      setPasswordData({ currentPassword: '', newPassword: '', confirmNewPassword: '' });
    } catch (error) {
      console.error('Error changing password:', error);
      setError('Failed to change password. Please try again.');
    }
  };

  // Tab navigation handler
  const handleTabChange = (tab) => {
    setActiveTab(tab);
    setError('');
    setSuccess('');
  };

  return (
    <div className="min-h-screen bg-background text-text">
      <Header />
      <main className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        <div className="px-4 py-6 sm:px-0">
          <h1 className="text-3xl font-semibold mb-6">Profile</h1>

          {/* Level and Alerts */}
          <div className="mb-6">
            <h2 className="text-xl font-semibold mb-2">Level {level}</h2>
            <p className="mt-2 text-sm">{nextLevelRequirement}</p>
          </div>

          {/* Achievements */}
          <div className="mb-6">
            <h2 className="text-xl font-semibold mb-2">Achievements</h2>
            <ul className="list-disc list-inside">
              {achievements.map((achievement, index) => (
                <li key={index}>{achievement}</li>
              ))}
            </ul>
          </div>

          {error && (
            <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
              {error}
            </div>
          )}
          {success && (
            <div className="bg-green-100 border border-green-400 text-green-700 px-4 py-3 rounded mb-4">
              {success}
            </div>
          )}

          {/* Tabs */}
          <div className="mb-6">
            <nav className="flex space-x-4">
              <button
                onClick={() => handleTabChange('overview')}
                className={`px-3 py-2 font-medium ${
                  activeTab === 'overview' ? 'text-primary border-b-2 border-primary' : 'text-gray-500'
                }`}
              >
                Overview
              </button>
              <button
                onClick={() => handleTabChange('business')}
                className={`px-3 py-2 font-medium ${
                  activeTab === 'business' ? 'text-primary border-b-2 border-primary' : 'text-gray-500'
                }`}
              >
                Business Info
              </button>
              <button
                onClick={() => handleTabChange('services')}
                className={`px-3 py-2 font-medium ${
                  activeTab === 'services' ? 'text-primary border-b-2 border-primary' : 'text-gray-500'
                }`}
              >
                Services
              </button>
              <button
                onClick={() => handleTabChange('security')}
                className={`px-3 py-2 font-medium ${
                  activeTab === 'security' ? 'text-primary border-b-2 border-primary' : 'text-gray-500'
                }`}
              >
                Security
              </button>
            </nav>
          </div>

          {/* Tab Content */}
          {activeTab === 'overview' && (
            <div>
              {/* User Information Section */}
              <div className="bg-surface shadow overflow-hidden sm:rounded-lg p-6 mb-6">
                <h2 className="text-2xl font-semibold mb-4">User Information</h2>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div>
                    <h3 className="text-lg font-medium">Name</h3>
                    <p className="mt-1 text-sm">{user?.name}</p>
                  </div>
                  <div>
                    <h3 className="text-lg font-medium">Email</h3>
                    <p className="mt-1 text-sm">{user?.email}</p>
                  </div>
                </div>
              </div>
            </div>
          )}

          {activeTab === 'business' && (
            <div>
              {/* Business Information Form */}
              <div className="bg-surface shadow overflow-hidden sm:rounded-lg p-6 mb-6">
                <h2 className="text-2xl font-semibold mb-4">Business Information</h2>
                <form onSubmit={handleBusinessInfoSubmit}>
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <div>
                      <label htmlFor="businessName" className="block text-sm font-medium">
                        Business Name
                      </label>
                      <input
                        type="text"
                        id="businessName"
                        name="name"
                        value={businessInfo.name}
                        onChange={handleBusinessInfoChange}
                        className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary focus:ring focus:ring-primary focus:ring-opacity-50"
                        required
                      />
                    </div>
                    <div>
                      <label htmlFor="logo" className="block text-sm font-medium">
                        Logo
                      </label>
                      <input
                        type="file"
                        id="logo"
                        name="logo"
                        onChange={handleLogoChange}
                        className="mt-1 block w-full"
                        accept="image/*"
                      />
                    </div>
                    <div>
                      <label htmlFor="address" className="block text-sm font-medium">
                        Address
                      </label>
                      <input
                        type="text"
                        id="address"
                        name="address"
                        value={businessInfo.address}
                        onChange={handleBusinessInfoChange}
                        className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary focus:ring focus:ring-primary focus:ring-opacity-50"
                        required
                      />
                    </div>
                    <div>
                      <label htmlFor="phone" className="block text-sm font-medium">
                        Phone
                      </label>
                      <input
                        type="tel"
                        id="phone"
                        name="phone"
                        value={businessInfo.phone}
                        onChange={handleBusinessInfoChange}
                        className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary focus:ring focus:ring-primary focus:ring-opacity-50"
                        required
                      />
                    </div>
                    <div>
                      <label htmlFor="email" className="block text-sm font-medium">
                        Email
                      </label>
                      <input
                        type="email"
                        id="email"
                        name="email"
                        value={businessInfo.email}
                        onChange={handleBusinessInfoChange}
                        className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary focus:ring focus:ring-primary focus:ring-opacity-50"
                        required
                      />
                    </div>
                    <div>
                      <label htmlFor="website" className="block text-sm font-medium">
                        Website
                      </label>
                      <input
                        type="url"
                        id="website"
                        name="website"
                        value={businessInfo.website}
                        onChange={handleBusinessInfoChange}
                        className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary focus:ring focus:ring-primary focus:ring-opacity-50"
                      />
                    </div>
                  </div>
                  <button
                    type="submit"
                    className="mt-4 bg-primary text-white px-4 py-2 rounded-md text-sm font-medium hover:bg-primary-dark focus:outline-none focus:ring-2 focus:ring-primary"
                  >
                    Update Business Information
                  </button>
                </form>
              </div>
            </div>
          )}

          {activeTab === 'services' && (
            <div>
              {/* Services Section */}
              <div className="bg-surface shadow overflow-hidden sm:rounded-lg p-6">
                <h2 className="text-2xl font-semibold mb-4">Services</h2>
                <form onSubmit={handleServiceSubmit} className="mb-6">
                  <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                    <div>
                      <label htmlFor="name" className="block text-sm font-medium">
                        Service Name
                      </label>
                      <input
                        type="text"
                        id="name"
                        name="name"
                        value={newService.name}
                        onChange={handleServiceChange}
                        className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary focus:ring focus:ring-primary focus:ring-opacity-50"
                        required
                      />
                    </div>
                    <div>
                      <label htmlFor="description" className="block text-sm font-medium">
                        Description
                      </label>
                      <input
                        type="text"
                        id="description"
                        name="description"
                        value={newService.description}
                        onChange={handleServiceChange}
                        className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary focus:ring focus:ring-primary focus:ring-opacity-50"
                        required
                      />
                    </div>
                    <div>
                      <label htmlFor="defaultPrice" className="block text-sm font-medium">
                        Default Price
                      </label>
                      <input
                        type="number"
                        id="defaultPrice"
                        name="defaultPrice"
                        value={newService.defaultPrice}
                        onChange={handleServiceChange}
                        className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary focus:ring focus:ring-primary focus:ring-opacity-50"
                        required
                      />
                    </div>
                  </div>
                  <button
                    type="submit"
                    className="mt-4 bg-primary text-white px-4 py-2 rounded-md text-sm font-medium hover:bg-primary-dark focus:outline-none focus:ring-2 focus:ring-primary"
                  >
                    Add Service
                  </button>
                </form>

                <div className="overflow-x-auto">
                  <table className="min-w-full divide-y divide-gray-200">
                    <thead className="bg-gray-50">
                      <tr>
                        <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                          Name
                        </th>
                        <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                          Description
                        </th>
                        <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                          Default Price
                        </th>
                        <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                          Actions
                        </th>
                      </tr>
                    </thead>
                    <tbody className="bg-white divide-y divide-gray-200">
                      {services.map((service) => (
                        <tr key={service._id}>
                          <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">
                            {service.name}
                          </td>
                          <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                            {service.description}
                          </td>
                          <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                            ${service.defaultPrice}
                          </td>
                          <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                            <button
                              onClick={() => handleServiceDelete(service._id)}
                              className="text-red-600 hover:text-red-900"
                            >
                              Delete
                            </button>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              </div>
            </div>
          )}

          {activeTab === 'security' && (
            <div>
              {/* Password Change Section */}
              <div className="bg-surface shadow overflow-hidden sm:rounded-lg p-6 mb-6">
                <h2 className="text-2xl font-semibold mb-4">Change Password</h2>
                <form onSubmit={handlePasswordChangeSubmit}>
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <div>
                      <label htmlFor="currentPassword" className="block text-sm font-medium">
                        Current Password
                      </label>
                      <input
                        type="password"
                        id="currentPassword"
                        name="currentPassword"
                        value={passwordData.currentPassword}
                        onChange={(e) =>
                          setPasswordData({ ...passwordData, currentPassword: e.target.value })
                        }
                        className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary focus:ring focus:ring-primary focus:ring-opacity-50"
                        required
                      />
                    </div>
                    <div>
                      <label htmlFor="newPassword" className="block text-sm font-medium">
                        New Password
                      </label>
                      <input
                        type="password"
                        id="newPassword"
                        name="newPassword"
                        value={passwordData.newPassword}
                        onChange={(e) =>
                          setPasswordData({ ...passwordData, newPassword: e.target.value })
                        }
                        className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary focus:ring focus:ring-primary focus:ring-opacity-50"
                        required
                      />
                    </div>
                    <div>
                      <label htmlFor="confirmNewPassword" className="block text-sm font-medium">
                        Confirm New Password
                      </label>
                      <input
                        type="password"
                        id="confirmNewPassword"
                        name="confirmNewPassword"
                        value={passwordData.confirmNewPassword}
                        onChange={(e) =>
                          setPasswordData({ ...passwordData, confirmNewPassword: e.target.value })
                        }
                        className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary focus:ring focus:ring-primary focus:ring-opacity-50"
                        required
                      />
                    </div>
                  </div>
                  <button
                    type="submit"
                    className="mt-4 bg-primary text-white px-4 py-2 rounded-md text-sm font-medium hover:bg-primary-dark focus:outline-none focus:ring-2 focus:ring-primary"
                  >
                    Change Password
                  </button>
                </form>
              </div>
            </div>
          )}
        </div>
      </main>
    </div>
  );
};

export default Profile;
