import React, { useState, useEffect, useCallback, memo } from 'react';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import { useNavigate } from 'react-router-dom';
import FullCalendar from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';
import timeGridPlugin from '@fullcalendar/timegrid';
import interactionPlugin, { Draggable as FcDraggable } from '@fullcalendar/interaction';
import CustomerMap from '../../components/CustomerMap';
import { 
  fetchJobs, 
  fetchRoutes, 
  saveRoute, 
  deleteRoute, 
  updateJob, 
  updateRoute,
  addJobToRoute,
  removeJobFromRoute,
  fetchCalendarEvents,
  createCalendarEvent,
  updateCalendarEvent,
  deleteCalendarEvent
} from './api';
import Header from '../../components/Header';
import ErrorBoundary from '../../components/ErrorBoundary';

const MemoizedFullCalendar = memo(FullCalendar);

const BuildRoutes = () => {
  const [jobs, setJobs] = useState([]);
  const [routes, setRoutes] = useState([]);
  const [events, setEvents] = useState([]);
  const [activeTab, setActiveTab] = useState('routes');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [successMessage, setSuccessMessage] = useState('');
  const [selectedEvent, setSelectedEvent] = useState(null);
  const [showStartTimeModal, setShowStartTimeModal] = useState(false);
  const [pendingEvent, setPendingEvent] = useState(null);
  const [newRouteName, setNewRouteName] = useState('');
  const navigate = useNavigate();

  const fetchData = useCallback(async () => {
    setLoading(true);
    setError(null);
    try {
      const [jobsData, routesData] = await Promise.all([fetchJobs(), fetchRoutes()]);
      setJobs(jobsData);
      setRoutes(routesData);
      generateEvents(jobsData, routesData);
    } catch (err) {
      console.error('Error fetching data:', err);
      setError('Failed to load data. Please try refreshing the page.');
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    fetchData();
  }, [fetchData]);

  const generateEvents = useCallback((jobsData, routesData) => {
    const jobEvents = jobsData.map(job => ({
      id: job._id,
      title: job.title,
      start: job.scheduledDate,
      end: job.scheduledEndDate,
      color: '#4CAF50',
      extendedProps: { type: 'job', jobId: job._id }
    })).filter(event => event.start && event.end);

    const routeEvents = routesData.map(route => ({
      id: route._id,
      title: route.name,
      start: route.scheduledDate,
      end: route.scheduledEndDate,
      color: '#2196F3',
      extendedProps: { type: 'route', routeId: route._id }
    })).filter(event => event.start && event.end);

    setEvents([...jobEvents, ...routeEvents]);
  }, []);

  const handleAddRoute = async () => {
    if (!newRouteName.trim()) {
      setError('Please enter a route name');
      return;
    }
    try {
      const newRoute = await saveRoute({ name: newRouteName, jobs: [] });
      setRoutes(prevRoutes => [...prevRoutes, newRoute]);
      setNewRouteName('');
      setSuccessMessage('New route added successfully!');
    } catch (error) {
      console.error('Error adding new route:', error);
      setError('Failed to add new route. Please try again.');
    }
  };

  const handleDeleteRoute = async (routeId) => {
    try {
      await deleteRoute(routeId);
      
      // Move jobs back to unassigned pool
      const updatedJobs = jobs.map(job => 
        job.route === routeId ? { ...job, route: null } : job
      );
      
      setJobs(updatedJobs);
      setRoutes(prevRoutes => prevRoutes.filter(route => route._id !== routeId));
      
      // Update events
      setEvents(prevEvents => prevEvents.filter(event => 
        !(event.extendedProps.type === 'route' && event.extendedProps.routeId === routeId)
      ));

      setSuccessMessage('Route deleted successfully!');
    } catch (error) {
      console.error('Error deleting route:', error);
      setError('Failed to delete route. Please try again.');
    }
  };

  const onDragEnd = async (result) => {
    const { source, destination, draggableId } = result;
    if (!destination) return;

    try {
      if (source.droppableId === 'jobPool' && destination.droppableId.startsWith('route-')) {
        const routeId = destination.droppableId.split('-')[1];
        await moveJobToRoute(draggableId, routeId);
      } else if (source.droppableId.startsWith('route-') && destination.droppableId === 'jobPool') {
        const routeId = source.droppableId.split('-')[1];
        await moveJobToPool(draggableId, routeId);
      } else if (source.droppableId.startsWith('route-') && destination.droppableId.startsWith('route-')) {
        const sourceRouteId = source.droppableId.split('-')[1];
        const destRouteId = destination.droppableId.split('-')[1];
        await moveJobBetweenRoutes(draggableId, sourceRouteId, destRouteId);
      }
    } catch (error) {
      console.error('Error moving job:', error);
      setError('Failed to move job. Please try again.');
    }
  };

  const moveJobToRoute = async (jobId, routeId) => {
    const route = routes.find(r => r._id === routeId);
    if (route.jobs.includes(jobId)) {
      setError('This job is already in the route.');
      return;
    }

    try {
      const updatedJob = await updateJob(jobId, { route: routeId });
      const updatedRoute = await updateRoute(routeId, { $push: { jobs: jobId } });
      
      setJobs(prevJobs => prevJobs.map(job => job._id === jobId ? updatedJob : job));
      setRoutes(prevRoutes => prevRoutes.map(route => route._id === routeId ? updatedRoute : route));
      
      // Remove job from scheduling pool
      setEvents(prevEvents => prevEvents.filter(event => event.extendedProps.jobId !== jobId));
      
      setSuccessMessage('Job added to route successfully!');
    } catch (error) {
      console.error('Error moving job to route:', error);
      setError('Failed to add job to route. Please try again.');
    }
  };

  const moveJobToPool = async (jobId, routeId) => {
    try {
      const updatedJob = await updateJob(jobId, { route: null });
      const updatedRoute = await updateRoute(routeId, { $pull: { jobs: jobId } });
      
      setJobs(prevJobs => prevJobs.map(job => job._id === jobId ? updatedJob : job));
      setRoutes(prevRoutes => prevRoutes.map(route => route._id === routeId ? updatedRoute : route));
      
      // Add job back to scheduling pool
      const jobEvent = {
        id: updatedJob._id,
        title: updatedJob.title,
        start: updatedJob.scheduledDate,
        end: updatedJob.scheduledEndDate,
        color: '#4CAF50',
        extendedProps: { type: 'job', jobId: updatedJob._id }
      };
      setEvents(prevEvents => [...prevEvents, jobEvent]);
      
      setSuccessMessage('Job removed from route successfully!');
    } catch (error) {
      console.error('Error moving job to pool:', error);
      setError('Failed to remove job from route. Please try again.');
    }
  };

  const moveJobBetweenRoutes = async (jobId, sourceRouteId, destRouteId) => {
    try {
      const updatedJob = await updateJob(jobId, { route: destRouteId });
      await updateRoute(sourceRouteId, { $pull: { jobs: jobId } });
      await updateRoute(destRouteId, { $push: { jobs: jobId } });
      
      setJobs(prevJobs => prevJobs.map(job => job._id === jobId ? updatedJob : job));
      setRoutes(prevRoutes => prevRoutes.map(route => {
        if (route._id === sourceRouteId) {
          return { ...route, jobs: route.jobs.filter(id => id !== jobId) };
        } else if (route._id === destRouteId) {
          return { ...route, jobs: [...route.jobs, jobId] };
        }
        return route;
      }));
      
      setSuccessMessage('Job moved between routes successfully!');
    } catch (error) {
      console.error('Error moving job between routes:', error);
      setError('Failed to move job between routes. Please try again.');
    }
  };

  const handleEventDrop = async (info) => {
    const { event, oldEvent } = info;
    try {
      if (event.extendedProps.type === 'job') {
        await updateJob(event.id, {
          scheduledDate: event.start,
          scheduledEndDate: event.end
        });
      } else if (event.extendedProps.type === 'route') {
        await updateRoute(event.id, {
          scheduledDate: event.start,
          scheduledEndDate: event.end
        });
      }
      setSuccessMessage('Event updated successfully!');
    } catch (error) {
      console.error('Error updating event:', error);
      setError('Failed to update event. Please try again.');
      info.revert();
    }
  };

  const handleEventReceive = (info) => {
    const { event } = info;
    setPendingEvent(event);
    setShowStartTimeModal(true);
  };

  const handleStartTimeSubmit = async (startTime) => {
    setShowStartTimeModal(false);
    const event = pendingEvent;
    if (!event) return;

    const startDate = new Date(event.start);
    startDate.setHours(startTime.getHours(), startTime.getMinutes());
    const endDate = new Date(startDate.getTime() + 60 * 60 * 1000); // Add 1 hour for duration

    try {
      if (event.extendedProps.type === 'job') {
        await updateJob(event.id, {
          scheduledDate: startDate,
          scheduledEndDate: endDate
        });
      } else if (event.extendedProps.type === 'route') {
        await updateRoute(event.id, {
          scheduledDate: startDate,
          scheduledEndDate: endDate
        });
      }
      setEvents(prevEvents => [...prevEvents, { ...event, start: startDate, end: endDate }]);
      setSuccessMessage('Event scheduled successfully!');
    } catch (error) {
      console.error('Error scheduling event:', error);
      setError('Failed to schedule event. Please try again.');
    }

    setPendingEvent(null);
  };

  const renderRoutesList = () => (
    <DragDropContext onDragEnd={onDragEnd}>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {routes.map(route => (
          <Droppable key={route._id} droppableId={`route-${route._id}`}>
            {(provided, snapshot) => (
              <div
                ref={provided.innerRef}
                {...provided.droppableProps}
                className={`bg-gray-800 p-4 rounded-lg ${snapshot.isDraggingOver ? 'border-2 border-blue-500' : ''}`}
              >
                <h3 className="font-semibold mb-2 text-white">{route.name}</h3>
                {route.jobs.map((jobId, index) => {
                  const job = jobs.find(j => j._id === jobId);
                  if (!job) return null;
                  return (
                    <Draggable key={job._id} draggableId={job._id} index={index}>
                      {(provided) => (
                        <div
                          ref={provided.innerRef}
                          {...provided.draggableProps}
                          {...provided.dragHandleProps}
                          className="bg-gray-700 p-2 mb-2 rounded shadow text-white"
                        >
                          {job.title}
                        </div>
                      )}
                    </Draggable>
                  );
                })}
                {provided.placeholder}
                <button onClick={() => handleDeleteRoute(route._id)} className="mt-2 bg-red-600 text-white px-2 py-1 rounded text-sm hover:bg-red-700">
                  Delete Route
                </button>
              </div>
            )}
          </Droppable>
        ))}
      </div>
      <Droppable droppableId="jobPool">
        {(provided, snapshot) => (
          <div
            ref={provided.innerRef}
            {...provided.droppableProps}
            className={`mt-6 bg-gray-800 p-4 rounded-lg ${snapshot.isDraggingOver ? 'border-2 border-green-500' : ''}`}
          >
            <h3 className="font-semibold mb-2 text-white">Unassigned Jobs</h3>
            {jobs.filter(job => !job.route).map((job, index) => (
              <Draggable key={job._id} draggableId={job._id} index={index}>
                {(provided) => (
                  <div
                    ref={provided.innerRef}
                    {...provided.draggableProps}
                    {...provided.dragHandleProps}
                    className="bg-gray-700 p-2 mb-2 rounded shadow text-white"
                  >
                    {job.title}
                  </div>
                )}
              </Draggable>
            ))}
            {provided.placeholder}
          </div>
        )}
      </Droppable>
    </DragDropContext>
  );

  const renderCalendarView = () => (
    <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
      <div className="md:col-span-3">
      <MemoizedFullCalendar
          plugins={[dayGridPlugin, timeGridPlugin, interactionPlugin]}
          initialView="dayGridMonth"
          headerToolbar={{
            left: 'prev,next today',
            center: 'title',
            right: 'dayGridMonth,timeGridWeek,timeGridDay'
          }}
          editable={true}
          droppable={true}
          events={events}
          eventDrop={handleEventDrop}
          eventReceive={handleEventReceive}
          eventClick={handleEventClick}
          drop={(info) => {
            const droppedEvent = JSON.parse(info.draggedEl.dataset.event);
            handleEventReceive({ event: { ...droppedEvent, start: info.date } });
          }}
        />
      </div>
      <div>
        <div className="bg-gray-800 p-4 rounded-lg mb-4">
          <h3 className="font-semibold mb-2 text-white">Job Pool</h3>
          {jobs.filter(job => !job.route).map(job => (
            <div
              key={job._id}
              className="fc-event bg-gray-700 p-2 mb-2 rounded shadow text-white cursor-move"
              data-event={JSON.stringify({
                id: job._id,
                title: job.title,
                duration: '01:00',
                extendedProps: { type: 'job', jobId: job._id }
              })}
            >
              {job.title}
            </div>
          ))}
        </div>
        <div className="bg-gray-800 p-4 rounded-lg">
          <h3 className="font-semibold mb-2 text-white">Route Pool</h3>
          {routes.filter(route => !route.scheduledDate).map(route => (
            <div
              key={route._id}
              className="fc-event bg-gray-700 p-2 mb-2 rounded shadow text-white cursor-move"
              data-event={JSON.stringify({
                id: route._id,
                title: route.name,
                duration: '02:00',
                extendedProps: { type: 'route', routeId: route._id }
              })}
            >
              {route.name}
            </div>
          ))}
        </div>
      </div>
    </div>
  );

  const handleEventClick = (info) => {
    setSelectedEvent(info.event);
  };

  const renderMapView = () => (
    <div className="h-[600px]">
      <CustomerMap routes={routes} jobs={jobs} />
    </div>
  );

  const renderTimelineView = () => {
    const routesPerRow = 3;
    const rowCount = Math.ceil(routes.length / routesPerRow);
    
    return (
      <div className="space-y-8">
        {[...Array(rowCount)].map((_, rowIndex) => (
          <div key={rowIndex} className="grid grid-cols-1 md:grid-cols-3 gap-4">
            {routes.slice(rowIndex * routesPerRow, (rowIndex + 1) * routesPerRow).map(route => (
              <div key={route._id} className="bg-gray-800 p-4 rounded-lg">
                <h3 className="font-semibold text-xl mb-2 text-white">{route.name}</h3>
                <p className="text-green-400 font-bold mb-4">
                  Total Revenue: ${calculateRouteTotalRevenue(route).toFixed(2)}
                </p>
                <div className="space-y-2">
                  {route.jobs.map(jobId => {
                    const job = jobs.find(j => j._id === jobId);
                    if (!job) return null;
                    return (
                      <div key={job._id} className="bg-gray-700 p-2 rounded text-white">
                        <p className="font-semibold">{job.title}</p>
                        <p>Customer: {job.customer?.name}</p>
                        <p>Address: {job.customer?.address?.street}, {job.customer?.address?.city}</p>
                        <p>Status: {job.status}</p>
                        <p>Revenue: ${job.price || 0}</p>
                        <p>Estimated Duration: {job.estimatedDuration || 60} minutes</p>
                        <p>Scheduled: {job.scheduledDate ? new Date(job.scheduledDate).toLocaleString() : 'Not scheduled'}</p>
                      </div>
                    );
                  })}
                </div>
              </div>
            ))}
          </div>
        ))}
      </div>
    );
  };

  const calculateRouteTotalRevenue = (route) => {
    return route.jobs.reduce((total, jobId) => {
      const job = jobs.find(j => j._id === jobId);
      return total + (job?.price || 0);
    }, 0);
  };

  if (loading) return <div className="text-center mt-8">Loading...</div>;
  if (error) return <div className="text-center mt-8 text-red-600">{error}</div>;

  return (
    <ErrorBoundary>
      <div className="min-h-screen bg-gray-900 text-white">
        <Header />
        <main className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
          <div className="px-4 py-6 sm:px-0">
            <h1 className="text-3xl font-bold mb-6">Route Management</h1>

            {successMessage && (
              <div className="bg-green-100 border-l-4 border-green-500 text-green-700 p-4 mb-4" role="alert">
                <p>{successMessage}</p>
              </div>
            )}

            <div className="mb-6 flex items-center">
              <input
                type="text"
                value={newRouteName}
                onChange={(e) => setNewRouteName(e.target.value)}
                placeholder="Enter route name"
                className="mr-2 p-2 bg-gray-700 text-white rounded"
              />
              <button
                onClick={handleAddRoute}
                className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
              >
                Add New Route
              </button>
            </div>

            <div className="mb-6">
              <nav className="flex space-x-4" aria-label="Tabs">
                {['routes', 'calendar', 'map', 'timeline'].map((tab) => (
                  <button
                    key={tab}
                    onClick={() => setActiveTab(tab)}
                    className={`${
                      activeTab === tab
                        ? 'bg-blue-600 text-white'
                        : 'text-blue-300 hover:text-blue-100'
                    } px-3 py-2 font-medium text-sm rounded-md`}
                  >
                    {tab.charAt(0).toUpperCase() + tab.slice(1)}
                  </button>
                ))}
              </nav>
            </div>

            <div className="bg-gray-800 shadow overflow-hidden sm:rounded-lg p-6">
              {activeTab === 'routes' && renderRoutesList()}
              {activeTab === 'calendar' && renderCalendarView()}
              {activeTab === 'map' && renderMapView()}
              {activeTab === 'timeline' && renderTimelineView()}
            </div>
          </div>
        </main>
        {selectedEvent && (
          <EventModal
            event={selectedEvent}
            onClose={() => setSelectedEvent(null)}
            onSave={handleEventUpdate}
            onRemove={handleRemoveEvent}
          />
        )}
        {showStartTimeModal && (
          <StartTimeModal
            onClose={() => {
              setShowStartTimeModal(false);
              setPendingEvent(null);
            }}
            onSubmit={handleStartTimeSubmit}
          />
        )}
      </div>
    </ErrorBoundary>
  );
};

const EventModal = ({ event, onClose, onSave, onRemove }) => {
  const [title, setTitle] = useState(event.title);
  const [start, setStart] = useState(event.start);
  const [end, setEnd] = useState(event.end);

  const handleSave = () => {
    onSave({ ...event, title, start, end });
    onClose();
  };

  const handleRemove = () => {
    onRemove(event);
    onClose();
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white p-4 rounded-lg">
        <h2 className="text-xl font-bold mb-4">Edit Event</h2>
        <input
          type="text"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          className="w-full mb-2 p-2 border rounded"
        />
        <input
          type="datetime-local"
          value={start ? start.toISOString().slice(0, 16) : ''}
          onChange={(e) => setStart(new Date(e.target.value))}
          className="w-full mb-2 p-2 border rounded"
        />
        <input
          type="datetime-local"
          value={end ? end.toISOString().slice(0, 16) : ''}
          onChange={(e) => setEnd(new Date(e.target.value))}
          className="w-full mb-2 p-2 border rounded"
        />
        <div className="flex justify-end">
          <button onClick={onClose} className="mr-2 px-4 py-2 bg-gray-200 rounded">Cancel</button>
          <button onClick={handleSave} className="mr-2 px-4 py-2 bg-blue-500 text-white rounded">Save</button>
          <button onClick={handleRemove} className="px-4 py-2 bg-red-500 text-white rounded">Remove</button>
        </div>
      </div>
    </div>
  );
};

const StartTimeModal = ({ onClose, onSubmit }) => {
  const [startTime, setStartTime] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    const [hours, minutes] = startTime.split(':');
    const dateTime = new Date();
    dateTime.setHours(parseInt(hours, 10), parseInt(minutes, 10));
    onSubmit(dateTime);
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white p-4 rounded-lg">
        <h2 className="text-xl font-bold mb-4">Set Start Time</h2>
        <form onSubmit={handleSubmit}>
          <input
            type="time"
            value={startTime}
            onChange={(e) => setStartTime(e.target.value)}
            className="w-full mb-4 p-2 border rounded"
            required
          />
          <div className="flex justify-end">
            <button type="button" onClick={onClose} className="mr-2 px-4 py-2 bg-gray-200 rounded">Cancel</button>
            <button type="submit" className="px-4 py-2 bg-blue-500 text-white rounded">Set Time</button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default BuildRoutes;
