import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { DragDropContext } from 'react-beautiful-dnd';
import { useAuth } from '../../contexts/AuthContext';
import Header from '../../components/Header';
import RouteBuilder from './RouteBuilder';
import MapView from './MapView';
import CalendarView from './CalendarView';
import Scheduling from './Scheduling';
import { fetchJobs, fetchRoutes } from './api';

const BuildRoutes = () => {
  const { user } = useAuth();
  const navigate = useNavigate();
  const [jobs, setJobs] = useState([]);
  const [routes, setRoutes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [activeTab, setActiveTab] = useState('routes');
  const [successMessage, setSuccessMessage] = useState(null);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    setLoading(true);
    setError(null);
    try {
      const [jobsData, routesData] = await Promise.all([fetchJobs(), fetchRoutes()]);
      setJobs(jobsData);
      setRoutes(routesData);
    } catch (err) {
      console.error('Error fetching data:', err);
      setError('Failed to load data. Please try refreshing the page.');
    } finally {
      setLoading(false);
    }
  };

  const onDragEnd = (result) => {
    // Implement drag and drop logic here for moving jobs between routes
  };

  if (loading) return <div className="text-center mt-8">Loading...</div>;
  if (error) return <div className="text-center mt-8 text-red-600">{error}</div>;

  return (
    <DragDropContext onDragEnd={onDragEnd}>
      <div className="min-h-screen bg-background text-gray-800">
        <Header />
        <main className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
          <div className="px-4 py-6 sm:px-0">
            <div className="flex justify-between items-center mb-4">
              <h1 className="text-2xl font-bold">Route Management</h1>
              <button
                onClick={() => navigate('/route-manager')}
                className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
              >
                Go to Route Manager
              </button>
            </div>
            {successMessage && (
              <div className="bg-green-100 border-l-4 border-green-500 text-green-700 p-4 mb-4" role="alert">
                <p>{successMessage}</p>
              </div>
            )}
            <div className="mb-4">
              {/* Tab buttons */}
            </div>
            <div className="bg-surface p-4 rounded-b-lg rounded-tr-lg">
              {activeTab === 'routes' && (
                <RouteBuilder 
                  jobs={jobs}
                  routes={routes}
                  setJobs={setJobs}
                  setRoutes={setRoutes}
                  setSuccessMessage={setSuccessMessage}
                />
              )}
              {activeTab === 'map' && <MapView routes={routes} user={user} />}
              {activeTab === 'calendar' && <CalendarView routes={routes} />}
              {activeTab === 'scheduling' && <Scheduling routes={routes} setSuccessMessage={setSuccessMessage} />}
            </div>
          </div>
        </main>
      </div>
    </DragDropContext>
  );
};

export default BuildRoutes;
