import React, { useState, useEffect, useCallback } from 'react';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import axios from 'axios';

const BuildRoutes = () => {
  const [customers, setCustomers] = useState([]);
  const [routes, setRoutes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const fetchData = useCallback(async () => {
    setLoading(true);
    setError(null);
    try {
      const [customersResponse, routesResponse] = await Promise.all([
        axios.get('http://localhost:5000/api/customers', {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        }),
        axios.get('http://localhost:5000/api/routes', {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        })
      ]);
      setCustomers(customersResponse.data);
      setRoutes(routesResponse.data);
    } catch (err) {
      console.error('Error fetching data:', err);
      setError('Failed to load data. Please try refreshing the page.');
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    fetchData();
  }, [fetchData]);

  const onDragEnd = useCallback((result) => {
    const { source, destination } = result;

    if (!destination) return;

    try {
      if (source.droppableId === 'customerPool' && destination.droppableId.startsWith('route-')) {
        const routeIndex = parseInt(destination.droppableId.split('-')[1], 10);
        const [reorderedCustomer] = customers.splice(source.index, 1);
        routes[routeIndex].customers.splice(destination.index, 0, reorderedCustomer);
      } else if (source.droppableId.startsWith('route-') && destination.droppableId === 'customerPool') {
        const routeIndex = parseInt(source.droppableId.split('-')[1], 10);
        const [reorderedCustomer] = routes[routeIndex].customers.splice(source.index, 1);
        customers.splice(destination.index, 0, reorderedCustomer);
      } else if (source.droppableId.startsWith('route-') && destination.droppableId.startsWith('route-')) {
        const sourceRouteIndex = parseInt(source.droppableId.split('-')[1], 10);
        const destRouteIndex = parseInt(destination.droppableId.split('-')[1], 10);
        const [reorderedCustomer] = routes[sourceRouteIndex].customers.splice(source.index, 1);
        routes[destRouteIndex].customers.splice(destination.index, 0, reorderedCustomer);
      }

      setCustomers([...customers]);
      setRoutes([...routes]);
    } catch (err) {
      console.error('Error during drag and drop:', err);
      setError('An error occurred during drag and drop. Please try again.');
    }
  }, [customers, routes]);

  const addNewRoute = useCallback(() => {
    setRoutes(prevRoutes => [
      ...prevRoutes,
      { _id: Date.now().toString(), name: `Route ${prevRoutes.length + 1}`, customers: [] }
    ]);
  }, []);

  if (loading) return <div className="text-center mt-8">Loading...</div>;
  if (error) return <div className="text-center mt-8 text-red-600">{error}</div>;

  return (
    <div className="build-routes p-6">
      <h1 className="text-2xl font-bold mb-4">Build Routes</h1>
      <button onClick={addNewRoute} className="bg-blue-500 text-white px-4 py-2 rounded mb-4">
        Add New Route
      </button>
      <DragDropContext onDragEnd={onDragEnd}>
        <div className="flex space-x-4">
          <div className="routes-list w-1/2">
            <h2 className="text-xl font-semibold mb-2">Routes</h2>
            {routes.map((route, routeIndex) => (
              <Droppable key={route._id} droppableId={`route-${routeIndex}`}>
                {(provided) => (
                  <div {...provided.droppableProps} ref={provided.innerRef} className="bg-gray-100 p-2 rounded mb-4 min-h-[100px]">
                    <h3 className="font-semibold">{route.name}</h3>
                    {route.customers.map((customer, index) => (
                      <Draggable key={customer._id} draggableId={customer._id} index={index}>
                        {(provided) => (
                          <div
                            ref={provided.innerRef}
                            {...provided.draggableProps}
                            {...provided.dragHandleProps}
                            className="bg-white p-2 mb-2 rounded shadow"
                          >
                            {customer.name} - {customer.address?.street}
                          </div>
                        )}
                      </Draggable>
                    ))}
                    {provided.placeholder}
                  </div>
                )}
              </Droppable>
            ))}
          </div>
          <div className="customer-pool w-1/2">
            <h2 className="text-xl font-semibold mb-2">Customer Pool</h2>
            <Droppable droppableId="customerPool">
              {(provided) => (
                <div {...provided.droppableProps} ref={provided.innerRef} className="bg-gray-100 p-2 rounded min-h-[200px] max-h-[600px] overflow-y-auto">
                  {customers.map((customer, index) => (
                    <Draggable key={customer._id} draggableId={customer._id} index={index}>
                      {(provided) => (
                        <div
                          ref={provided.innerRef}
                          {...provided.draggableProps}
                          {...provided.dragHandleProps}
                          className="bg-white p-2 mb-2 rounded shadow"
                        >
                          {customer.name} - {customer.address?.street}
                        </div>
                      )}
                    </Draggable>
                  ))}
                  {provided.placeholder}
                </div>
              )}
            </Droppable>
          </div>
        </div>
      </DragDropContext>
    </div>
  );
};

export default BuildRoutes;
