import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import Header from '../components/Header';
import { ChevronLeftIcon, ChevronRightIcon, PlusIcon } from '@heroicons/react/24/outline';

const API_BASE_URL = 'http://localhost:5000/api';

const BuildRoutes = () => {
  const [routes, setRoutes] = useState({
    Monday: [[]],
    Tuesday: [[]],
    Wednesday: [[]],
    Thursday: [[]],
    Friday: [[]],
    Saturday: [[]],
    Sunday: [[]],
  });
  const [jobPool, setJobPool] = useState([]);
  const [selectedDay, setSelectedDay] = useState('Monday');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const fetchRouteAndJobs = useCallback(async () => {
    setLoading(true);
    setError(null);
    try {
      const [routesResponse, jobsResponse] = await Promise.all([
        axios.get(`${API_BASE_URL}/routes`, {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        }),
        axios.get(`${API_BASE_URL}/routes/jobs/available`, {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        })
      ]);

      const fetchedRoutes = routesResponse.data.reduce((acc, route) => {
        acc[route.day] = [route.jobs]; // Wrap route.jobs in an array
        return acc;
      }, {
        Monday: [[]],
        Tuesday: [[]],
        Wednesday: [[]],
        Thursday: [[]],
        Friday: [[]],
        Saturday: [[]],
        Sunday: [[]],
      });

      setRoutes(fetchedRoutes);
      setJobPool(jobsResponse.data || []);

    } catch (error) {
      console.error('Error fetching data:', error);
      setError('Failed to load routes and jobs. Please try again.');
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    fetchRouteAndJobs();
  }, [fetchRouteAndJobs]);

  const onDragEnd = async (result) => {
    const { source, destination } = result;
    if (!destination) return;

    const sourceList = source.droppableId.split('-')[0];
    const destList = destination.droppableId.split('-')[0];
    const sourceIndex = parseInt(source.droppableId.split('-')[1]);
    const destIndex = parseInt(destination.droppableId.split('-')[1]);

    let updatedRoutes = { ...routes };
    let updatedJobPool = [...jobPool];
    let movedJob;

    if (sourceList === 'jobPool') {
      [movedJob] = updatedJobPool.splice(source.index, 1);
      updatedRoutes[destList][destIndex].splice(destination.index, 0, movedJob);
    } else if (destList === 'jobPool') {
      [movedJob] = updatedRoutes[sourceList][sourceIndex].splice(source.index, 1);
      updatedJobPool.splice(destination.index, 0, movedJob);
    } else {
      [movedJob] = updatedRoutes[sourceList][sourceIndex].splice(source.index, 1);
      updatedRoutes[destList][destIndex].splice(destination.index, 0, movedJob);
    }

    setRoutes(updatedRoutes);
    setJobPool(updatedJobPool);

    try {
      if (destList !== 'jobPool') {
        await axios.put(`${API_BASE_URL}/routes/${destList}`, { jobs: updatedRoutes[destList][destIndex].map(job => job._id) }, {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        });
      }
      if (sourceList !== 'jobPool') {
        await axios.put(`${API_BASE_URL}/routes/${sourceList}`, { jobs: updatedRoutes[sourceList][sourceIndex].map(job => job._id) }, {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        });
      }
    } catch (error) {
      console.error('Error updating route:', error);
      setError('Failed to update route. Please try again.');
      fetchRouteAndJobs(); // Revert changes if update fails
    }
  };

  const handleCompleteRoute = async (routeIndex) => {
    try {
      await axios.post(`${API_BASE_URL}/routes/${selectedDay}/${routeIndex}/complete`, null, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      fetchRouteAndJobs(); // Refresh the data after completing the route
    } catch (error) {
      console.error('Error completing route:', error);
      setError('Failed to complete route. Please try again.');
    }
  };

  const addNewRoute = () => {
    setRoutes(prevRoutes => ({
      ...prevRoutes,
      [selectedDay]: [...prevRoutes[selectedDay], []]
    }));
  };

  const deleteRoute = (index) => {
    setRoutes(prevRoutes => ({
      ...prevRoutes,
      [selectedDay]: prevRoutes[selectedDay].filter((_, i) => i !== index)
    }));
  };

  const cycleRoutes = (direction) => {
    const totalRoutes = routes[selectedDay].length;
    setVisibleRoutes(prev => {
      const newStart = (prev.start + direction + totalRoutes) % totalRoutes;
      return {
        start: newStart,
        end: (newStart + 3) % totalRoutes
      };
    });
  };

  const renderJobList = (listId, jobs, isRoute = false, routeIndex = 0) => (
    <Droppable droppableId={`${listId}-${routeIndex}`}>
      {(provided) => (
        <div
          {...provided.droppableProps}
          ref={provided.innerRef}
          className={`bg-gray-800 p-4 rounded-lg shadow-lg ${isRoute ? 'h-[400px]' : 'h-[200px]'} overflow-y-auto`}
        >
          <h2 className="text-xl font-bold mb-4 text-white">
            {isRoute ? `Route ${routeIndex + 1}` : 'Job Pool'}
          </h2>
          {Array.isArray(jobs) && jobs.map((job, index) => (
            <Draggable key={job._id} draggableId={job._id} index={index}>
              {(provided) => (
                <div
                  ref={provided.innerRef}
                  {...provided.draggableProps}
                  {...provided.dragHandleProps}
                  className={`p-3 mb-2 rounded-md shadow-md transition-colors duration-200 
                    ${job.isRecurring ? 'bg-green-700 hover:bg-green-600' : 'bg-blue-700 hover:bg-blue-600'}`}
                >
                  <p className="font-bold text-white">{job.title}</p>
                  <p className="text-sm text-gray-300">{job.customer?.name}</p>
                  <p className="text-xs text-gray-400">{job.location?.address}</p>
                  <p className="text-xs text-gray-400">Revenue: ${job.price}</p>
                  <p className="text-xs text-gray-400">Type: {job.isRecurring ? 'Recurring' : 'One-time'}</p>
                </div>
              )}
            </Draggable>
          ))}
          {provided.placeholder}
        </div>
      )}
    </Droppable>
  );

  if (loading) {
    return <div className="text-center mt-8 text-white">Loading...</div>;
  }

  if (error) {
    return (
      <div className="text-center mt-8 text-red-500">
        <p>{error}</p>
        <button
          onClick={fetchRouteAndJobs}
          className="bg-blue-500 text-white px-4 py-2 rounded-md mt-4 hover:bg-blue-600 transition-colors duration-200"
        >
          Retry
        </button>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-900 text-white">
      <Header />
      <main className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        <h1 className="text-3xl font-bold mb-6">Build Routes</h1>
        <div className="flex justify-between items-center mb-6">
          <button onClick={() => setSelectedDay(prev => {
            const days = ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday', 'Sunday'];
            const currentIndex = days.indexOf(prev);
            return days[(currentIndex - 1 + 7) % 7];
          })} className="bg-blue-500 text-white p-2 rounded-md hover:bg-blue-600 transition-colors duration-200">
            <ChevronLeftIcon className="h-5 w-5" />
          </button>
          <h2 className="text-2xl font-semibold">{selectedDay}</h2>
          <button onClick={() => setSelectedDay(prev => {
            const days = ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday', 'Sunday'];
            const currentIndex = days.indexOf(prev);
            return days[(currentIndex + 1) % 7];
          })} className="bg-blue-500 text-white p-2 rounded-md hover:bg-blue-600 transition-colors duration-200">
            <ChevronRightIcon className="h-5 w-5" />
          </button>
        </div>
        <DragDropContext onDragEnd={onDragEnd}>
          <div className="mb-6">
            <div className="flex justify-between items-center mb-4">
              <button onClick={() => cycleRoutes(-1)} className="bg-blue-500 text-white p-2 rounded-md hover:bg-blue-600 transition-colors duration-200">
                <ChevronLeftIcon className="h-5 w-5" />
              </button>
              <button onClick={addNewRoute} className="bg-green-500 text-white px-4 py-2 rounded-md hover:bg-green-600 transition-colors duration-200 flex items-center">
                <PlusIcon className="h-5 w-5 mr-2" />
                Add Route
              </button>
              <button onClick={() => cycleRoutes(1)} className="bg-blue-500 text-white p-2 rounded-md hover:bg-blue-600 transition-colors duration-200">
                <ChevronRightIcon className="h-5 w-5" />
              </button>
            </div>
            <div className="grid grid-cols-3 gap-4">
              {routes[selectedDay].slice(visibleRoutes.start, visibleRoutes.end).map((route, index) => (
                <div key={index} className="bg-gray-800 p-4 rounded-lg shadow-lg">
                  <div className="flex justify-between items-center mb-4">
                    <h3 className="text-xl font-bold">Route {visibleRoutes.start + index + 1}</h3>
                    <div>
                      <button
                        onClick={() => handleCompleteRoute(visibleRoutes.start + index)}
                        className="bg-green-500 text-white px-3 py-1 rounded-md hover:bg-green-600 transition-colors duration-200 mr-2"
                      >
                        Complete
                      </button>
                      <button
                        onClick={() => deleteRoute(visibleRoutes.start + index)}
                        className="bg-red-500 text-white p-1 rounded-md hover:bg-red-600 transition-colors duration-200"
                      >
                        <TrashIcon className="h-5 w-5" />
                      </button>
                    </div>
                  </div>
                  {renderJobList(selectedDay, route, true, visibleRoutes.start + index)}
                </div>
              ))}
            </div>
          </div>
          <div className="mt-8">
            <h3 className="text-xl font-bold mb-4">Job Pool</h3>
            {renderJobList('jobPool', jobPool)}
          </div>
        </DragDropContext>
      </main>
    </div>
  );
};

export default BuildRoutes;
