// frontend/src/api/dashboardApi.js

import axios from 'axios';

// Base URL for API requests; use environment variables for flexibility
const API_URL = process.env.REACT_APP_API_BASE_URL || 'http://localhost:5000/api';

/**
 * Function to retrieve headers with the Authorization token
 * @returns {Object} - Headers object containing the Bearer token
 */
const getHeaders = () => {
  const token = localStorage.getItem('token');
  console.log('Using token:', token); // Remove or comment out in production
  return {
    headers: { Authorization: `Bearer ${token}` },
  };
};

/**
 * Fetch counts of customers, jobs, and employees from /api/analytics/counts
 * @returns {Promise<Object>} - Returns an object containing the counts
 */
export const fetchAnalyticsCounts = async () => {
  try {
    const response = await axios.get(`${API_URL}/analytics/counts`, getHeaders());
    if (response.data.success) {
      return response.data.data;
    } else {
      throw new Error('Failed to fetch analytics counts');
    }
  } catch (error) {
    console.error('Error fetching analytics counts:', error.response?.data || error.message);
    throw error;
  }
};

// Existing API functions for fetching jobs, customers, employees, etc.
// ...

export default {
  fetchAnalyticsCounts,
  // ... other functions
};
