// src/components/ProfileTabs/BadgesTab.js
import React from 'react';

const BadgesTab = ({ badges }) => {
  return (
    <div className="space-y-6">
      <h2 className="text-2xl font-semibold mb-4">Your Badges</h2>
      {badges && badges.length > 0 ? (
        <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
          {badges.map((badge, index) => (
            <div key={index} className="bg-surface p-4 rounded flex items-center">
              <div className="mr-4">
                {/* Badge Icon */}
                <img src={badge.iconUrl || '/default-badge.png'} alt={badge.name} className="h-12 w-12" />
              </div>
              <div>
                <h3 className="font-semibold">{badge.name}</h3>
                <p className="text-sm text-gray-500">{badge.description}</p>
              </div>
            </div>
          ))}
        </div>
      ) : (
        <p className="text-gray-500">You haven't earned any badges yet.</p>
      )}
    </div>
  );
};

export default BadgesTab;
