import React, { useState, useEffect } from 'react';
import axios from 'axios';

const API_URL = 'http://localhost:5000/api'; // Update as needed

const Profile = () => {
  const [businessInfo, setBusinessInfo] = useState({
    name: '',
    address: '',
    phone: '',
    email: '',
    website: '',
  });
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  useEffect(() => {
    // Fetch existing business info
    const fetchBusinessInfo = async () => {
      try {
        const response = await axios.get(`${API_URL}/business-info`, {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        });
        setBusinessInfo(response.data);
      } catch (error) {
        setError('Failed to fetch business info.');
      }
    };

    fetchBusinessInfo();
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setBusinessInfo({ ...businessInfo, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');

    try {
      const response = await axios.post(`${API_URL}/business-info`, businessInfo, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setSuccess('Business information updated successfully!');
    } catch (error) {
      setError('Failed to update business information.');
    }
  };

  return (
    <div>
      <h1>Profile Page</h1>

      {error && <p style={{ color: 'red' }}>{error}</p>}
      {success && <p style={{ color: 'green' }}>{success}</p>}

      <form onSubmit={handleSubmit}>
        <div>
          <label>Name:</label>
          <input
            type="text"
            name="name"
            value={businessInfo.name}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Address:</label>
          <input
            type="text"
            name="address"
            value={businessInfo.address}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Phone:</label>
          <input
            type="tel"
            name="phone"
            value={businessInfo.phone}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Email:</label>
          <input
            type="email"
            name="email"
            value={businessInfo.email}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Website:</label>
          <input
            type="url"
            name="website"
            value={businessInfo.website}
            onChange={handleChange}
            required
          />
        </div>

        <button type="submit">Update Info</button>
      </form>
    </div>
  );
};

export default Profile;
