// src/components/ProtectedRoute.js
import React from 'react';
import { Navigate, useLocation } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';

const ProtectedRoute = ({ children }) => {
  const { user, loading } = useAuth();
  const location = useLocation();

  if (loading) {
    return <div className="text-center mt-20">Loading...</div>; // Replace with a spinner if desired
  }

  if (!user) {
    // User is not authenticated, redirect to sign-in page
    return <Navigate to="/signin" state={{ from: location }} replace />;
  }

  if (!user.subscriptionActive) {
    // User does not have an active subscription, redirect to pricing page
    return <Navigate to="/pricing" replace />;
  }

  // User is authenticated and has an active subscription, allow access
  return children;
};

export default ProtectedRoute;
