import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import Header from '../components/Header';

const API_BASE_URL = 'http://localhost:5000/api'; // Update this to match your backend URL

const BuildRoutes = () => {
  const [routes, setRoutes] = useState({
    Monday: [],
    Tuesday: [],
    Wednesday: [],
    Thursday: [],
    Friday: [],
    Saturday: [],
    Sunday: [],
    jobPool: []
  });
  const [selectedDay, setSelectedDay] = useState('Monday');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const fetchRouteAndJobs = useCallback(async () => {
    setLoading(true);
    setError(null);
    try {
      const [routeResponse, jobsResponse] = await Promise.all([
        axios.get(`${API_BASE_URL}/routes/${selectedDay}`, {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        }),
        axios.get(`${API_BASE_URL}/routes/jobs/available`, {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        })
      ]);

      const scheduledJobs = routeResponse?.data?.jobs || [];
      const availableJobs = jobsResponse?.data || [];

      // Filter out jobs that are already scheduled for the selected day
      const jobPool = availableJobs.filter(job => !scheduledJobs.some(scheduledJob => scheduledJob._id === job._id));

      setRoutes(prevRoutes => ({
        ...prevRoutes,
        [selectedDay]: scheduledJobs,
        jobPool: jobPool,
      }));

      console.log('Scheduled jobs:', scheduledJobs);
      console.log('Job pool:', jobPool);

    } catch (error) {
      console.error('Error fetching data:', error);
      setError('Failed to load routes and jobs. Please try again.');
    } finally {
      setLoading(false);
    }
  }, [selectedDay]);

  useEffect(() => {
    fetchRouteAndJobs();
  }, [fetchRouteAndJobs, selectedDay]);

  const onDragEnd = async (result) => {
    const { source, destination } = result;
    if (!destination) return;

    const sourceList = source.droppableId;
    const destList = destination.droppableId;

    const newRoutes = { ...routes };
    const [movedJob] = newRoutes[sourceList].splice(source.index, 1);
    newRoutes[destList].splice(destination.index, 0, movedJob);

    setRoutes(newRoutes);

    if (destList !== 'jobPool') {
      try {
        await axios.put(`${API_BASE_URL}/routes/${selectedDay}`, { jobs: newRoutes[selectedDay] }, {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        });
      } catch (error) {
        console.error('Error updating route:', error);
        setError('Failed to update route. Please try again.');
        fetchRouteAndJobs(); // Revert changes if update fails
      }
    }
  };

  const handleCompleteRoute = async () => {
    try {
      await axios.post(`${API_BASE_URL}/routes/${selectedDay}/complete`, null, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      fetchRouteAndJobs(); // Refresh the data after completing the route
    } catch (error) {
      console.error('Error completing route:', error);
      setError('Failed to complete route. Please try again.');
    }
  };

  const calculateRouteRevenue = (jobs) => {
    return jobs.reduce((total, job) => total + job.price, 0);
  };

  const renderJobList = (listId, jobs) => (
    <Droppable droppableId={listId}>
      {(provided) => (
        <div
          {...provided.droppableProps}
          ref={provided.innerRef}
          className="bg-gray-800 p-4 rounded min-h-[200px]"
        >
          <h2 className="text-xl font-bold mb-2 text-white">
            {listId === 'jobPool' ? 'Available Jobs' : `${selectedDay} Route`}
          </h2>
          {jobs.map((job, index) => (
            <Draggable key={job._id} draggableId={job._id} index={index}>
              {(provided) => (
                <div
                  ref={provided.innerRef}
                  {...provided.draggableProps}
                  {...provided.dragHandleProps}
                  className="bg-gray-700 p-2 mb-2 rounded"
                >
                  <p className="font-bold text-white">{job.title}</p>
                  <p className="text-sm text-gray-300">{job.customer?.name}</p>
                  <p className="text-xs text-gray-400">{job.location?.address}</p>
                  <p className="text-xs text-gray-500">Revenue: ${job.price}</p>
                  <p className="text-xs text-gray-500">Type: {job.isRecurring ? `Recurring (${job.recurrencePattern})` : 'One-time'}</p>
                  <p className="text-xs text-gray-500">Last Service: {job.lastServiceDate ? new Date(job.lastServiceDate).toLocaleDateString() : 'N/A'}</p>
                  <p className="text-xs text-gray-500">Qty Served: {job.completionCount}</p>
                </div>
              )}
            </Draggable>
          ))}
          {provided.placeholder}
        </div>
      )}
    </Droppable>
  );

  if (loading) {
    return <div className="text-center mt-8 text-white">Loading...</div>;
  }

  if (error) {
    return (
      <div className="text-center mt-8 text-red-500">
        <p>{error}</p>
        <button
          onClick={fetchRouteAndJobs}
          className="bg-blue-500 text-white p-2 rounded mt-4"
        >
          Retry
        </button>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-900 text-white">
      <Header />
      <main className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        <h1 className="text-3xl font-bold mb-4">Build Routes</h1>
        <div className="flex justify-between items-center mb-4">
          <select
            value={selectedDay}
            onChange={(e) => setSelectedDay(e.target.value)}
            className="p-2 bg-gray-800 rounded text-white"
          >
            {['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday', 'Sunday'].map(day => (
              <option key={day} value={day}>{day}</option>
            ))}
          </select>
          <button
            onClick={handleCompleteRoute}
            className="bg-green-500 text-white p-2 rounded"
          >
            Complete Route
          </button>
        </div>
        <DragDropContext onDragEnd={onDragEnd}>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            {renderJobList(selectedDay, routes[selectedDay] || [])}
            {renderJobList('jobPool', routes.jobPool || [])}
          </div>
        </DragDropContext>
        <div className="mt-4 text-right">
          <p className="text-xl font-bold">
            Total Route Revenue: ${calculateRouteRevenue(routes[selectedDay] || [])}
          </p>
        </div>
      </main>
    </div>
  );
};

export default BuildRoutes;
