import React, { useState, useEffect } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { Link } from 'react-router-dom';
import { Line, Bar } from 'react-chartjs-2';
import { PlusIcon, UsersIcon, MapIcon, DocumentIcon } from '@heroicons/react/24/outline';
import Header from '../components/Header';
import { fetchJobs } from '../api/dashboardApi';

const Dashboard = () => {
  const { user } = useAuth();
  const [jobData, setJobData] = useState([]);
  const [timeRange, setTimeRange] = useState('30');
  const [analytics, setAnalytics] = useState({
    revenueOverTime: null,
    oneTimeVsRecurring: null,
    averageDurationAndRate: null,
    pastDueRecurring: null
  });

  useEffect(() => {
    fetchDashboardData();
  }, [timeRange]);

  const fetchDashboardData = async () => {
    try {
      const jobs = await fetchJobs();
      calculateAnalytics(jobs);
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
    }
  };

  const calculateAnalytics = (jobs) => {
    const filteredJobs = jobs.filter((job) => filterByTimeRange(job, timeRange));

    // Revenue Over Time
    const revenueOverTime = calculateRevenueOverTime(filteredJobs);

    // One-Time vs Recurring Revenue
    const oneTimeVsRecurring = calculateOneTimeVsRecurringRevenue(filteredJobs);

    // Average Duration and Rate
    const averageDurationAndRate = calculateAverageDurationAndRate(filteredJobs);

    setAnalytics({
      revenueOverTime,
      oneTimeVsRecurring,
      averageDurationAndRate
    });
  };

  const filterByTimeRange = (job, timeRange) => {
    const endDate = new Date();
    const startDate = new Date(endDate);
    startDate.setDate(endDate.getDate() - parseInt(timeRange));
    const jobDate = new Date(job.date);
    return jobDate >= startDate && jobDate <= endDate;
  };

  const calculateRevenueOverTime = (jobs) => {
    const revenueOverTime = jobs.reduce((acc, job) => {
      const date = new Date(job.date).toISOString().split('T')[0];
      acc[date] = (acc[date] || 0) + job.price;
      return acc;
    }, {});

    return {
      labels: Object.keys(revenueOverTime),
      data: Object.values(revenueOverTime)
    };
  };

  const calculateOneTimeVsRecurringRevenue = (jobs) => {
    const oneTimeRevenue = jobs
      .filter((job) => job.isRecurring === false)
      .reduce((sum, job) => sum + job.price, 0);

    const recurringRevenue = jobs
      .filter((job) => job.isRecurring === true)
      .reduce((sum, job) => sum + job.price, 0);

    return {
      oneTime: oneTimeRevenue,
      recurring: recurringRevenue
    };
  };

  const calculateAverageDurationAndRate = (jobs) => {
    const totalDuration = jobs.reduce((sum, job) => sum + job.estimatedDuration, 0);
    const totalRevenue = jobs.reduce((sum, job) => sum + job.price, 0);
    const averageDuration = totalDuration / jobs.length;
    const hourlyRate = totalDuration > 0 ? (totalRevenue / totalDuration) * 60 : 0;

    return {
      averageDuration: averageDuration || 0,
      hourlyRate: hourlyRate || 0
    };
  };

  const renderAnalytics = () => {
    return (
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {/* Revenue Over Time */}
        <div className="bg-gray-800 p-4 rounded-lg shadow">
          <h3 className="text-lg leading-6 font-medium text-white">Revenue Over Time</h3>
          {analytics.revenueOverTime && (
            <Line
              data={{
                labels: analytics.revenueOverTime.labels,
                datasets: [{
                  label: 'Revenue',
                  data: analytics.revenueOverTime.data,
                  borderColor: 'rgb(75, 192, 192)',
                  tension: 0.1
                }]
              }}
              options={{
                responsive: true,
                scales: {
                  x: { type: 'time' },
                  y: { beginAtZero: true }
                }
              }}
            />
          )}
        </div>

        {/* One-Time vs Recurring Jobs */}
        <div className="bg-gray-800 p-4 rounded-lg shadow">
          <h3 className="text-lg leading-6 font-medium text-white">One-Time vs Recurring Revenue</h3>
          <Bar
            data={{
              labels: ['One-Time', 'Recurring'],
              datasets: [{
                label: 'Revenue',
                data: [analytics.oneTimeVsRecurring?.oneTime, analytics.oneTimeVsRecurring?.recurring],
                backgroundColor: ['#FF6384', '#36A2EB'],
              }]
            }}
            options={{
              responsive: true,
              scales: {
                y: { beginAtZero: true }
              }
            }}
          />
        </div>

        {/* Average Job Duration & Hourly Rate */}
        <div className="bg-gray-800 p-4 rounded-lg shadow">
          <h3 className="text-lg leading-6 font-medium text-white">Avg. Duration & Hourly Rate</h3>
          <div className="mt-2">
            <p className="text-xl text-white">Duration: {analytics.averageDurationAndRate?.averageDuration.toFixed(2)} mins</p>
            <p className="text-xl text-white">Hourly Rate: ${analytics.averageDurationAndRate?.hourlyRate.toFixed(2)}</p>
          </div>
        </div>
      </div>
    );
  };

  return (
    <div className="min-h-screen bg-gray-900 text-white">
      <Header />
      <main className="max-w-7xl mx-auto py-6 px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between items-center mb-6">
          <h1 className="text-3xl font-bold">Welcome, {user?.name || 'User'}</h1>
          <select
            value={timeRange}
            onChange={(e) => setTimeRange(e.target.value)}
            className="bg-gray-800 text-white px-4 py-2 rounded-md"
          >
            <option value="7">Past 7 days</option>
            <option value="30">Past 30 days</option>
            <option value="90">Past 90 days</option>
            <option value="365">Past Year</option>
          </select>
        </div>

        {/* Quick Actions */}
        <div className="mb-8">
          <h2 className="text-2xl font-semibold mb-4">Quick Actions</h2>
          <div className="grid grid-cols-2 gap-4 sm:grid-cols-3 lg:grid-cols-5">
            <QuickActionButton to="/jobs" icon={PlusIcon} text="Create New Job" />
            <QuickActionButton to="/customers" icon={PlusIcon} text="Add New Customer" />
            <QuickActionButton to="/add-employee" icon={UsersIcon} text="Add New Employee" />
            <QuickActionButton to="/generate-report" icon={DocumentIcon} text="Generate Report" />
            <QuickActionButton to="/build-routes" icon={MapIcon} text="Build Routes" />
          </div>
        </div>

        {/* Analytics */}
        {renderAnalytics()}
      </main>
    </div>
  );
};

const QuickActionButton = ({ to, icon: Icon, text }) => (
  <Link
    to={to}
    className="bg-blue-500 text-white px-4 py-2 rounded-md text-sm font-medium hover:bg-blue-600 flex items-center justify-center"
  >
    <Icon className="h-5 w-5 mr-2" />
    {text}
  </Link>
);

export default Dashboard;
