import React, { useState, useEffect, useCallback } from 'react';
import {
  ChevronLeftIcon,
  ChevronRightIcon,
  PlusIcon,
  TrashIcon,
  ChevronDownIcon,
  ChevronUpIcon,
} from '@heroicons/react/24/outline';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import { CheckIcon } from '@heroicons/react/24/solid';
import {
  API_BASE_URL,
  DAYS_OF_WEEK,
  defaultRoutes,
  validateRoutesStructure,
  fetchWithAuth,
} from '../components/utils';
import Header from '../components/Header';
import debounce from 'lodash/debounce';
import { ClipLoader } from 'react-spinners';

const BuildRoutes = () => {
  const [routes, setRoutes] = useState(defaultRoutes);
  const [jobPool, setJobPool] = useState([]);
  const [selectedDay, setSelectedDay] = useState(DAYS_OF_WEEK[0]);
  const [loading, setLoading] = useState(false);
  const [saving, setSaving] = useState(false);
  const [error, setError] = useState(null);
   const [successMessage, setSuccessMessage] = useState('');  // New state for success messages
  const [visibleRoutesRange, setVisibleRoutesRange] = useState({ start: 0, end: 3 });
  const [expandedJobs, setExpandedJobs] = useState({});
  const [selectedJobs, setSelectedJobs] = useState([]);
  const [employees, setEmployees] = useState([]);
  const [crews, setCrews] = useState([]);
  const [confirmDialog, setConfirmDialog] = useState({ isOpen: false, routeIndex: null });
  
  const initializeData = useCallback(async () => {
  setLoading(true);
  try {
    const [
      fetchedRoutesResponse,
      fetchedJobsResponse,
      fetchedEmployeesResponse,
      fetchedCrewsResponse,
    ] = await Promise.all([
      fetchWithAuth('/routes'),
      fetchWithAuth('/routes/jobs/available'),
      fetchWithAuth('/employees'),
      fetchWithAuth('/crews'),
    ]);

    const fetchedRoutes = fetchedRoutesResponse?.routes || defaultRoutes;
    let fetchedJobs = fetchedJobsResponse || [];
    const fetchedEmployees = fetchedEmployeesResponse || [];
    const fetchedCrews = fetchedCrewsResponse || [];

    const validatedRoutes = validateRoutesStructure(fetchedRoutes);

    // Filter bi-weekly jobs based on last service date
    const now = new Date();
    fetchedJobs = fetchedJobs.filter((job) => {
      if (job.recurrencePattern === 'Bi-weekly' && job.lastServiceDate) {
        const lastServiceDate = new Date(job.lastServiceDate);
        const daysSinceLastService = (now - lastServiceDate) / (1000 * 60 * 60 * 24); // Days since last service
        return daysSinceLastService > 7; // Only include jobs where last service was more than 7 days ago
      }
      return true;
    });

    setRoutes(validatedRoutes);
    setJobPool(fetchedJobs);
    setEmployees(fetchedEmployees);
    setCrews(fetchedCrews);
  } catch (err) {
    console.error('Error fetching data:', err);
    setError('Failed to load routes, jobs, employees, and crews. Please refresh the page.');
  } finally {
    setLoading(false);
  }
}, []);

  useEffect(() => {
    initializeData();
  }, [initializeData]);

  const saveRoutes = useCallback(async (updatedRoutes = routes) => {
    setSaving(true);
    try {
      const routesToSave = Object.entries(updatedRoutes).reduce((acc, [day, dayRoutes]) => {
        acc[day] = dayRoutes.map(route => ({
          index: route.index,
          jobs: route.jobs.map(job => (typeof job === 'string' ? job : job._id)),
          employee: route.employee?._id,
          crew: route.crew?._id,
        }));
        return acc;
      }, {});
  
      await fetchWithAuth('/routes', 'PUT', { routes: routesToSave });
      console.log('Routes saved successfully:', routesToSave);
    } catch (err) {
      console.error('Error saving routes:', err);
      setError('Failed to save routes. Please try again.');
    } finally {
      setSaving(false);
    }
  }, [routes]);

  const debouncedSaveRoutes = useCallback(debounce(saveRoutes, 1000), [saveRoutes]);

  const onDragEnd = useCallback((result) => {
    const { source, destination } = result;
  
    if (!destination) {
      console.log("Drag operation cancelled or no destination found.");
      return;
    }
  
    console.log("Drag started from:", source.droppableId, "to", destination.droppableId);
    console.log("Before moving jobs:", { jobPool, routes });

    setRoutes((prevRoutes) => {
      const updatedRoutes = { ...prevRoutes };
      const sourceId = source.droppableId.split('-');
      const destId = destination.droppableId.split('-');
  
      const sourceRouteIndex = sourceId[0] === 'route' ? parseInt(sourceId[1], 10) : null;
      const destRouteIndex = destId[0] === 'route' ? parseInt(destId[1], 10) : null;
  
      // Moving from job pool to a route
      if (source.droppableId === 'jobPool-0' && destRouteIndex !== null) {
        console.log("Moving jobs from job pool to route:", destRouteIndex);
  
        const selectedJobsToMove = selectedJobs.length > 0 
          ? jobPool.filter(job => selectedJobs.includes(job._id)) 
          : [jobPool[source.index]];  // Only the single dragged job
  
        console.log("Selected jobs to move from job pool:", selectedJobsToMove);
  
        // Add jobs to the route
        selectedJobsToMove.forEach((job, index) => {
          updatedRoutes[selectedDay][destRouteIndex].jobs.splice(destination.index + index, 0, job);
          console.log(`Job added to route ${destRouteIndex}:`, job);
        });
  
        // Remove moved jobs from job pool
        const updatedJobPool = jobPool.filter(job => !selectedJobs.includes(job._id));
        if (selectedJobs.length === 0) {
          // If no jobs were selected, remove the single dragged job from the pool
          updatedJobPool.splice(source.index, 1);
        }
        setJobPool(updatedJobPool);
  
        console.log("Updated job pool after move:", updatedJobPool);
      }
  
      // Moving between routes or within the same route
      if (sourceRouteIndex !== null && destRouteIndex !== null) {
        console.log(`Moving jobs from route ${sourceRouteIndex} to route ${destRouteIndex}`);
  
        selectedJobs.forEach((jobId, index) => {
          // Find job in the source route
          const jobIndex = updatedRoutes[selectedDay][sourceRouteIndex].jobs.findIndex(job => job._id === jobId);
          const [removedJob] = updatedRoutes[selectedDay][sourceRouteIndex].jobs.splice(jobIndex, 1);
  
          console.log(`Removed job ${jobId} from route ${sourceRouteIndex}`, removedJob);
  
          // Add job to destination route
          if (removedJob) {
            updatedRoutes[selectedDay][destRouteIndex].jobs.splice(destination.index, 0, removedJob);
            console.log(`Added job ${jobId} to route ${destRouteIndex}`);
          }
        });
      }
  
      console.log("After moving jobs:", { jobPool, updatedRoutes });
  
      setSelectedJobs([]); // Clear selected jobs after moving
      return updatedRoutes;
    });

    debouncedSaveRoutes();
  }, [jobPool, selectedJobs, selectedDay, debouncedSaveRoutes]);

  const addNewRoute = useCallback(async () => {
    const newRouteState = {
      ...routes,
      [selectedDay]: [
        ...routes[selectedDay],
        { index: routes[selectedDay].length, jobs: [] },
      ],
    };
    setRoutes(newRouteState);
    await saveRoutes(newRouteState);
  }, [routes, selectedDay, saveRoutes]);

  const deleteRoute = useCallback(async (routeIndex) => {
    try {
      setRoutes(prevRoutes => {
        const updatedRoutes = { ...prevRoutes };
        const deletedRoute = updatedRoutes[selectedDay].splice(routeIndex, 1)[0];
        
        setJobPool(prevJobPool => [...prevJobPool, ...deletedRoute.jobs]);
        
        updatedRoutes[selectedDay] = updatedRoutes[selectedDay].map((route, idx) => ({
          ...route,
          index: idx,
        }));
        
        return updatedRoutes;
      });
  
      await fetchWithAuth(`/routes/${selectedDay}/${routeIndex}`, 'DELETE');
  
      await saveRoutes(); // Ensure routes are saved to keep consistent state
    } catch (error) {
      console.error('Error deleting route:', error);
      setError('Failed to delete route. Please try again.');
    }
  }, [selectedDay, saveRoutes]);

  const removeJobFromRoute = async (routeIndex, jobIndex) => {
    try {
      setRoutes(prevRoutes => {
        const updatedRoutes = { ...prevRoutes };
        const removedJob = updatedRoutes[selectedDay][routeIndex].jobs.splice(jobIndex, 1)[0];
        setJobPool(prevJobPool => [...prevJobPool, removedJob]);
        return updatedRoutes;
      });
  
      await fetchWithAuth(`/routes/${selectedDay}/${routeIndex}`, 'PUT', {
        index: routeIndex,
        jobs: routes[selectedDay][routeIndex].jobs
      });
  
      await saveRoutes();
    } catch (error) {
      console.error('Error removing job from route:', error);
      setError('Failed to remove job from route. Please try again.');
    }
  };

  const [assigningRoutes, setAssigningRoutes] = useState({});
  const assignRoute = async (routeIndex, assigneeId) => {
    const [type, id] = assigneeId.split('-');
    setAssigningRoutes(prev => ({ ...prev, [routeIndex]: true }));
  
    try {
      console.log(`Assigning ${type} with ID: ${id} to route ${routeIndex} for day ${selectedDay}`);
      
      // Sending the appropriate payload to the API
      const response = await fetchWithAuth(`/routes/${selectedDay}/${routeIndex}/assign`, 'PUT', {
        [type]: id,
      });
  
      // Since fetchWithAuth returns parsed data directly, no need to check response.ok or status
      console.log('Response from server:', response);
  
      // Ensure the response contains the expected fields, otherwise consider it an error
      if (!response || !response._id) {
        throw new Error('Invalid response format');
      }
  
      // Assuming the response is valid and contains the updated route
      const updatedRoute = response; // Use the response directly since fetchWithAuth returns the data
      setRoutes((prevRoutes) => {
        const updatedDayRoutes = prevRoutes[selectedDay].map((route) => {
          if (route.index === routeIndex) {
            return {
              ...route,
              employee: type === 'employee' ? { _id: id, name: employees.find(e => e._id === id)?.name } : null,
              crew: type === 'crew' ? { _id: id, name: crews.find(c => c._id === id)?.name } : null,
            };
          }
          return route;
        });
  
        return {
          ...prevRoutes,
          [selectedDay]: updatedDayRoutes,
        };
      });
      console.log(`Route assigned successfully: day ${selectedDay}, index ${routeIndex}, ${type}: ${id}`);
    } catch (error) {
      console.error('Error assigning route:', error);
      setError('Failed to assign route. Please try again.');
    } finally {
      setAssigningRoutes(prev => ({ ...prev, [routeIndex]: false }));
    }
  };
  
  const handleJobSelection = (job) => {
    setSelectedJobs(prevSelectedJobs => {
      if (prevSelectedJobs.includes(job._id)) {
        return prevSelectedJobs.filter(id => id !== job._id);
      } else {
        return [...prevSelectedJobs, job._id];
      }
    });
  };

  const toggleJobExpansion = (jobId) => {
    setExpandedJobs((prev) => ({ ...prev, [jobId]: !prev[jobId] }));
  };

  const changeSelectedDay = (direction) => {
    setSelectedDay((prevDay) => {
      const currentIndex = DAYS_OF_WEEK.indexOf(prevDay);
      const newIndex = (currentIndex + direction + DAYS_OF_WEEK.length) % DAYS_OF_WEEK.length;
      return DAYS_OF_WEEK[newIndex];
    });
    setVisibleRoutesRange({ start: 0, end: 3 });
  };

  const cycleVisibleRoutes = (direction) => {
    setVisibleRoutesRange((prevRange) => {
      const totalRoutes = routes[selectedDay].length;
      const newStart = (prevRange.start + direction + totalRoutes) % totalRoutes;
      const newEnd = Math.min(newStart + 3, totalRoutes);
      return { start: newStart, end: newEnd };
    });
  };

  const renderLegend = () => (
    <div className="mt-2 p-2 bg-gray-800 rounded-lg text-white">
      <h3 className="font-bold mb-2">Job Color Legend</h3>
      <ul className="space-y-1">
        <li className="flex items-center">
          <span className="w-4 h-4 inline-block bg-green-600 mr-2" /> One-time job
        </li>
        <li className="flex items-center">
          <span className="w-4 h-4 inline-block bg-blue-600 mr-2" /> Weekly recurring job
        </li>
        <li className="flex items-center">
          <span className="w-4 h-4 inline-block bg-yellow-600 mr-2" /> Bi-weekly recurring job
        </li>
        <li className="flex items-center">
          <span className="w-4 h-4 inline-block bg-purple-600 mr-2" /> Monthly recurring job
        </li>
      </ul>
    </div>
  );  

  const shouldReturnToPool = (job, now) => {
    if (!job.isRecurring) return false;
    
    const daysSinceLastService = (now - new Date(job.lastServiceDate)) / (1000 * 60 * 60 * 24);
    
    switch (job.recurrencePattern) {
      case 'Weekly':
        return daysSinceLastService > 3;
      case 'Bi-weekly':
        return daysSinceLastService > 10;
      case 'Monthly':
        return daysSinceLastService > 25;
      default:
        return false;
    }
  };
  
  // Updated handleCompleteJob function
  const handleCompleteJob = async () => {
    try {
      const now = new Date();
      const jobsToComplete = selectedJobs.filter(jobId => {
        const job = jobPool.find(j => j._id === jobId) || 
                    Object.values(routes).flatMap(dayRoutes => 
                      dayRoutes.flatMap(route => route.jobs)
                    ).find(j => j._id === jobId);
        return job && job.status !== 'Completed';
      });
  
      if (jobsToComplete.length === 0) {
        setError("All selected jobs are already completed.");
        return;
      }
  
      const completedJobs = await Promise.all(
        jobsToComplete.map(async jobId => {
          await fetchWithAuth(`/jobs/${jobId}/complete`, 'POST', {});
          const job = jobPool.find(j => j._id === jobId) || 
                      Object.values(routes).flatMap(dayRoutes => 
                        dayRoutes.flatMap(route => route.jobs)
                      ).find(j => j._id === jobId);
          if (job) {
            job.status = 'Completed';
            job.lastServiceDate = now;
          }
          return job;
        })
      );
  
      // Update job pool
      setJobPool(prevJobs => prevJobs.filter(j => !jobsToComplete.includes(j._id)));
  
      // Update routes
      setRoutes(prevRoutes => {
        const updatedRoutes = { ...prevRoutes };
        for (const day in updatedRoutes) {
          updatedRoutes[day] = updatedRoutes[day].map(route => ({
            ...route,
            jobs: route.jobs.filter(job => !jobsToComplete.includes(job._id))
          }));
        }
        return updatedRoutes;
      });
  
      // Add recurring jobs back to pool if they meet criteria
      const jobsToReturnToPool = completedJobs.filter(job => shouldReturnToPool(job, now));
      if (jobsToReturnToPool.length > 0) {
        setJobPool(prevPool => [...prevPool, ...jobsToReturnToPool]);
      }
  
      setSelectedJobs([]);
      setSuccessMessage("Jobs completed successfully!");
  
    } catch (error) {
      console.error('Error completing jobs:', error);
      setError('Failed to complete jobs. Please try again.');
    }
  };
  
  
  const renderJobCard = (job, isInRoute = false, routeIndex, jobIndex) => {
    if (!job) return null; // Safeguard against undefined jobs
  
    const isExpanded = expandedJobs[job._id];
    const customerName = job.customer?.name || 'No Customer';
    const isSelected = selectedJobs.includes(job._id); // Check if the job is selected
    const lastServiceDate = job.lastServiceDate ? new Date(job.lastServiceDate).toLocaleDateString() : 'No Service Date';
  
    const jobColors = {
      oneTime: 'bg-green-600 hover:bg-green-700',
      weekly: 'bg-blue-600 hover:bg-blue-700',
      biWeekly: 'bg-teal-600 hover:bg-teal-700',
      monthly: 'bg-cyan-600 hover:bg-cyan-700',
    };
  
    // Job styling, including border for selected jobs
    const jobStyle = {
      backgroundColor: job.isRecurring
        ? job.recurrencePattern === 'Weekly'
          ? jobColors.weekly
          : job.recurrencePattern === 'Bi-weekly'
          ? jobColors.biWeekly
          : job.recurrencePattern === 'Monthly'
          ? jobColors.monthly
          : jobColors.recurring
        : jobColors.oneTime,
      border: isSelected ? '2px solid white' : 'none', // Keep the yellow border when selected
    };
  
    return (
      <div
        className={`p-2 rounded-md shadow-md ${jobStyle.backgroundColor}`}
        style={{ border: jobStyle.border }} // Apply the border based on selection
        onClick={() => handleJobSelection(job)} // Toggle selection
      >
        <div className="flex justify-between items-center">
          <p className="font-bold text-white">{customerName}</p>
          <div className="flex items-center space-x-2">
            {isInRoute && (
              <>
                <button
                  onClick={(e) => { e.stopPropagation(); handleCompleteJob(job._id); }}  // Complete job button
                  className="text-green-500 hover:text-green-700"
                >
                  <CheckIcon className="h-5 w-5" />
                </button>
                <button
                  onClick={(e) => { e.stopPropagation(); removeJobFromRoute(routeIndex, jobIndex); }}
                  className="text-red-500 hover:text-red-700"
                >
                  <TrashIcon className="h-5 w-5" />
                </button>
              </>
            )}
            <button onClick={(e) => { e.stopPropagation(); toggleJobExpansion(job._id); }}>
              {isExpanded ? <ChevronUpIcon className="h-5 w-5" /> : <ChevronDownIcon className="h-5 w-5" />}
            </button>
          </div>
        </div>
        {isExpanded && (
          <div className="mt-2">
            <p className="text-gray-300">{job.title || 'No Title'}</p>
            <p className="text-gray-400">{job.location?.address || 'No Address'}</p>
            <p className="text-gray-400">Revenue: ${job.cost || 'No Price'}</p>
            <p className="text-gray-400">Type: {job.isRecurring ? 'Recurring' : 'One-time'}</p>
            <p className="text-gray-400">Last Service Date: {lastServiceDate}</p> 
          </div>
        )}
      </div>
    );
  };  

  const handleCompleteRoute = async (routeIndex) => {
    try {
      const now = new Date();
      const routeJobs = routes[selectedDay][routeIndex].jobs.filter(job => job.status !== 'Completed');
  
      const completedJobs = await Promise.all(
        routeJobs.map(async job => {
          await fetchWithAuth(`/jobs/${job._id}/complete`, 'POST', {});
          job.status = 'Completed';
          job.lastServiceDate = now;
          return job;
        })
      );
  
      // Update route by removing all jobs
      setRoutes(prevRoutes => {
        const updatedRoutes = { ...prevRoutes };
        updatedRoutes[selectedDay][routeIndex].jobs = [];
        return updatedRoutes;
      });
  
      // Add recurring jobs back to pool if they meet criteria
      const jobsToReturnToPool = completedJobs.filter(job => shouldReturnToPool(job, now));
      if (jobsToReturnToPool.length > 0) {
        setJobPool(prevPool => [...prevPool, ...jobsToReturnToPool]);
      }
  
      setSuccessMessage("Route completed successfully!");
  
    } catch (error) {
      console.error('Error completing route:', error);
      setError('Failed to complete the route. Please try again.');
    }
  };
  
  const renderDroppableArea = (id, jobs, isJobPool = false) => {
    const routeIndex = parseInt(id.split('-')[1], 10);
    const route = routes[selectedDay][routeIndex];
  
    return (
      <Droppable droppableId={id}>
        {(provided) => (
          <div
            ref={provided.innerRef}
            {...provided.droppableProps}
            className={`bg-gray-800 p-4 rounded-lg min-h-[200px] ${isJobPool ? 'grid grid-cols-3 gap-4' : 'space-y-2'}`}
          >
            {!isJobPool && (
              <div className="mb-4">
                <select
                  className="w-full p-2 bg-gray-700 text-white rounded"
                  onChange={(e) => assignRoute(routeIndex, e.target.value)}
                  value={route.employee ? `employee-${route.employee._id}` : route.crew ? `crew-${route.crew._id}` : ''}
                  disabled={assigningRoutes[routeIndex]}
                >
                  <option value="">Assign to...</option>
                  <optgroup label="Employees">
                    {employees.map((employee) => (
                      <option key={employee._id} value={`employee-${employee._id}`}>{employee.name}</option>
                    ))}
                  </optgroup>
                  <optgroup label="Crews">
                    {crews.map((crew) => (
                      <option key={crew._id} value={`crew-${crew._id}`}>{crew.name}</option>
                    ))}
                  </optgroup>
                </select>
              </div>
            )}
  
            {jobs.length > 0 ? (
              jobs.map((job, index) => (
                <Draggable key={job._id} draggableId={job._id} index={index}>
                  {(provided) => (
                    <div
                      ref={provided.innerRef}
                      {...provided.draggableProps}
                      {...provided.dragHandleProps}
                    >
                      {renderJobCard(job, !isJobPool, routeIndex, index)}
                    </div>
                  )}
                </Draggable>
              ))
            ) : (
              <p className="text-white text-center">No jobs available</p>
            )}
  
            {provided.placeholder}
  
            {/* Move Complete Route button to the bottom */}
            {!isJobPool && (
              <div className="mt-4 text-right">
              </div>
            )}
          </div>
        )}
      </Droppable>
    );
  };  
  
  if (loading) {
    return (
      <div className="min-h-screen bg-gray-900 flex items-center justify-center">
        <p className="text-white text-xl">Loading...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen bg-gray-900 flex flex-col items-center justify-center space-y-4">
        <p className="text-red-500 text-xl">{error}</p>
        <button
          onClick={() => window.location.reload()}
          className="px-4 py-2 bg-blue-500 text-white rounded-md hover:bg-blue-600 transition"
        >
          Refresh Page
        </button>
      </div>
    );
  }  

  return (
    <div className="min-h-screen bg-gray-900 text-white">
      <Header />
  
      <main className="max-w-7xl mx-auto py-8 px-4">
  
        {/* Success and Error Messages */}
        {successMessage && (
          <div className="bg-green-500 text-white p-4 rounded-lg mb-4">
            {successMessage}
          </div>
        )}
        {error && (
          <div className="bg-red-500 text-white p-4 rounded-lg mb-4">
            {error}
          </div>
        )}
  
        {/* Navigation for selecting days */}
        <div className="flex items-center justify-between mb-6">
          <button
            onClick={() => changeSelectedDay(-1)}
            className="p-2 bg-blue-500 rounded-md hover:bg-blue-600 transition"
          >
            <ChevronLeftIcon className="w-6 h-6" />
          </button>
          <h1 className="text-3xl font-bold">{selectedDay}</h1>
          <button
            onClick={() => changeSelectedDay(1)}
            className="p-2 bg-blue-500 rounded-md hover:bg-blue-600 transition"
          >
            <ChevronRightIcon className="w-6 h-6" />
          </button>
        </div>
  
        {/* Buttons to cycle routes and add new routes */}
        <div className="flex items-center justify-between mb-4">
          <button
            onClick={() => cycleVisibleRoutes(-1)}
            className="p-2 bg-blue-500 rounded-md hover:bg-blue-600 transition"
          >
            <ChevronLeftIcon className="w-6 h-6" />
          </button>
          <button
            onClick={addNewRoute}
            className="px-4 py-2 bg-green-500 rounded-md hover:bg-green-600 transition flex items-center space-x-2"
          >
            <PlusIcon className="w-5 h-5" />
            <span>Add Route</span>
          </button>
          <button
            onClick={() => cycleVisibleRoutes(1)}
            className="p-2 bg-blue-500 rounded-md hover:bg-blue-600 transition"
          >
            <ChevronRightIcon className="w-6 h-6" />
          </button>
        </div>
  
        <DragDropContext onDragEnd={onDragEnd}>
          {/* Render Routes */}
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            {routes[selectedDay]
              .slice(visibleRoutesRange.start, visibleRoutesRange.end)
              .map((route) => (
                <div key={route.index} className="bg-gray-800 p-4 rounded-lg shadow-lg">
                  <div className="flex items-center justify-between mb-4">
                    <h2 className="text-xl font-semibold">Route {route.index + 1}</h2>
                    <button
                      onClick={() => deleteRoute(route.index)}
                      className="p-2 bg-red-500 rounded-md hover:bg-red-600 transition"
                    >
                      <TrashIcon className="w-5 h-5" />
                    </button>
                  </div>
                  {renderDroppableArea(`route-${route.index}`, route.jobs)}
  
                  {/* Complete Route Button at the bottom of each route */}
                  <div className="mt-4 text-right">
                    <button
                      onClick={() => setConfirmDialog({ isOpen: true, routeIndex: route.index })}
                      className="bg-green-500 text-white px-2 py-1 rounded-md hover:bg-green-600 transition"
                    >
                      Complete Route
                    </button>
                  </div>
                </div>
              ))}
          </div>
  
          {/* Job Pool */}
          <div className="mt-8">
            <h2 className="text-2xl font-semibold mb-4 flex justify-between items-center">
              Job Pool
              {selectedJobs.length > 0 && (
                <div className="flex space-x-4">
                  <button
                    onClick={() => setSelectedJobs([])}
                    className="px-4 py-2 bg-red-500 text-white rounded-md hover:bg-red-600 transition"
                  >
                    Clear Selection
                  </button>
                  <button
                    onClick={handleCompleteJob} // Complete selected jobs
                    className="px-4 py-2 bg-green-500 text-white rounded-md hover:bg-green-600 transition"
                  >
                    Complete Selected Jobs
                  </button>
                </div>
              )}
            </h2>
            <p className="text-sm text-gray-400 mb-2">
              Click on jobs to select multiple, then drag to a route or complete.
            </p>
  
            {/* Job Color Legend */}
            <div className="mt-2 p-2 bg-gray-800 rounded-lg text-white">
              <h3 className="font-bold mb-2">Job Color Legend</h3>
              <ul className="flex space-x-8">
                <li className="flex items-center">
                  <span className="w-4 h-4 inline-block bg-green-600 mr-2" /> One-time job
                </li>
                <li className="flex items-center">
                  <span className="w-4 h-4 inline-block bg-blue-600 mr-2" /> Weekly
                </li>
                <li className="flex items-center">
                  <span className="w-4 h-4 inline-block bg-yellow-600 mr-2" /> Bi-weekly
                </li>
                <li className="flex items-center">
                  <span className="w-4 h-4 inline-block bg-purple-600 mr-2" /> Monthly
                </li>
              </ul>
            </div>
  
            {renderDroppableArea('jobPool-0', jobPool, true)}
          </div>
        </DragDropContext>
  
        {saving && (
          <div className="fixed inset-0 flex items-center justify-center bg-gray-800 bg-opacity-50 z-50">
            <ClipLoader color="#ffffff" loading={saving} size={50} />
            <p className="text-white mt-4">Saving...</p>
          </div>
        )}
  
        {/* Confirmation Dialog for Route Completion */}
        {confirmDialog.isOpen && (
          <div className="fixed inset-0 z-50 flex items-center justify-center bg-black bg-opacity-50">
            <div className="bg-gray-800 p-6 rounded-lg text-white shadow-lg">
              <h3 className="text-xl font-semibold">Confirm Route Completion</h3>
              <p className="mt-4">Are you sure you want to complete all jobs in this route?</p>
              <div className="mt-6 flex justify-end space-x-4">
                <button
                  onClick={() => setConfirmDialog({ isOpen: false, routeIndex: null })}
                  className="px-4 py-2 bg-gray-600 rounded-md hover:bg-gray-700"
                >
                  Cancel
                </button>
                <button
                  onClick={() => {
                    handleCompleteRoute(confirmDialog.routeIndex);
                    setConfirmDialog({ isOpen: false, routeIndex: null }); // Close after confirming
                  }}
                  className="px-4 py-2 bg-green-500 rounded-md hover:bg-green-600"
                >
                  Confirm
                </button>
              </div>
            </div>
          </div>
        )}
      </main>
    </div>
  );  
};

export default BuildRoutes;
