import React, { useState, useEffect } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { Link, useNavigate } from 'react-router-dom';
import {
  PlusIcon,
  UsersIcon,
  MapIcon,
  DocumentIcon,
  ArrowPathIcon,
  ChevronLeftIcon,
  ChevronRightIcon,
  EyeIcon,
  EyeSlashIcon,
} from '@heroicons/react/24/outline';
import Header from '../components/Header';
import { Line, Bar, Doughnut } from 'react-chartjs-2';
import GaugeChart from '../components/GaugeChart';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  ArcElement,
  Title,
  Tooltip,
  Legend,
  TimeScale,
  Filler,
} from 'chart.js';
import 'chartjs-adapter-date-fns';
import { fetchJobs, fetchCustomers, fetchEmployees, fetchAnalytics } from '../api/dashboardApi';
import TrialTimer from '../components/TrialTimer';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  ArcElement,
  Title,
  Tooltip,
  Legend,
  TimeScale,
  Filler
);

const Dashboard = () => {
  const { user, setUser } = useAuth();
  const navigate = useNavigate();
  const [jobCount, setJobCount] = useState(0);
  const [customerCount, setCustomerCount] = useState(0);
  const [employeeCount, setEmployeeCount] = useState(0);
  const [analyticsData, setAnalyticsData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [globalTimeRange, setGlobalTimeRange] = useState('30');
  const [customDateRange, setCustomDateRange] = useState({ start: '', end: '' });
  const [currentChartGroup, setCurrentChartGroup] = useState(0);
  const [showInsights, setShowInsights] = useState(true);

  useEffect(() => {
    fetchDashboardData(globalTimeRange);
    const interval = setInterval(() => {
      fetchDashboardData(globalTimeRange);
    }, 600000); // Refresh every 10 minutes
    return () => clearInterval(interval);
  }, [globalTimeRange]);

  const fetchDashboardData = async (timeRange, customRange = null) => {
    setLoading(true);
    setError(null);
    try {
      const [jobs, customers, employees, analytics] = await Promise.all([
        fetchJobs(),
        fetchCustomers(),
        fetchEmployees(),
        fetchAnalytics(timeRange, customRange),
      ]);
      setJobCount(jobs?.length || 0);
      setCustomerCount(customers?.length || 0);
      setEmployeeCount(employees?.length || 0);
      setAnalyticsData(analytics);
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
      setError('Failed to load dashboard data. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleCustomDateChange = (e) => {
    setCustomDateRange({ ...customDateRange, [e.target.name]: e.target.value });
  };

  const applyCustomDateRange = () => {
    if (customDateRange.start && customDateRange.end) {
      fetchDashboardData('custom', customDateRange);
    }
  };

  const getChartOptions = (title, chartType) => {
    return {
      responsive: true,
      maintainAspectRatio: false,
      plugins: {
        legend: {
          position: 'top',
          labels: { color: 'rgba(255, 255, 255, 0.7)', font: { size: 12 } },
        },
        title: {
          display: true,
          text: title,
          color: 'rgba(255, 255, 255, 0.9)',
          font: { size: 16, weight: 'bold' },
        },
      },
      scales: chartType !== 'doughnut'
        ? {
            x: {
              grid: { color: 'rgba(255, 255, 255, 0.1)' },
              ticks: { color: 'rgba(255, 255, 255, 0.7)', font: { size: 10 } },
            },
            y: {
              grid: { color: 'rgba(255, 255, 255, 0.1)' },
              ticks: { color: 'rgba(255, 255, 255, 0.7)', font: { size: 10 } },
              beginAtZero: true,
            },
          }
        : {},
    };
  };

  const chartConfigs = [
    [
      { title: 'Customer Growth', type: Line, dataKey: 'customerGrowth', label: 'New Customers', color: '#00bcd4' },
      { title: 'Customer Churn Rate', type: GaugeChart, dataKey: 'customerRetention', label: 'Churn Rate', color: '#e53935' },
      { title: 'Customer Satisfaction', type: Bar, dataKey: 'customerSatisfaction', label: 'Satisfaction Score', color: '#4caf50' },
    ],
    [
      { title: 'Revenue Over Time', type: Line, dataKey: 'revenue', label: 'Revenue', color: '#ab47bc' },
      { title: 'Job Status Distribution', type: Doughnut, dataKey: 'jobStatusDistribution', label: 'Job Status', color: ['#00bcd4', '#ab47bc', '#ff7043', '#ffca28'] },
      { title: 'Top Services', type: Bar, dataKey: 'topServices', label: 'Revenue', color: '#009688' },
    ],
    [
      { title: 'Top Performing Employees', type: Bar, dataKey: 'topPerformingEmployees', label: 'Jobs Completed', color: '#ab47bc' },
      { title: 'Employee Distribution', type: Bar, dataKey: 'employeeDistribution', label: 'Employees', color: ['#4caf50', '#2196f3', '#ff9800', '#f44336'] },
      { title: 'Job Completion Rate', type: GaugeChart, dataKey: 'jobCompletionRate', label: 'Completion Rate', color: '#8d6e63' },
    ],
  ];

  const nextChartGroup = () => {
    setCurrentChartGroup((prev) => (prev + 1) % chartConfigs.length);
  };

  const prevChartGroup = () => {
    setCurrentChartGroup((prev) => (prev - 1 + chartConfigs.length) % chartConfigs.length);
  };

  const renderChart = (config, index) => {
    const chartData = analyticsData?.[config.dataKey];
    if (!chartData || !chartData.labels || !chartData.data || chartData.data.length === 0) {
      return <div className="text-center py-4">No data available for {config.title}</div>;
    }

    if (config.type === GaugeChart) {
      return <GaugeChart value={chartData.data[0] || 0} label={config.label} />;
    }

    const chartOptions = getChartOptions(config.title, config.type.name.toLowerCase());

    return (
      <config.type
        data={{
          labels: chartData.labels,
          datasets: [
            {
              label: config.label,
              data: chartData.data,
              backgroundColor: Array.isArray(config.color) ? config.color : [config.color],
              borderColor: config.type === Line ? config.color : 'rgba(255, 255, 255, 0.1)',
              borderWidth: config.type === Line ? 2 : 1,
              fill: config.type === Line ? 'start' : undefined,
              tension: 0.4,
            },
          ],
        }}
        options={chartOptions}
      />
    );
  };

  const renderDashboardContent = () => {
    if (!analyticsData) {
      return <div className="text-center py-12">No analytics data available. Please try refreshing the dashboard.</div>;
    }

    const chartsToRender = user?.subscriptionTier === 'Free' 
      ? chartConfigs.flat().slice(0, 3)  // Only first 3 charts for free tier
      : chartConfigs[currentChartGroup];

    return (
      <div>
        <div className="grid grid-cols-1 gap-6 sm:grid-cols-2 lg:grid-cols-3">
          {chartsToRender.map((config, index) => (
            <ChartContainer key={index} title={config.title}>
              {renderChart(config, index)}
            </ChartContainer>
          ))}
        </div>
        {user?.subscriptionTier !== 'Free' && (
          <div className="flex justify-center mt-4">
            <button onClick={prevChartGroup} className="mr-2 bg-blue-500 text-white px-3 py-1 rounded-md hover:bg-blue-600">
              <ChevronLeftIcon className="h-6 w-6" />
            </button>
            <button onClick={nextChartGroup} className="bg-blue-500 text-white px-3 py-1 rounded-md hover:bg-blue-600">
              <ChevronRightIcon className="h-6 w-6" />
            </button>
          </div>
        )}
      </div>
    );
  };

  return (
    <div className="min-h-screen bg-gray-900 text-white">
      <Header />
      <main className="max-w-7xl mx-auto py-6 px-4 sm:px-6 lg:px-8">
        {user?.subscriptionTier === 'Free' && (
          <div className="flex flex-col sm:flex-row items-center justify-between mb-6">
            <TrialTimer trialEndDate={user.trialEndDate} />
            <Link
              to="/pricing"
              className="mt-4 sm:mt-0 bg-blue-500 text-white px-4 py-2 rounded-md text-sm font-medium hover:bg-blue-600"
            >
              Upgrade Now
            </Link>
          </div>
        )}

        <div className="flex justify-between items-center mb-6">
          <h1 className="text-3xl font-bold">Welcome, {user?.name || 'User'}</h1>
          <div className="flex items-center">
            <select
              value={globalTimeRange}
              onChange={(e) => setGlobalTimeRange(e.target.value)}
              className="mr-2 p-2 border rounded text-black"
            >
              <option value="7">Past 7 days</option>
              <option value="30">Past 30 days</option>
              <option value="90">Past 90 days</option>
              <option value="all">All time</option>
              <option value="custom">Custom range</option>
            </select>
            {globalTimeRange === 'custom' && (
              <>
                <input
                  type="date"
                  name="start"
                  value={customDateRange.start}
                  onChange={handleCustomDateChange}
                  className="mr-2 p-2 border rounded"
                />
                <input
                  type="date"
                  name="end"
                  value={customDateRange.end}
                  onChange={handleCustomDateChange}
                  className="mr-2 p-2 border rounded"
                />
                <button
                  onClick={applyCustomDateRange}
                  className="bg-blue-500 text-white px-4 py-2 rounded-md text-sm font-medium hover:bg-blue-600"
                >
                  Apply
                </button>
              </>
            )}
            <button
              onClick={() => fetchDashboardData(globalTimeRange, globalTimeRange === 'custom' ? customDateRange : null)}
              className="ml-4 bg-green-500 text-white px-4 py-2 rounded-md text-sm font-medium hover:bg-green-600 flex items-center"
            >
              <ArrowPathIcon className="h-5 w-5 mr-2" />
              Refresh Dashboard
            </button>
          </div>
        </div>

        {error && (
          <div className="bg-red-100 border-l-4 border-red-500 text-red-700 p-4 mb-6" role="alert">
            <p>{error}</p>
          </div>
        )}

        <div className="grid grid-cols-1 gap-5 sm:grid-cols-2 lg:grid-cols-3 mb-8">
          <OverviewCard title="Jobs" count={jobCount} link="/manage-jobs" />
          <OverviewCard title="Customers" count={customerCount} link="/manage-customers" />
          <OverviewCard title="Employees" count={employeeCount} link="/manage-employees" />
        </div>

        <div className="mb-8">
          <h2 className="text-2xl font-semibold mb-4">Quick Actions</h2>
          <div className="grid grid-cols-2 gap-4 sm:grid-cols-3 lg:grid-cols-5">
            <QuickActionButton to="/jobs" icon={PlusIcon} text="Create New Job" />
            <QuickActionButton to="/customers" icon={PlusIcon} text="Add New Customer" />
            <QuickActionButton to="/add-employee" icon={UsersIcon} text="Add New Employee" />
            <QuickActionButton to="/generate-report" icon={DocumentIcon} text="Generate Report" />
            <QuickActionButton to="/build-routes" icon={MapIcon} text="Build Routes" />
          </div>
        </div>

        <div className="flex justify-between items-center mb-4">
          <h2 className="text-2xl font-semibold">Dashboard Insights</h2>
          <button onClick={() => setShowInsights(!showInsights)} className="text-white hover:text-gray-400">
            {showInsights ? <EyeSlashIcon className="h-6 w-6" /> : <EyeIcon className="h-6 w-6" />}
          </button>
        </div>

        {showInsights && (
          <>
            {loading ? (
              <div className="text-center py-12">Loading dashboard data...</div>
            ) : (
              renderDashboardContent()
            )}
          </>
        )}
      </main>
    </div>
  );
};

const OverviewCard = ({ title, count, link }) => (
  <div className="bg-gray-800 overflow-hidden shadow rounded-lg">
    <div className="px-4 py-5 sm:p-6">
      <h3 className="text-lg leading-6 font-medium text-white">{title}</h3>
      <div className="mt-2 text-3xl font-semibold text-white">{count}</div>
      <div className="mt-4">
      <Link to={link} className="text-blue-400 hover:text-blue-300">
          View all {title.toLowerCase()}
        </Link>
      </div>
    </div>
  </div>
);

const QuickActionButton = ({ to, icon: Icon, text }) => (
  <Link
    to={to}
    className="bg-blue-500 text-white px-4 py-2 rounded-md text-sm font-medium hover:bg-blue-600 flex items-center justify-center"
  >
    <Icon className="h-5 w-5 mr-2" />
    {text}
  </Link>
);

const ChartContainer = ({ title, children }) => (
  <div className="bg-gray-800 p-4 rounded-lg shadow">
    <h3 className="text-lg leading-6 font-medium mb-4 text-white">{title}</h3>
    <div className="h-64">{children}</div>
  </div>
);

export default Dashboard;
