import React, { useEffect, useState } from 'react';
import { Tab } from '@headlessui/react';
import axiosInstance from '../utils/axiosInstance';
import { useAuth } from '../contexts/AuthContext';
import Header from '../components/Header';

function classNames(...classes) {
  return classes.filter(Boolean).join(' ');
}

const Profile = () => {
  const [profile, setProfile] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const { user } = useAuth();
  const [isEditing, setIsEditing] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    bio: '',
    businessName: '',
    businessPhone: '',
    businessWebsite: '',
    businessAddress: '',
  });

  useEffect(() => {
    const fetchProfile = async () => {
      if (!user) {
        setError('User not authenticated');
        setLoading(false);
        return;
      }

      try {
        const response = await axiosInstance.get('/api/profile');
        setProfile(response.data);
        setFormData({
          name: response.data.user.name,
          email: response.data.user.email,
          bio: response.data.bio || '',
          businessName: response.data.user.businessInfo?.name || '',
          businessPhone: response.data.user.businessInfo?.phone || '',
          businessWebsite: response.data.user.businessInfo?.website || '',
          businessAddress: response.data.user.businessInfo?.address || '',
        });
      } catch (err) {
        console.error('Error fetching profile:', err);
        setError(err.response?.data?.error || 'Failed to load profile.');
      } finally {
        setLoading(false);
      }
    };
    fetchProfile();
  }, [user]);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axiosInstance.put('/api/profile', formData);
      setProfile(response.data);
      setIsEditing(false);
    } catch (error) {
      console.error('Error updating profile:', error);
      setError('Failed to update profile. Please try again.');
    }
  };

  const GeneralTab = () => (
    <div className="space-y-4">
      <h2 className="text-2xl font-semibold mb-4">General Information</h2>
      {isEditing ? (
        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <label htmlFor="name" className="block text-sm font-medium text-gray-300">Name</label>
            <input
              type="text"
              name="name"
              id="name"
              value={formData.name}
              onChange={handleChange}
              className="mt-1 block w-full rounded-md bg-gray-700 border-gray-600 text-white"
            />
          </div>
          <div>
            <label htmlFor="email" className="block text-sm font-medium text-gray-300">Email</label>
            <input
              type="email"
              name="email"
              id="email"
              value={formData.email}
              onChange={handleChange}
              className="mt-1 block w-full rounded-md bg-gray-700 border-gray-600 text-white"
            />
          </div>
          <div>
            <label htmlFor="bio" className="block text-sm font-medium text-gray-300">Bio</label>
            <textarea
              name="bio"
              id="bio"
              value={formData.bio}
              onChange={handleChange}
              rows="3"
              className="mt-1 block w-full rounded-md bg-gray-700 border-gray-600 text-white"
            ></textarea>
          </div>
          <div>
            <label htmlFor="businessName" className="block text-sm font-medium text-gray-300">Business Name</label>
            <input
              type="text"
              name="businessName"
              id="businessName"
              value={formData.businessName}
              onChange={handleChange}
              className="mt-1 block w-full rounded-md bg-gray-700 border-gray-600 text-white"
            />
          </div>
          <div>
            <label htmlFor="businessPhone" className="block text-sm font-medium text-gray-300">Business Phone</label>
            <input
              type="tel"
              name="businessPhone"
              id="businessPhone"
              value={formData.businessPhone}
              onChange={handleChange}
              className="mt-1 block w-full rounded-md bg-gray-700 border-gray-600 text-white"
            />
          </div>
          <div>
            <label htmlFor="businessWebsite" className="block text-sm font-medium text-gray-300">Business Website</label>
            <input
              type="url"
              name="businessWebsite"
              id="businessWebsite"
              value={formData.businessWebsite}
              onChange={handleChange}
              className="mt-1 block w-full rounded-md bg-gray-700 border-gray-600 text-white"
            />
          </div>
          <div>
            <label htmlFor="businessAddress" className="block text-sm font-medium text-gray-300">Business Address</label>
            <input
              type="text"
              name="businessAddress"
              id="businessAddress"
              value={formData.businessAddress}
              onChange={handleChange}
              className="mt-1 block w-full rounded-md bg-gray-700 border-gray-600 text-white"
            />
          </div>
          <div className="flex justify-end space-x-2">
            <button
              type="button"
              onClick={() => setIsEditing(false)}
              className="px-4 py-2 bg-gray-600 text-white rounded-md hover:bg-gray-700"
            >
              Cancel
            </button>
            <button
              type="submit"
              className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700"
            >
              Save Changes
            </button>
          </div>
        </form>
      ) : (
        <div className="space-y-4">
          <p><strong>Name:</strong> {profile.user.name}</p>
          <p><strong>Email:</strong> {profile.user.email}</p>
          <p><strong>Bio:</strong> {profile.bio || 'No bio provided'}</p>
          <p><strong>Business Name:</strong> {profile.user.businessInfo?.name || 'Not provided'}</p>
          <p><strong>Business Phone:</strong> {profile.user.businessInfo?.phone || 'Not provided'}</p>
          <p><strong>Business Website:</strong> {profile.user.businessInfo?.website || 'Not provided'}</p>
          <p><strong>Business Address:</strong> {profile.user.businessInfo?.address || 'Not provided'}</p>
          <button
            onClick={() => setIsEditing(true)}
            className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700"
          >
            Edit Profile
          </button>
        </div>
      )}
    </div>
  );

  const BadgesTab = () => (
    <div>
      <h2 className="text-2xl font-semibold mb-4">Your Badges</h2>
      <p>Badges will be displayed here</p>
    </div>
  );

  const SecurityTab = () => (
    <div>
      <h2 className="text-2xl font-semibold mb-4">Security Settings</h2>
      <p>Security settings will be displayed here</p>
    </div>
  );

  const StatsTab = () => (
    <div>
      <h2 className="text-2xl font-semibold mb-4">Your Stats</h2>
      <p>Stats will be displayed here</p>
    </div>
  );

  const AccountTab = () => (
    <div>
      <h2 className="text-2xl font-semibold mb-4">Account Details</h2>
      <p><strong>Subscription Tier:</strong> {profile.subscriptionTier}</p>
      <p><strong>Subscription Active:</strong> {profile.subscriptionActive ? 'Yes' : 'No'}</p>
    </div>
  );

  const tabs = [
    { name: 'General', content: <GeneralTab /> },
    { name: 'Badges', content: <BadgesTab /> },
    { name: 'Security', content: <SecurityTab /> },
    { name: 'Stats', content: <StatsTab /> },
    { name: 'Account', content: <AccountTab /> },
  ];

  return (
    <div className="min-h-screen bg-gray-900 text-white">
      <Header />
      <main className="max-w-7xl mx-auto py-6 px-4 sm:px-6 lg:px-8">
        <h1 className="text-3xl font-bold mb-6">Your Profile</h1>
        {loading ? (
          <div className="text-center py-12">Loading profile...</div>
        ) : error ? (
          <div className="bg-red-600 text-white p-4 rounded-md mb-6">{error}</div>
        ) : (
          <Tab.Group>
            <Tab.List className="flex space-x-1 rounded-xl bg-blue-900/20 p-1">
              {tabs.map((tab) => (
                <Tab
                  key={tab.name}
                  className={({ selected }) =>
                    classNames(
                      'w-full rounded-lg py-2.5 text-sm font-medium leading-5',
                      'ring-white ring-opacity-60 ring-offset-2 ring-offset-blue-400 focus:outline-none focus:ring-2',
                      selected
                        ? 'bg-white text-blue-700 shadow'
                        : 'text-blue-100 hover:bg-white/[0.12] hover:text-white'
                    )
                  }
                >
                  {tab.name}
                </Tab>
              ))}
            </Tab.List>
            <Tab.Panels className="mt-2">
              {tabs.map((tab, idx) => (
                <Tab.Panel
                  key={idx}
                  className={classNames(
                    'rounded-xl bg-gray-800 p-3',
                    'ring-white ring-opacity-60 ring-offset-2 ring-offset-blue-400 focus:outline-none focus:ring-2'
                  )}
                >
                  {tab.content}
                </Tab.Panel>
              ))}
            </Tab.Panels>
          </Tab.Group>
        )}
      </main>
    </div>
  );
};

export default Profile;
