// src/pages/Jobs.js
import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const Jobs = () => {
  const [job, setJob] = useState({
    title: '',
    description: '',
    deadline: '',
    customer: ''
  });

  const handleChange = (e) => {
    setJob({ ...job, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // TODO: Implement job creation API call
    console.log('Job to create:', job);
  };

  return (
    <div className="min-h-screen bg-background text-text">
      <main className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        <div className="px-4 py-6 sm:px-0">
          <h1 className="text-2xl font-semibold mb-6">Jobs</h1>
          <div className="bg-surface overflow-hidden shadow rounded-lg p-6 mb-6">
            <h2 className="text-xl font-semibold mb-4">Create New Job</h2>
            <form onSubmit={handleSubmit} className="space-y-4">
              <div>
                <label htmlFor="title" className="block text-sm font-medium">Job Title</label>
                <input
                  type="text"
                  id="title"
                  name="title"
                  value={job.title}
                  onChange={handleChange}
                  className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary focus:ring focus:ring-primary focus:ring-opacity-50"
                  required
                />
              </div>
              <div>
                <label htmlFor="description" className="block text-sm font-medium">Description</label>
                <textarea
                  id="description"
                  name="description"
                  value={job.description}
                  onChange={handleChange}
                  rows="4"
                  className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary focus:ring focus:ring-primary focus:ring-opacity-50"
                ></textarea>
              </div>
              <div>
                <label htmlFor="deadline" className="block text-sm font-medium">Deadline</label>
                <input
                  type="date"
                  id="deadline"
                  name="deadline"
                  value={job.deadline}
                  onChange={handleChange}
                  className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary focus:ring focus:ring-primary focus:ring-opacity-50"
                />
              </div>
              <div>
                <label htmlFor="customer" className="block text-sm font-medium">Customer</label>
                <input
                  type="text"
                  id="customer"
                  name="customer"
                  value={job.customer}
                  onChange={handleChange}
                  className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary focus:ring focus:ring-primary focus:ring-opacity-50"
                />
              </div>
              <button
                type="submit"
                className="bg-primary text-white px-4 py-2 rounded-md text-sm font-medium hover:bg-opacity-90"
              >
                Create Job
              </button>
            </form>
          </div>
          <p className="mt-4">Additional job management functionality coming soon!</p>
          <Link to="/dashboard" className="mt-4 inline-block text-primary hover:text-primary-dark">
            Back to Dashboard
          </Link>
        </div>
      </main>
    </div>
  );
};

export default Jobs;
