import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import Header from '../components/Header';

const API_BASE_URL = 'http://localhost:5000/api';

const BuildRoutes = () => {
  const [routes, setRoutes] = useState({});
  const [availableJobs, setAvailableJobs] = useState([]);
  const [selectedDay, setSelectedDay] = useState('Monday');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchRouteAndJobs();
  }, [selectedDay]);

  const fetchRouteAndJobs = async () => {
    setLoading(true);
    setError(null);
    try {
      const [routeResponse, jobsResponse] = await Promise.all([
        axios.get(`${API_BASE_URL}/routes/${selectedDay}`, {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        }),
        axios.get(`${API_BASE_URL}/jobs`, {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
          params: { status: 'Pending' } // Assuming 'Pending' status means available
        })
      ]);

      setRoutes({ 
        [selectedDay]: routeResponse.data.jobs || [], 
        jobPool: jobsResponse.data 
      });
      setAvailableJobs(jobsResponse.data);
    } catch (error) {
      console.error('Error fetching data:', error);
      setError('Failed to load routes and jobs. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const onDragEnd = async (result) => {
    const { source, destination } = result;
    if (!destination) return;

    const sourceList = source.droppableId;
    const destList = destination.droppableId;

    let updatedRoutes = { ...routes };
    let updatedJobPool = [...jobPool];
    let movedJob = null;

    if (sourceList === 'jobPool' && destList.startsWith('route')) {
      [movedJob] = updatedJobPool.splice(source.index, 1);
      if (!updatedRoutes[selectedDay]) {
        updatedRoutes[selectedDay] = { route1: [], route2: [], route3: [] };
      }
      if (!updatedRoutes[selectedDay][destList]) {
        updatedRoutes[selectedDay][destList] = [];
      }
      updatedRoutes[selectedDay][destList].push(movedJob);
    } else if (sourceList.startsWith('route') && destList === 'jobPool') {
      [movedJob] = updatedRoutes[selectedDay][sourceList].splice(source.index, 1);
      updatedJobPool.push(movedJob);
    } else if (sourceList.startsWith('route') && destList.startsWith('route')) {
      [movedJob] = updatedRoutes[selectedDay][sourceList].splice(source.index, 1);
      if (!updatedRoutes[selectedDay][destList]) {
        updatedRoutes[selectedDay][destList] = [];
      }
      updatedRoutes[selectedDay][destList].splice(destination.index, 0, movedJob);
    }

    setRoutes(updatedRoutes);
    setJobPool(updatedJobPool);

    if (movedJob && destList.startsWith('route')) {
      try {
        // Save the updated route to the backend
        await axios.put(`${API_BASE_URL}/routes/${selectedDay}`, { jobs: updatedRoutes[selectedDay][destList] }, {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        });

        // Update the job status in the backend to persist the change
        await axios.put(`${API_BASE_URL}/jobs/${movedJob._id}/schedule`, { 
          scheduledDay: selectedDay, 
          route: destList 
        }, {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        });
      } catch (error) {
        console.error('Error updating route or job:', error);
        setError('Failed to update route or job. Please try again.');
        fetchRouteAndJobs();
      }
    }
  };

  const renderJobList = (listId, jobs, routeName) => (
    <Droppable droppableId={listId}>
      {(provided) => (
        <div
          {...provided.droppableProps}
          ref={provided.innerRef}
          className={`bg-gray-800 p-4 rounded ${routeName ? 'min-h-[600px]' : 'min-h-[150px] flex flex-wrap gap-4'}`}
        >
          <h2 className="text-xl font-bold mb-2 text-white">
            {routeName ? routeName : 'Job Pool'}
          </h2>
          <div className="flex flex-wrap gap-4">
            {jobs.map((job, index) => (
              <Draggable key={job._id} draggableId={job._id} index={index}>
                {(provided, snapshot) => (
                  <div
                    ref={provided.innerRef}
                    {...provided.draggableProps}
                    {...provided.dragHandleProps}
                    className={`bg-green-700 p-2 rounded text-white ${job.recurrence === 'One-time' ? 'bg-opacity-50' : ''}`}
                    style={{
                      ...provided.draggableProps.style,
                      boxShadow: snapshot.isDragging ? "0 0 10px rgba(0,0,0,0.5)" : "none",
                      width: 'calc(100% - 16px)', // Adjusted width
                      height: `${job.duration * 50}px`, // Adjusted height based on duration
                      marginBottom: '8px', // Added margin to create space between rows
                    }} 
                  >
                    <p className="font-bold">{job.title}</p>
                    <p className="text-sm">{job.customer?.name}</p>
                    <p className="text-xs">{job.recurrence}</p>
                  </div>
                )}
              </Draggable>
            ))}
            {provided.placeholder}
          </div>
        </div>
      )}
    </Droppable>
  );

  const handlePrevDay = () => {
    const currentIndex = DAYS_OF_WEEK.indexOf(selectedDay);
    const prevIndex = (currentIndex - 1 + DAYS_OF_WEEK.length) % DAYS_OF_WEEK.length;
    setSelectedDay(DAYS_OF_WEEK[prevIndex]);
  };

  const handleNextDay = () => {
    const currentIndex = DAYS_OF_WEEK.indexOf(selectedDay);
    const nextIndex = (currentIndex + 1) % DAYS_OF_WEEK.length;
    setSelectedDay(DAYS_OF_WEEK[nextIndex]);
  };

  const renderTimeScale = () => (
    <div className="time-scale flex flex-col mr-2">
      {['6 AM', '7 AM', '8 AM', '9 AM', '10 AM', '11 AM', '12 PM', '1 PM', '2 PM', '3 PM', '4 PM', '5 PM', '6 PM'].map(time => (
        <div key={time} className="time-slot h-[50px] text-white">
          {time}
        </div>
      ))}
    </div>
  );

  if (loading) {
    return <div className="text-center mt-8 text-white">Loading...</div>;
  }

  if (error) {
    return <div className="text-center mt-8 text-red-500">{error}</div>;
  }

  return (
    <div className="min-h-screen bg-gray-900 text-white">
      <Header />
      <main className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        <h1 className="text-3xl font-bold mb-4">Build Routes</h1>
        <div className="flex items-center justify-between mb-4">
          <button onClick={handlePrevDay} className="bg-gray-800 p-2 rounded">
            <ChevronLeftIcon className="h-6 w-6" />
          </button>
          <h2 className="text-2xl font-bold">{selectedDay}</h2>
          <button onClick={handleNextDay} className="bg-gray-800 p-2 rounded">
            <ChevronRightIcon className="h-6 w-6" />
          </button>
        </div>
        <DragDropContext onDragEnd={onDragEnd}>
          <div className="grid grid-cols-5 gap-2">
            <div className="col-span-1">
              {renderTimeScale()}
            </div>
            <div className="col-span-4 grid grid-cols-3 gap-2">
              <div>{renderJobList('route1', routes[selectedDay]?.route1 || [], 'Route 1')}</div>
              <div>{renderJobList('route2', routes[selectedDay]?.route2 || [], 'Route 2')}</div>
              <div>{renderJobList('route3', routes[selectedDay]?.route3 || [], 'Route 3')}</div>
            </div>
          </div>
          <div className="mt-4">
            {renderJobList('jobPool', jobPool)}
          </div>
        </DragDropContext>
      </main>
    </div>
  );
};

export default BuildRoutes;
