// src/pages/Jobs.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const Jobs = () => {
  const [job, setJob] = useState({
    title: '',
    description: '',
    scheduledDate: '',
    customer: '',
    price: '',
    status: 'Scheduled'
  });
  const [jobs, setJobs] = useState([]);
  const [customers, setCustomers] = useState([]);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    fetchCustomers();
    fetchJobs();
  }, []);

  const fetchCustomers = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/customers', {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setCustomers(response.data);
    } catch (error) {
      console.error('Error fetching customers:', error);
    }
  };

  const fetchJobs = async () => {
    setLoading(true);
    try {
      const response = await axios.get('http://localhost:5000/api/jobs', {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setJobs(response.data);
    } catch (error) {
      console.error('Error fetching jobs:', error);
      setError('Failed to fetch jobs. Please try again.');
    }
    setLoading(false);
  };

  const handleChange = (e) => {
    setJob({ ...job, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    setLoading(true);

    try {
      const response = await axios.post('http://localhost:5000/api/jobs', job, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setSuccess('Job created successfully!');
      setJob({ title: '', description: '', scheduledDate: '', customer: '', price: '', status: 'Scheduled' });
      // Refresh the jobs list immediately after creating a job
      fetchJobs();
    } catch (error) {
      console.error('Error creating job:', error.response?.data || error.message);
      setError(error.response?.data?.message || 'Failed to create job. Please try again.');
    }
    setLoading(false);
  };

  return (
    <div className="min-h-screen bg-background text-text">
      <main className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        <div className="px-4 py-6 sm:px-0">
          <h1 className="text-2xl font-semibold mb-6">Jobs</h1>
          
          {/* Create New Job Form */}
          <div className="bg-surface overflow-hidden shadow rounded-lg p-6 mb-6">
            <h2 className="text-xl font-semibold mb-4">Create New Job</h2>
            {error && <div className="text-red-500 mb-4">{error}</div>}
            {success && <div className="text-green-500 mb-4">{success}</div>}
            <form onSubmit={handleSubmit} className="space-y-4">
              <div>
                <label htmlFor="title" className="block text-sm font-medium">Job Title</label>
                <input
                  type="text"
                  id="title"
                  name="title"
                  value={job.title}
                  onChange={handleChange}
                  className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary focus:ring focus:ring-primary focus:ring-opacity-50 text-black"
                  required
                />
              </div>
              <div>
                <label htmlFor="description" className="block text-sm font-medium">Description</label>
                <textarea
                  id="description"
                  name="description"
                  value={job.description}
                  onChange={handleChange}
                  rows="4"
                  className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary focus:ring focus:ring-primary focus:ring-opacity-50 text-black"
                ></textarea>
              </div>
              <div>
                <label htmlFor="scheduledDate" className="block text-sm font-medium">Scheduled Date</label>
                <input
                  type="date"
                  id="scheduledDate"
                  name="scheduledDate"
                  value={job.scheduledDate}
                  onChange={handleChange}
                  className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary focus:ring focus:ring-primary focus:ring-opacity-50 text-black"
                  required
                />
              </div>
              <div>
                <label htmlFor="customer" className="block text-sm font-medium">Customer</label>
                <select
                  id="customer"
                  name="customer"
                  value={job.customer}
                  onChange={handleChange}
                  className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary focus:ring focus:ring-primary focus:ring-opacity-50 text-black"
                  required
                >
                  <option value="">Select a customer</option>
                  {customers.map((customer) => (
                    <option key={customer._id} value={customer._id}>
                      {customer.name}
                    </option>
                  ))}
                </select>
              </div>
              <div>
                <label htmlFor="price" className="block text-sm font-medium">Price</label>
                <input
                  type="number"
                  id="price"
                  name="price"
                  value={job.price}
                  onChange={handleChange}
                  className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary focus:ring focus:ring-primary focus:ring-opacity-50 text-black"
                  required
                />
              </div>
              <div>
                <label htmlFor="status" className="block text-sm font-medium">Status</label>
                <select
                  id="status"
                  name="status"
                  value={job.status}
                  onChange={handleChange}
                  className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary focus:ring focus:ring-primary focus:ring-opacity-50 text-black"
                  required
                >
                  <option value="Scheduled">Scheduled</option>
                  <option value="In Progress">In Progress</option>
                  <option value="Completed">Completed</option>
                  <option value="Cancelled">Cancelled</option>
                </select>
              </div>
              <button
                type="submit"
                className="bg-primary text-white px-4 py-2 rounded-md text-sm font-medium hover:bg-opacity-90"
                disabled={loading}
              >
                {loading ? 'Creating...' : 'Create Job'}
              </button>
            </form>
          </div>

          {/* Most Recent Job */}
          <div className="bg-surface overflow-hidden shadow rounded-lg p-6">
            <h2 className="text-xl font-semibold mb-4">Most Recent Job</h2>
            {loading ? (
              <p>Loading...</p>
            ) : jobs.length > 0 ? (
              <div className="py-4">
                <h3 className="text-lg font-medium">{jobs[jobs.length - 1].title}</h3>
                <p className="text-sm text-gray-500">{jobs[jobs.length - 1].description}</p>
                <p className="text-sm">Customer: {jobs[jobs.length - 1].customer.name}</p>
                <p className="text-sm">Date: {new Date(jobs[jobs.length - 1].scheduledDate).toLocaleDateString()}</p>
                <p className="text-sm">Price: ${jobs[jobs.length - 1].price}</p>
                <p className="text-sm">Status: {jobs[jobs.length - 1].status}</p>
              </div>
            ) : (
              <p>No jobs found.</p>
            )}
          </div>

          <Link to="/manage-jobs" className="mt-4 inline-block text-primary hover:text-primary-dark">
            Manage All Jobs
          </Link>
          <Link to="/dashboard" className="mt-4 inline-block text-primary hover:text-primary-dark">
            Back to Dashboard
          </Link>
        </div>
      </main>
    </div>
  );
};

export default Jobs;
