import React from 'react';
import { PDFDownloadLink, Document, Page, Text, View, StyleSheet } from '@react-pdf/renderer';

const QuotePDF = ({ quote, customerInfo }) => {
  const styles = StyleSheet.create({
    page: { padding: 30 },
    section: { margin: 10, padding: 10, flexGrow: 1 },
    header: { fontSize: 20, marginBottom: 20 },
  });

  return (
    <Document>
      <Page size="A4" style={styles.page}>
        <View style={styles.section}>
          <Text style={styles.header}>Quote for Lawncare Services</Text>
          <Text>Customer Name: {customerInfo.name}</Text>
          <Text>Customer Address: {customerInfo.address}</Text>
          <Text>Base Price: ${quote.basePrice.toFixed(2)}</Text>
          <Text>Additional Price: ${quote.additionalPrice.toFixed(2)}</Text>
          <Text>Total Price: ${quote.totalPrice.toFixed(2)}</Text>
        </View>
      </Page>
    </Document>
  );
};

export const QuoteDownloadLink = ({ quote, customerInfo }) => (
  <PDFDownloadLink
    document={<QuotePDF quote={quote} customerInfo={customerInfo} />}
    fileName="quote.pdf"
  >
    {({ loading }) => (loading ? 'Generating document...' : 'Download PDF')}
  </PDFDownloadLink>
);
