// frontend/src/pages/Profile.js
import React, { useEffect, useState } from 'react';
import axiosInstance from '../utils/axiosInstance';
import { useAuth } from '../contexts/AuthContext';

const Profile = () => {
  const [profile, setProfile] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const { user } = useAuth();

  useEffect(() => {
    const fetchProfile = async () => {
      if (!user) {
        setError('User not authenticated');
        setLoading(false);
        return;
      }

      const token = localStorage.getItem('token');
      if (!token) {
        setError('No authentication token found');
        setLoading(false);
        return;
      }

      try {
        console.log('Fetching profile...');
        const response = await axiosInstance.get('/api/profile');
        console.log('Profile response:', response.data);
        setProfile(response.data);
      } catch (err) {
        console.error('Error fetching profile:', err);
        console.error('Error response:', err.response);
        setError(err.response?.data?.error || 'Failed to load profile.');
      } finally {
        setLoading(false);
      }
    };
    fetchProfile();
  }, [user]);

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-100">
        <p className="text-gray-500">Loading profile...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-100">
        <p className="text-red-500">{error}</p>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-100">
      <header className="bg-white shadow-md p-4">
        <h1 className="text-2xl font-bold">Your Profile</h1>
      </header>
      <main className="max-w-4xl mx-auto p-6">
        <div className="bg-white rounded-lg shadow-md p-6">
          <h2 className="text-xl font-semibold mb-4">Basic Information</h2>
          <p><strong>Name:</strong> {profile.user.name}</p>
          <p><strong>Email:</strong> {profile.user.email}</p>
          <p><strong>Bio:</strong> {profile.bio || 'N/A'}</p>

          <h2 className="text-xl font-semibold mt-6 mb-4">Address</h2>
          <p><strong>Street:</strong> {profile.address.street || 'N/A'}</p>
          <p><strong>City:</strong> {profile.address.city || 'N/A'}</p>
          <p><strong>State:</strong> {profile.address.state || 'N/A'}</p>
          <p><strong>Zip Code:</strong> {profile.address.zipCode || 'N/A'}</p>
        </div>
      </main>
    </div>
  );
};

export default Profile;
