// src/pages/Employee.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Employee = () => {
  const [employees, setEmployees] = useState([]);
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [phone, setPhone] = useState('');
  const [address, setAddress] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    fetchEmployees();
  }, []);

  const fetchEmployees = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/employees', {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setEmployees(response.data);
    } catch (error) {
      console.error('Error fetching employees:', error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/api/employees', {
        name,
        email,
        phone,
        address
      }, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      fetchEmployees(); // Refresh the employee list
      setName('');
      setEmail('');
      setPhone('');
      setAddress('');
    } catch (error) {
      console.error('Error adding employee:', error);
      setError('Failed to add employee. Please try again.');
    }
  };

  const handleDelete = async (employeeId) => {
    if (window.confirm('Are you sure you want to delete this employee?')) {
      try {
        await axios.delete(`http://localhost:5000/api/employees/${employeeId}`, {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        });
        fetchEmployees(); // Refresh the employee list
      } catch (error) {
        console.error('Error deleting employee:', error);
        setError('Failed to delete employee. Please try again.');
      }
    }
  };

  return (
    <div className="min-h-screen bg-background text-text">
      <main className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        <div className="px-4 py-6 sm:px-0">
          <h1 className="text-3xl font-bold mb-6 text-white">Manage Employees</h1>
          {error && <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">{error}</div>}
          
          <form onSubmit={handleSubmit} className="mb-6">
            <div className="mb-4">
              <label htmlFor="name" className="block text-sm font-medium">Name</label>
              <input
                type="text"
                id="name"
                value={name}
                onChange={(e) => setName(e.target.value)}
                className="border rounded p-2 w-full bg-background text-text"
                required
              />
            </div>
            <div className="mb-4">
              <label htmlFor="email" className="block text-sm font-medium">Email</label>
              <input
                type="email"
                id="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                className="border rounded p-2 w-full bg-background text-text"
                required
              />
            </div>
            <div className="mb-4">
              <label htmlFor="phone" className="block text-sm font-medium">Phone</label>
              <input
                type="text"
                id="phone"
                value={phone}
                onChange={(e) => setPhone(e.target.value)}
                className="border rounded p-2 w-full bg-background text-text"
              />
            </div>
            <div className="mb-4">
              <label htmlFor="address" className="block text-sm font-medium">Address</label>
              <input
                type="text"
                id="address"
                value={address}
                onChange={(e) => setAddress(e.target.value)}
                className="border rounded p-2 w-full bg-background text-text"
              />
            </div>
            <div>
              <button
                type="submit"
                className="bg-primary text-white px-4 py-2 rounded hover:bg-opacity-90 transition duration-200"
              >
                Add Employee
              </button>
            </div>
          </form>

          <h2 className="text-2xl font-bold mb-4 text-white">Current Employees</h2>
          <ul className="space-y-4">
            {employees.map(employee => (
              <li key={employee._id} className="bg-gray-800 p-4 rounded shadow">
                <div className="text-white font-medium">{employee.name}</div>
                <div className="text-gray-400">{employee.email}</div>
                <div className="text-gray-400">{employee.phone}</div>
                <div className="text-gray-400">{employee.address}</div>
                <button
                  onClick={() => handleDelete(employee._id)}
                  className="mt-2 bg-red-600 text-white px-4 py-2 rounded hover:bg-red-800 transition duration-200"
                >
                  Delete
                </button>
              </li>
            ))}
          </ul>
        </div>
      </main>
    </div>
  );
};

export default Employee;
