// src/components/RouteManager.js
import React, { useState, useEffect } from 'react';
import { fetchRoutes, deleteRoute, updateRoute } from '../api';

const RouteManager = () => {
  const [routes, setRoutes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    loadRoutes();
  }, []);

  const loadRoutes = async () => {
    try {
      setLoading(true);
      const fetchedRoutes = await fetchRoutes();
      setRoutes(fetchedRoutes);
    } catch (err) {
      setError('Failed to load routes');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const handleDeleteRoute = async (routeId) => {
    try {
      await deleteRoute(routeId);
      setRoutes(routes.filter(route => route._id !== routeId));
    } catch (err) {
      setError('Failed to delete route');
      console.error(err);
    }
  };

  const handleEditRoute = async (routeId, updatedData) => {
    try {
      const updatedRoute = await updateRoute(routeId, updatedData);
      setRoutes(routes.map(route => route._id === routeId ? updatedRoute : route));
    } catch (err) {
      setError('Failed to update route');
      console.error(err);
    }
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div className="route-manager">
      <h2>Route Manager</h2>
      {routes.map(route => (
        <div key={route._id} className="route-item">
          <h3>{route.name}</h3>
          <p>Customers: {route.customers.length}</p>
          <button onClick={() => handleEditRoute(route._id, { name: `${route.name} (edited)` })}>
            Edit
          </button>
          <button onClick={() => handleDeleteRoute(route._id)}>Delete</button>
        </div>
      ))}
    </div>
  );
};

export default RouteManager;
