import React, { useState } from 'react';
import { Droppable, Draggable, DragDropContext } from 'react-beautiful-dnd';
import { saveRoute, deleteRoute } from './api';

const RouteBuilder = ({ customers, setCustomers, routes, setRoutes, setSuccessMessage, handleSaveRoute }) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const customersPerPage = 10;

  const filteredCustomers = customers.filter(customer =>
    customer.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    (customer.address && `${customer.address.street} ${customer.address.city} ${customer.address.state} ${customer.address.zipCode}`
      .toLowerCase().includes(searchTerm.toLowerCase()))
  );

  const indexOfLastCustomer = currentPage * customersPerPage;
  const indexOfFirstCustomer = indexOfLastCustomer - customersPerPage;
  const currentCustomers = filteredCustomers.slice(indexOfFirstCustomer, indexOfLastCustomer);

  const paginate = (pageNumber) => setCurrentPage(pageNumber);

  const addNewRoute = () => {
    const newRouteName = `Route ${routes.length + 1}`;
    if (!routes.some(route => route.name === newRouteName)) {
      setRoutes(prevRoutes => [...prevRoutes, { _id: Date.now().toString(), name: newRouteName, customers: [] }]);
    } else {
      setSuccessMessage('A route with this name already exists.');
    }
  };

  const onDragEnd = (result) => {
    const { source, destination } = result;

    if (!destination) return;

    if (source.droppableId === 'customersList' && destination.droppableId.startsWith('route-')) {
      moveCustomerToRoute(source.index, parseInt(destination.droppableId.split('-')[1]), destination.index);
    } else if (source.droppableId.startsWith('route-') && destination.droppableId === 'customersList') {
      moveCustomerToPool(parseInt(source.droppableId.split('-')[1]), source.index);
    } else if (source.droppableId.startsWith('route-') && destination.droppableId.startsWith('route-')) {
      moveCustomerBetweenRoutes(
        parseInt(source.droppableId.split('-')[1]),
        source.index,
        parseInt(destination.droppableId.split('-')[1]),
        destination.index
      );
    }
  };

  const moveCustomerToRoute = (customerIndex, routeIndex, destinationIndex) => {
    const customer = currentCustomers[customerIndex];
    setRoutes(prevRoutes => {
      const newRoutes = [...prevRoutes];
      if (!newRoutes[routeIndex].customers.some(c => c._id === customer._id)) {
        newRoutes[routeIndex].customers.splice(destinationIndex, 0, customer);
        setCustomers(prevCustomers => prevCustomers.filter(c => c._id !== customer._id));
      }
      return newRoutes;
    });
  };

  const moveCustomerToPool = (routeIndex, customerIndex) => {
    setRoutes(prevRoutes => {
      const newRoutes = [...prevRoutes];
      const [customer] = newRoutes[routeIndex].customers.splice(customerIndex, 1);
      setCustomers(prevCustomers => [...prevCustomers, customer]);
      return newRoutes;
    });
  };

  const moveCustomerBetweenRoutes = (sourceRouteIndex, sourceIndex, destRouteIndex, destIndex) => {
    setRoutes(prevRoutes => {
      const newRoutes = [...prevRoutes];
      const [customer] = newRoutes[sourceRouteIndex].customers.splice(sourceIndex, 1);
      if (!newRoutes[destRouteIndex].customers.some(c => c._id === customer._id)) {
        newRoutes[destRouteIndex].customers.splice(destIndex, 0, customer);
      }
      return newRoutes;
    });
  };

  return (
    <DragDropContext onDragEnd={onDragEnd}>
      <div className="grid grid-cols-3 gap-4">
        <div className="routes-list col-span-2 text-gray-800">
          <h2 className="text-xl text-white font-semibold mb-2">Routes</h2>
          <button onClick={addNewRoute} className="bg-blue-500 text-white px-4 py-2 rounded mb-4 hover:bg-blue-600">
            Add New Route
          </button>
          {routes.map((route, routeIndex) => (
            <Droppable key={route._id} droppableId={`route-${routeIndex}`}>
              {(provided) => (
                <div {...provided.droppableProps} ref={provided.innerRef} className="bg-gray-100 p-4 rounded mb-4 min-h-[150px]">
                  <div className="flex justify-between items-center mb-2">
                    <h3 className="font-semibold">{route.name}</h3>
                    <div>
                      <button
                        onClick={() => handleSaveRoute(route)}
                        className={`px-2 py-1 rounded mr-2 ${
                          route.isSaved
                            ? 'bg-green-500 text-white'
                            : 'bg-blue-500 text-white hover:bg-blue-600'
                        }`}
                      >
                        {route.isSaved ? 'Saved' : 'Save'}
                      </button>
                    </div>
                  </div>
                  {route.customers.map((customer, index) => (
                    <Draggable key={`${route._id}-${customer._id}`} draggableId={`${route._id}-${customer._id}`} index={index}>
                      {(provided) => (
                        <div
                          ref={provided.innerRef}
                          {...provided.draggableProps}
                          {...provided.dragHandleProps}
                          className="bg-white p-2 mb-2 rounded shadow text-gray-800"
                        >
                          {customer.name} - {customer.address?.street}, {customer.address?.city}, {customer.address?.state} {customer.address?.zipCode}
                        </div>
                      )}
                    </Draggable>
                  ))}
                  {provided.placeholder}
                </div>
              )}
            </Droppable>
          ))}
        </div>
        <div className="customers-list text-gray-800">
          <h2 className="text-xl text-white font-semibold mb-2">Customers</h2>
          <input
            type="text"
            placeholder="Search customers..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="w-full p-2 mb-4 rounded"
          />
          <Droppable droppableId="customersList">
            {(provided) => (
              <ul {...provided.droppableProps} ref={provided.innerRef} className="bg-gray-100 p-4 rounded min-h-[300px] max-h-[500px] overflow-auto">
                {currentCustomers.map((customer, index) => (
                  <Draggable key={customer._id} draggableId={customer._id} index={index}>
                    {(provided) => (
                      <li
                        ref={provided.innerRef}
                        {...provided.draggableProps}
                        {...provided.dragHandleProps}
                        className="bg-white p-2 mb-2 rounded shadow"
                      >
                        {customer.name} - {customer.address?.street}, {customer.address?.city}, {customer.address?.state} {customer.address?.zipCode}
                      </li>
                    )}
                  </Draggable>
                ))}
                {provided.placeholder}
              </ul>
            )}
          </Droppable>
          <div className="flex justify-center mt-4">
            {Array.from({ length: Math.ceil(filteredCustomers.length / customersPerPage) }, (_, i) => (
              <button
                key={i}
                onClick={() => paginate(i + 1)}
                className={`mx-1 px-3 py-1 rounded ${currentPage === i + 1 ? 'bg-blue-500 text-white' : 'bg-gray-200'}`}
              >
                {i + 1}
              </button>
            ))}
          </div>
        </div>
      </div>
    </DragDropContext>
  );
};

export default RouteBuilder;
