import React, { useState } from 'react';

const AccountTab = ({ profile, onAccountUpdate, onLogout, ConfirmDialog }) => {
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [isDialogOpen, setIsDialogOpen] = useState(false);

  const handleCancelSubscription = async () => {
    setIsLoading(true);
    setError('');
    setSuccess('');
    try {
      const response = await fetch('/api/subscription/cancel', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          // Add any necessary authentication headers here
        },
      });
      const data = await response.json();
      
      if (!response.ok) {
        throw new Error(data.message || 'Failed to cancel subscription');
      }

      console.log('Subscription cancellation response:', data);
      setSuccess(`Your subscription has been cancelled. You will have access to premium features until ${new Date(data.cancelAt).toLocaleDateString()}.`);
      onAccountUpdate(); // Refresh the profile data
      setIsDialogOpen(false);
    } catch (err) {
      console.error('Error cancelling subscription:', err);
      setError(err.message || 'An error occurred while cancelling the subscription.');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="space-y-6 text-white">
      <h2 className="text-2xl font-bold">Account Details</h2>
      <p><strong>Subscription Tier:</strong> {profile.subscriptionTier}</p>
      <p><strong>Subscription Status:</strong> {profile.subscriptionActive ? 'Active' : 'Inactive'}</p>
      
      {profile.subscriptionActive && !profile.cancellationRequested && (
        <button
          className="bg-red-600 text-white px-4 py-2 rounded-md hover:bg-red-700 disabled:opacity-50"
          onClick={() => setIsDialogOpen(true)}
          disabled={isLoading}
        >
          {isLoading ? 'Processing...' : 'Cancel Subscription'}
        </button>
      )}

      {ConfirmDialog && (
        <ConfirmDialog
          isOpen={isDialogOpen}
          title="Are you sure you want to cancel?"
          message="Your subscription will be cancelled at the end of the current billing cycle. You will continue to have access to premium features until then."
          onConfirm={handleCancelSubscription}
          onCancel={() => setIsDialogOpen(false)}
        />
      )}

      {error && (
        <div className="bg-red-600 text-white p-4 rounded-md">
          <strong>Error:</strong> {error}
        </div>
      )}

      {success && (
        <div className="bg-green-600 text-white p-4 rounded-md">
          <strong>Success:</strong> {success}
        </div>
      )}
      
      <div className="mt-8">
        <h3 className="text-xl font-semibold mb-4">Danger Zone</h3>
        <button 
          onClick={() => {/* Implement account deletion logic */}}
          className="bg-red-600 text-white px-4 py-2 rounded-md hover:bg-red-700"
        >
          Delete Account
        </button>
      </div>
    </div>
  );
};

export default AccountTab;
