import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { QuoteDownloadLink } from './QuotePDF';
import Header from './Header';

const QuoteTool = () => {
  const [type, setType] = useState('lawncare');  // Add this line
  const [area, setArea] = useState('');
  const [options, setOptions] = useState({
    lawncare: {
      frequency: 'bi-weekly',
      terrain: 'flat',
      grassHeight: 'normal',
      obstacles: 'few',
      edging: false,
      fertilizing: false,
      debrisRemoval: false,
      seasonalCleanup: false,
    },
    hedge_trimming: {
      hedgeHeight: 'under6ft',
      hedgeDensity: 'normal',
      shapeComplexity: 'simple',
      accessDifficulty: 'easy',
      debrisVolume: 'low',
    },
    landscaping: {
      designComplexity: 'simple',
      soilQuality: 'good',
      plantDiversity: 'low',
      irrigationNeeds: 'basic',
      hardscapingAmount: 'none',
      siteAccessibility: 'easy',
    },
  });
  const [timeEstimate, setTimeEstimate] = useState(0);
  const [difficulty, setDifficulty] = useState('Easy');
  const [skillRequired, setSkillRequired] = useState('Basic');
  const [quote, setQuote] = useState(null);
  const [customerInfo, setCustomerInfo] = useState({
    name: '',
    address: '',
    phone: '',
    email: '',
  });
  const [savedQuotes, setSavedQuotes] = useState([]);
  const [activeCarousel, setActiveCarousel] = useState('quoting');

  useEffect(() => {
    fetchSavedQuotes();
  }, []);

  const fetchSavedQuotes = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/quotes', {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setSavedQuotes(response.data);
    } catch (error) {
      console.error('Error fetching saved quotes:', error);
    }
  };

  // ... (keep all existing functions)

  const handleStatusChange = async (id, newStatus) => {
    try {
      await axios.patch(`http://localhost:5000/api/quotes/${id}/status`, { status: newStatus }, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      fetchSavedQuotes();
    } catch (error) {
      console.error('Error updating quote status:', error);
    }
  };

  const handleDeleteQuote = async (id) => {
    if (window.confirm('Are you sure you want to delete this quote?')) {
      try {
        await axios.delete(`http://localhost:5000/api/quotes/${id}`, {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        });
        fetchSavedQuotes();
      } catch (error) {
        console.error('Error deleting quote:', error);
      }
    }
  };

  const handleConvertToJob = async (id) => {
    try {
      await axios.post('http://localhost:5000/api/quotes/convert-to-job', { quoteId: id }, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      fetchSavedQuotes();
    } catch (error) {
      console.error('Error converting quote to job:', error);
    }
  };

  return (
    <div className="min-h-screen bg-gray-900">
      <Header />
      <div className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        <div className="px-4 py-6 sm:px-0">
          <div className="bg-gray-800 rounded-lg shadow-xl p-6">
            <h2 className="text-2xl font-semibold text-white mb-4">Quoting Tool</h2>
            
            {/* Carousel Navigation */}
            <div className="flex mb-4 space-x-4">
              <button
                onClick={() => setActiveCarousel('quoting')}
                className={`px-4 py-2 rounded ${activeCarousel === 'quoting' ? 'bg-indigo-600 text-white' : 'bg-gray-700 text-gray-300'}`}
              >
                Create Quote
              </button>
              <button
                onClick={() => setActiveCarousel('managing')}
                className={`px-4 py-2 rounded ${activeCarousel === 'managing' ? 'bg-indigo-600 text-white' : 'bg-gray-700 text-gray-300'}`}
              >
                Manage Quotes
              </button>
            </div>
  
            {activeCarousel === 'quoting' && (
              <div>
                <div className="mb-4">
                  {['lawncare', 'hedge_trimming', 'landscaping'].map(t => (
                    <button
                      key={t}
                      onClick={() => setType(t)}
                      className={`mr-2 px-4 py-2 rounded ${type === t ? 'bg-blue-600 text-white' : 'bg-gray-700 text-gray-300'}`}
                    >
                      {t.replace('_', ' ')}
                    </button>
                  ))}
                </div>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div>
                    <label className="block mb-2 text-white">
                      Area (sq ft):
                      <input
                        type="number"
                        value={area}
                        onChange={e => setArea(e.target.value)}
                        className="mt-1 block w-full rounded-md bg-gray-700 border-transparent focus:border-gray-500 focus:bg-gray-600 focus:ring-0 text-white"
                      />
                    </label>
                    <label className="block mb-2 text-white">
                      Estimated Time (minutes):
                      <input
                        type="number"
                        value={timeEstimate}
                        onChange={e => setTimeEstimate(e.target.value)}
                        className="mt-1 block w-full rounded-md bg-gray-700 border-transparent focus:border-gray-500 focus:bg-gray-600 focus:ring-0 text-white"
                      />
                    </label>
                    <label className="block mb-2">
                      <span className="text-white">Difficulty:</span>
                      <select
                        value={difficulty}
                        onChange={e => setDifficulty(e.target.value)}
                        className="mt-1 block w-full rounded-md bg-gray-700 border-transparent focus:border-gray-500 focus:bg-gray-600 focus:ring-0 text-white"
                      >
                        {difficultyLevels.map(level => (
                          <option key={level} value={level}>{level}</option>
                        ))}
                      </select>
                    </label>
                    <label className="block mb-2">
                      <span className="text-white">Skill Required:</span>
                      <select
                        value={skillRequired}
                        onChange={e => setSkillRequired(e.target.value)}
                        className="mt-1 block w-full rounded-md bg-gray-700 border-transparent focus:border-gray-500 focus:bg-gray-600 focus:ring-0 text-white"
                      >
                        {skillLevels.map(level => (
                          <option key={level} value={level}>{level}</option>
                        ))}
                      </select>
                    </label>
                  </div>
                  <div>
                    {renderOptions()}
                  </div>
                </div>
                <button
                  onClick={handleCalculateQuote}
                  className="mt-4 bg-indigo-600 text-white px-4 py-2 rounded hover:bg-indigo-500"
                >
                  Calculate Quote
                </button>
                {quote && (
                  <div className="mt-6 bg-gray-700 p-4 rounded-lg">
                    <h3 className="text-xl font-semibold text-white mb-4">Quote Result</h3>
                    <div className="grid grid-cols-2 gap-2">
                      <p className="text-white">Base Price: ${quote.basePrice}</p>
                      <p className="text-white">Weighted Price: ${quote.weightedPrice}</p>
                      <p className="text-white">Additional Price: ${quote.additionalPrice}</p>
                      <p className="text-white">Total Price: ${quote.totalPrice}</p>
                      <p className="text-white">Area: {quote.area} sq ft</p>
                      <p className="text-white">Time Estimate: {quote.timeEstimate} minutes</p>
                      <p className="text-white">Difficulty: {quote.difficulty}</p>
                      <p className="text-white">Skill Required: {quote.skillRequired}</p>
                    </div>
                    <h4 className="text-lg font-semibold text-white mt-4 mb-2">Customer Information</h4>
                    <div className="grid grid-cols-2 gap-2">
                      {Object.entries(customerInfo).map(([key, value]) => (
                        <label key={key} className="block mb-2">
                          <span className="text-white">{key.charAt(0).toUpperCase() + key.slice(1)}:</span>
                          <input
                            type="text"
                            value={value}
                            onChange={e => setCustomerInfo({ ...customerInfo, [key]: e.target.value })}
                            className="mt-1 block w-full rounded-md bg-gray-600 border-transparent focus:border-gray-500 focus:bg-gray-500 focus:ring-0 text-white"
                          />
                        </label>
                      ))}
                    </div>
                    <QuoteDownloadLink quote={quote} customerInfo={customerInfo} type={type} options={options[type]} />
                  </div>
                )}
              </div>
            )}
  
            {activeCarousel === 'managing' && (
              <div>
                <h3 className="text-xl font-semibold text-white mb-4">Manage Quotes</h3>
                <div className="space-y-4">
                  {savedQuotes.map((quote) => (
                    <div key={quote._id} className="bg-gray-700 p-4 rounded-lg">
                      <div className="flex justify-between items-center">
                        <div>
                          <p className="text-white font-semibold">{quote.type} - ${quote.totalPrice}</p>
                          <p className="text-gray-300">Created: {new Date(quote.createdAt).toLocaleString()}</p>
                        </div>
                        <div className="flex space-x-2">
                          <select
                            value={quote.status}
                            onChange={(e) => handleStatusChange(quote._id, e.target.value)}
                            className="bg-gray-600 text-white rounded px-2 py-1"
                          >
                            <option value="Pending">Pending</option>
                            <option value="Accepted">Accepted</option>
                            <option value="Rejected">Rejected</option>
                          </select>
                          <button
                            onClick={() => handleConvertToJob(quote._id)}
                            className="bg-green-600 text-white px-3 py-1 rounded hover:bg-green-500"
                            disabled={quote.status === 'Converted'}
                          >
                            Convert to Job
                          </button>
                          <button
                            onClick={() => handleDeleteQuote(quote._id)}
                            className="bg-red-600 text-white px-3 py-1 rounded hover:bg-red-500"
                          >
                            Delete
                          </button>
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
  };
  
  export default QuoteTool;
