import React, { useState, useEffect } from 'react';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import axios from 'axios';
import CustomerMap from '../components/CustomerMap';

const BuildRoutes = () => {
  const [customers, setCustomers] = useState([]);
  const [routes, setRoutes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [activeTab, setActiveTab] = useState('routes');
  const [filteredCustomers, setFilteredCustomers] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const customersPerPage = 10;

  useEffect(() => {
    fetchData();
  }, []);

  useEffect(() => {
    const filtered = customers.filter(customer => 
      customer.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      customer.address?.street.toLowerCase().includes(searchTerm.toLowerCase())
    );
    setFilteredCustomers(filtered);
    setCurrentPage(1);
  }, [customers, searchTerm]);

  const fetchData = async () => {
    setLoading(true);
    setError(null);
    try {
      const [customersResponse, routesResponse] = await Promise.all([
        axios.get('http://localhost:5000/api/customers', {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        }),
        axios.get('http://localhost:5000/api/routes', {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        })
      ]);
      setCustomers(customersResponse.data);
      setRoutes(routesResponse.data);
    } catch (err) {
      console.error('Error fetching data:', err);
      setError('Failed to load data. Please try refreshing the page.');
    } finally {
      setLoading(false);
    }
  };

  const onDragEnd = (result) => {
    const { source, destination } = result;

    if (!destination) return;

    try {
      if (source.droppableId === 'customersList' && destination.droppableId.startsWith('route-')) {
        moveCustomerToRoute(source.index, parseInt(destination.droppableId.split('-')[1]), destination.index);
      } else if (source.droppableId.startsWith('route-') && destination.droppableId === 'customersList') {
        moveCustomerToPool(parseInt(source.droppableId.split('-')[1]), source.index);
      } else if (source.droppableId.startsWith('route-') && destination.droppableId.startsWith('route-')) {
        moveCustomerBetweenRoutes(
          parseInt(source.droppableId.split('-')[1]),
          source.index,
          parseInt(destination.droppableId.split('-')[1]),
          destination.index
        );
      }
    } catch (err) {
      console.error('Error during drag and drop:', err);
      setError('Failed to update routes. Please try again.');
    }
  };

  const moveCustomerToRoute = (customerIndex, routeIndex, destinationIndex) => {
    const customer = customers[customerIndex];
    setCustomers(prevCustomers => prevCustomers.filter((_, index) => index !== customerIndex));
    setRoutes(prevRoutes => {
      const newRoutes = [...prevRoutes];
      newRoutes[routeIndex].customers.splice(destinationIndex, 0, customer);
      return newRoutes;
    });
  };

  const moveCustomerToPool = (routeIndex, customerIndex) => {
    setRoutes(prevRoutes => {
      const newRoutes = [...prevRoutes];
      const [customer] = newRoutes[routeIndex].customers.splice(customerIndex, 1);
      setCustomers(prevCustomers => [...prevCustomers, customer]);
      return newRoutes;
    });
  };

  const moveCustomerBetweenRoutes = (sourceRouteIndex, sourceIndex, destRouteIndex, destIndex) => {
    setRoutes(prevRoutes => {
      const newRoutes = [...prevRoutes];
      const [customer] = newRoutes[sourceRouteIndex].customers.splice(sourceIndex, 1);
      newRoutes[destRouteIndex].customers.splice(destIndex, 0, customer);
      return newRoutes;
    });
  };

  const addNewRoute = () => {
    setRoutes(prevRoutes => [...prevRoutes, { _id: Date.now().toString(), name: `Route ${prevRoutes.length + 1}`, customers: [] }]);
  };

  const deleteRoute = (routeIndex) => {
    setRoutes(prevRoutes => prevRoutes.filter((_, index) => index !== routeIndex));
  };

  const renderRouteBuilder = () => (
    <DragDropContext onDragEnd={onDragEnd}>
      <div className="grid grid-cols-3 gap-4">
        <div className="routes-list col-span-2 text-gray-800">
          <h2 className="text-xl text-white font-semibold mb-2">Routes</h2>
          {routes.map((route, routeIndex) => (
            <Droppable key={route._id} droppableId={`route-${routeIndex}`}>
              {(provided) => (
                <div {...provided.droppableProps} ref={provided.innerRef} className="bg-gray-100 p-4 rounded mb-4 min-h-[150px]">
                  <div className="flex justify-between items-center mb-2">
                    <h3 className="font-semibold">{route.name}</h3>
                    <button onClick={() => deleteRoute(routeIndex)} className="bg-red-500 text-white px-2 py-1 rounded hover:bg-red-600">
                      Delete
                    </button>
                  </div>
                  {route.customers.map((customer, index) => (
                    <Draggable key={`${route._id}-${customer._id}`} draggableId={`${route._id}-${customer._id}`} index={index}>
                      {(provided) => (
                        <div
                          ref={provided.innerRef}
                          {...provided.draggableProps}
                          {...provided.dragHandleProps}
                          className="bg-white p-2 mb-2 rounded shadow text-gray-800"
                        >
                          {customer.name} - {customer.address?.street}
                        </div>
                      )}
                    </Draggable>
                  ))}
                  {provided.placeholder}
                </div>
              )}
            </Droppable>
          ))}
        </div>
        <div className="customers-list text-gray-800">
          <h2 className="text-xl text-white font-semibold mb-2">Customers</h2>
          <Droppable droppableId="customersList">
            {(provided) => (
              <ul {...provided.droppableProps} ref={provided.innerRef} className="bg-gray-100 p-4 rounded min-h-[300px] overflow-auto">
                {customers.map((customer, index) => (
                  <Draggable key={customer._id} draggableId={customer._id} index={index}>
                    {(provided) => (
                      <li
                        ref={provided.innerRef}
                        {...provided.draggableProps}
                        {...provided.dragHandleProps}
                        className="bg-white p-2 mb-2 rounded shadow"
                      >
                        {customer.name} - {customer.address?.street}
                      </li>
                    )}
                  </Draggable>
                ))}
                {provided.placeholder}
              </ul>
            )}
          </Droppable>
        </div>
      </div>
    </DragDropContext>
  );

  const renderMap = () => (
    <div className="map-container h-[600px] w-full">
      <CustomerMap customers={customers} routes={routes} />
    </div>
  );

  const renderCalendar = () => (
    <div className="calendar-container">
      <h2 className="text-xl font-semibold mb-2">Calendar View</h2>
      {/* Implement your calendar component here */}
      <p>Calendar view coming soon...</p>
    </div>
  );

  const renderScheduling = () => (
    <div className="scheduling-container">
      <h2 className="text-xl font-semibold mb-2">Scheduling</h2>
      {/* Implement your scheduling component here */}
      <p>Scheduling view coming soon...</p>
    </div>
  );

  if (loading) return <div className="text-center mt-8">Loading...</div>;
  if (error) return <div className="text-center mt-8 text-red-600">{error}</div>;

  return (
    <div className="min-h-screen bg-background text-gray-800 p-6">
      <h1 className="text-2xl font-bold mb-4">Route Management</h1>
      <div className="mb-4">
        <button onClick={addNewRoute} className="bg-blue-500 text-white px-4 py-2 rounded mr-2 hover:bg-blue-600">
          Add New Route
        </button>
      </div>
      <div className="mb-4">
        <button
          onClick={() => setActiveTab('routes')}
          className={`px-4 py-2 rounded-tl-lg rounded-tr-lg mr-1 ${activeTab === 'routes' ? 'bg-blue-500 text-white' : 'bg-gray-200 text-gray-800'}`}
        >
          Route Builder
        </button>
        <button
          onClick={() => setActiveTab('map')}
          className={`px-4 py-2 rounded-tl-lg rounded-tr-lg mr-1 ${activeTab === 'map' ? 'bg-blue-500 text-white' : 'bg-gray-200 text-gray-800'}`}
        >
          Map View
        </button>
        <button
          onClick={() => setActiveTab('calendar')}
          className={`px-4 py-2 rounded-tl-lg rounded-tr-lg mr-1 ${activeTab === 'calendar' ? 'bg-blue-500 text-white' : 'bg-gray-200 text-gray-800'}`}
        >
          Calendar
        </button>
        <button
          onClick={() => setActiveTab('scheduling')}
          className={`px-4 py-2 rounded-tl-lg rounded-tr-lg ${activeTab === 'scheduling' ? 'bg-blue-500 text-white' : 'bg-gray-200 text-gray-800'}`}
        >
          Scheduling
        </button>
      </div>
      <div className="bg-surface p-4 rounded-b-lg rounded-tr-lg">
        {activeTab === 'routes' && renderRouteBuilder()}
        {activeTab === 'map' && renderMap()}
        {activeTab === 'calendar' && renderCalendar()}
        {activeTab === 'scheduling' && renderScheduling()}
      </div>
    </div>
  );
};

export default BuildRoutes;
