// src/pages/BuildRoutes.js
import React, { useState, useEffect } from 'react';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import axios from 'axios';
import CustomerMap from '../components/CustomerMap';

const BuildRoutes = () => {
  const [customers, setCustomers] = useState([]);
  const [routes, setRoutes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    setLoading(true);
    setError(null);
    try {
      const [customersResponse, routesResponse] = await Promise.all([
        axios.get('http://localhost:5000/api/customers', {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        }),
        axios.get('http://localhost:5000/api/routes', {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        })
      ]);
      setCustomers(customersResponse.data);
      setRoutes(routesResponse.data);
    } catch (err) {
      console.error('Error fetching data:', err);
      setError('Failed to load data. Please try refreshing the page.');
    } finally {
      setLoading(false);
    }
  };

  const onDragEnd = async (result) => {
    const { source, destination } = result;

    if (!destination) return;

    try {
      if (source.droppableId === 'customersList' && destination.droppableId.startsWith('route-')) {
        await moveCustomerToRoute(source.index, parseInt(destination.droppableId.split('-')[1]), destination.index);
      } else if (source.droppableId.startsWith('route-') && destination.droppableId === 'customersList') {
        await moveCustomerToPool(parseInt(source.droppableId.split('-')[1]), source.index);
      } else if (source.droppableId.startsWith('route-') && destination.droppableId.startsWith('route-')) {
        await moveCustomerBetweenRoutes(
          parseInt(source.droppableId.split('-')[1]),
          source.index,
          parseInt(destination.droppableId.split('-')[1]),
          destination.index
        );
      }
    } catch (err) {
      console.error('Error during drag and drop:', err);
      setError('Failed to update routes. Please try again.');
      // Revert to the previous state by re-fetching data
      fetchData();
    }
  };

  const moveCustomerToRoute = async (customerIndex, routeIndex, destinationIndex) => {
    const customer = customers[customerIndex];
    const updatedCustomers = customers.filter((_, index) => index !== customerIndex);
    const updatedRoutes = [...routes];
    updatedRoutes[routeIndex].customers.splice(destinationIndex, 0, customer);

    setCustomers(updatedCustomers);
    setRoutes(updatedRoutes);

    await axios.put(`http://localhost:5000/api/routes/${routes[routeIndex]._id}`, {
      customers: updatedRoutes[routeIndex].customers.map(c => c._id)
    }, {
      headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
    });
  };

  const moveCustomerToPool = async (routeIndex, customerIndex) => {
    const updatedRoutes = [...routes];
    const [customer] = updatedRoutes[routeIndex].customers.splice(customerIndex, 1);
    
    setCustomers([...customers, customer]);
    setRoutes(updatedRoutes);

    await axios.put(`http://localhost:5000/api/routes/${routes[routeIndex]._id}`, {
      customers: updatedRoutes[routeIndex].customers.map(c => c._id)
    }, {
      headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
    });
  };

  const moveCustomerBetweenRoutes = async (sourceRouteIndex, sourceIndex, destRouteIndex, destIndex) => {
    const updatedRoutes = [...routes];
    const [customer] = updatedRoutes[sourceRouteIndex].customers.splice(sourceIndex, 1);
    updatedRoutes[destRouteIndex].customers.splice(destIndex, 0, customer);

    setRoutes(updatedRoutes);

    await Promise.all([
      axios.put(`http://localhost:5000/api/routes/${routes[sourceRouteIndex]._id}`, {
        customers: updatedRoutes[sourceRouteIndex].customers.map(c => c._id)
      }, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      }),
      axios.put(`http://localhost:5000/api/routes/${routes[destRouteIndex]._id}`, {
        customers: updatedRoutes[destRouteIndex].customers.map(c => c._id)
      }, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      })
    ]);
  };

  const addNewRoute = async () => {
    try {
      const response = await axios.post('http://localhost:5000/api/routes', {
        name: `Route ${routes.length + 1}`,
        customers: []
      }, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setRoutes([...routes, response.data]);
    } catch (err) {
      console.error('Error creating new route:', err);
      setError('Failed to create a new route. Please try again.');
    }
  };

  if (loading) return <div className="text-center mt-8">Loading...</div>;
  if (error) return <div className="text-center mt-8 text-red-600">{error}</div>;

  return (
    <div className="build-routes p-6">
      <h1 className="text-2xl font-bold mb-4">Build Routes</h1>
      <button onClick={addNewRoute} className="bg-blue-500 text-white px-4 py-2 rounded mb-4">
        Add New Route
      </button>
      <DragDropContext onDragEnd={onDragEnd}>
        <div className="flex space-x-4">
          <div className="customers-list w-1/4">
            <h2 className="text-xl font-semibold mb-2">Customers</h2>
            <Droppable droppableId="customersList">
              {(provided) => (
                <ul {...provided.droppableProps} ref={provided.innerRef} className="bg-gray-100 p-2 rounded min-h-[200px]">
                  {customers.map((customer, index) => (
                    <Draggable key={customer._id} draggableId={customer._id} index={index}>
                      {(provided) => (
                        <li
                          ref={provided.innerRef}
                          {...provided.draggableProps}
                          {...provided.dragHandleProps}
                          className="bg-white p-2 mb-2 rounded shadow"
                        >
                          {customer.name} - {customer.address?.street}
                        </li>
                      )}
                    </Draggable>
                  ))}
                  {provided.placeholder}
                </ul>
              )}
            </Droppable>
          </div>
          <div className="routes-list flex-1">
            <h2 className="text-xl font-semibold mb-2">Routes</h2>
            {routes.map((route, routeIndex) => (
              <Droppable key={route._id} droppableId={`route-${routeIndex}`}>
                {(provided) => (
                  <div {...provided.droppableProps} ref={provided.innerRef} className="bg-gray-100 p-2 rounded mb-4 min-h-[100px]">
                    <h3 className="font-semibold">{route.name}</h3>
                    {route.customers.map((customer, index) => (
                      <Draggable key={customer._id} draggableId={`route-${routeIndex}-customer-${customer._id}`} index={index}>
                        {(provided) => (
                          <div
                            ref={provided.innerRef}
                            {...provided.draggableProps}
                            {...provided.dragHandleProps}
                            className="bg-white p-2 mb-2 rounded shadow"
                          >
                            {customer.name} - {customer.address?.street}
                          </div>
                        )}
                      </Draggable>
                    ))}
                    {provided.placeholder}
                  </div>
                )}
              </Droppable>
            ))}
          </div>
        </div>
      </DragDropContext>
      <div className="map-container mt-6">
        <h2 className="text-xl font-semibold mb-2">Customer Map</h2>
        <CustomerMap customers={customers} />
      </div>
    </div>
  );
};

export default BuildRoutes;
