// src/components/ProfileTabs/PlanDetails.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';

const PlanDetails = () => {
  const [plans, setPlans] = useState([]);
  const [loadingPlanId, setLoadingPlanId] = useState(null);
  const [message, setMessage] = useState({ type: '', text: '' });

  useEffect(() => {
    const fetchPlans = async () => {
      try {
        const response = await axios.get(`${process.env.REACT_APP_API_URL}/api/payment/prices`);
        // Map Stripe prices to your plan details
        const mappedPlans = response.data.map((price) => ({
          id: price.id,
          name: price.nickname,
          price: price.unit_amount / 100,
          currency: price.currency.toUpperCase(),
          description: price.product.description || 'No description provided.',
        }));
        setPlans(mappedPlans);
      } catch (error) {
        console.error('Error fetching plans:', error);
      }
    };
    fetchPlans();
  }, []);

  const handleSelectPlan = async (planId) => {
    setLoadingPlanId(planId);
    setMessage({ type: '', text: '' });
    try {
      const token = localStorage.getItem('token');
      const response = await axios.post(
        `${process.env.REACT_APP_API_URL}/api/payment/create-checkout-session`,
        { priceId: planId, plan: plans.find((p) => p.id === planId)?.name },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );

      const { sessionId } = response.data;

      // Redirect to Stripe Checkout
      const stripe = window.Stripe(process.env.REACT_APP_STRIPE_PUBLISHABLE_KEY);
      const { error } = await stripe.redirectToCheckout({ sessionId });

      if (error) {
        console.error('Stripe redirect error:', error);
        setMessage({ type: 'error', text: 'Failed to redirect to checkout.' });
      }
    } catch (error) {
      console.error('Error initiating checkout:', error);
      setMessage({ type: 'error', text: 'Failed to initiate checkout.' });
    } finally {
      setLoadingPlanId(null);
    }
  };

  return (
    <div className="space-y-4">
      <h3 className="text-xl font-semibold">Available Plans</h3>
      {message.text && (
        <div
          className={`px-4 py-2 rounded ${
            message.type === 'success' ? 'bg-green-500 text-white' : 'bg-red-500 text-white'
          }`}
        >
          {message.text}
        </div>
      )}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        {plans.map((plan) => (
          <div key={plan.id} className="border rounded-lg p-4 flex flex-col">
            <h4 className="text-lg font-semibold">{plan.name}</h4>
            <p className="text-2xl font-bold">
              ${plan.price} / {plan.currency === 'USD' ? 'month' : plan.currency.toLowerCase()}
            </p>
            <p className="mt-2 text-gray-600">{plan.description}</p>
            <button
              className="mt-auto bg-primary text-white px-4 py-2 rounded-md text-sm font-medium hover:bg-opacity-90"
              onClick={() => handleSelectPlan(plan.id)}
              disabled={loadingPlanId === plan.id}
            >
              {loadingPlanId === plan.id ? 'Processing...' : 'Select Plan'}
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default PlanDetails;
