import axios from 'axios';

const API_URL = 'http://localhost:5000/api';

const getHeaders = () => ({
  headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
});

const handleApiError = (error, fallbackMessage) => {
  if (error.response && error.response.status === 401) {
    // Token has expired or is invalid, redirect to login page
    localStorage.removeItem('token'); // Clear the expired token
    window.location.href = '/login'; // Redirect to login page
  } else {
    console.error(fallbackMessage, error.response?.data || error.message);
  }
  throw error;
};

export const fetchJobs = async () => {
  try {
    const response = await axios.get(`${API_URL}/jobs`, getHeaders());
    return response.data;
  } catch (error) {
    handleApiError(error, 'Error fetching jobs:');
  }
};

export const fetchCustomers = async () => {
  try {
    const response = await axios.get(`${API_URL}/customers`, getHeaders());
    return response.data;
  } catch (error) {
    handleApiError(error, 'Error fetching customers:');
  }
};

export const fetchEmployees = async () => {
  try {
    const response = await axios.get(`${API_URL}/employees`, getHeaders());
    return response.data;
  } catch (error) {
    handleApiError(error, 'Error fetching employees:');
  }
};

export const fetchAnalytics = async (timeRange, customRange = null) => {
  let startDate, endDate;
  
  if (timeRange === 'custom' && customRange) {
    startDate = customRange.start;
    endDate = customRange.end;
  } else {
    const end = new Date();
    const start = new Date();
    start.setDate(end.getDate() - (timeRange === 'all' ? 365 : parseInt(timeRange)));
    startDate = start.toISOString().split('T')[0];
    endDate = end.toISOString().split('T')[0];
  }

  const url = `${API_URL}/analytics?startDate=${startDate}&endDate=${endDate}`;
  try {
    const response = await axios.get(url, getHeaders());
    return response.data;
  } catch (error) {
    handleApiError(error, 'Error fetching analytics:');
  }
};

export const fetchCustomerGrowth = async (startDate, endDate) => {
  const response = await axios.get(`${API_URL}/analytics/customer-growth?startDate=${startDate}&endDate=${endDate}`, getHeaders());
  return response.data;
};

export const fetchRevenue = async (startDate, endDate) => {
  const response = await axios.get(`${API_URL}/analytics/revenue?startDate=${startDate}&endDate=${endDate}`, getHeaders());
  return response.data;
};

export const fetchJobStatistics = async (startDate, endDate) => {
  const response = await axios.get(`${API_URL}/analytics/job-statistics?startDate=${startDate}&endDate=${endDate}`, getHeaders());
  return response.data;
};

export const fetchEmployeePerformance = async (startDate, endDate) => {
  const response = await axios.get(`${API_URL}/analytics/employee-performance?startDate=${startDate}&endDate=${endDate}`, getHeaders());
  return response.data;
};
