// src/api/mockAnalytics.js
export const getMockAnalytics = () => {
    return {
      customerGrowth: {
        labels: ['2024-07-01', '2024-07-02', '2024-07-03', '2024-07-04', '2024-07-05'],
        data: [2, 4, 6, 8, 10]
      },
      revenue: {
        labels: ['2024-07-01', '2024-07-02', '2024-07-03', '2024-07-04', '2024-07-05'],
        data: [100, 200, 300, 400, 500]
      },
      averageJobCost: {
        labels: ['2024-07-01', '2024-07-02', '2024-07-03', '2024-07-04', '2024-07-05'],
        data: [50, 60, 70, 80, 90]
      },
      jobStatusDistribution: {
        labels: ['Completed', 'Pending', 'Cancelled'],
        data: [15, 5, 2]
      },
      topPerformingEmployees: {
        labels: ['Employee 1', 'Employee 2', 'Employee 3'],
        data: [10, 15, 5]
      },
      customerRetention: {
        labels: ['Churn Rate'],
        data: [10]
      },
      jobCancellationRate: {
        labels: ['Cancellation Rate'],
        data: [5]
      },
      jobFrequencyByCustomer: {
        labels: ['Customer 1', 'Customer 2', 'Customer 3'],
        data: [3, 2, 1]
      },
      customerSatisfaction: {
        labels: ['1 Star', '2 Stars', '3 Stars', '4 Stars', '5 Stars'],
        data: [1, 2, 3, 4, 5],
        averageRating: 4.17
      },
      topServices: {
        labels: ['Service 1', 'Service 2', 'Service 3'],
        data: [100, 200, 150]
      },
      employeeDistribution: {
        labels: ['Active', 'Inactive', 'Unassigned'],
        data: [4, 1, 2]
      },
      customerLifetimeValue: {
        labels: ['Customer 1', 'Customer 2', 'Customer 3'],
        data: [500, 700, 600]
      },
      averageLifetimeValue: {
        labels: ['Average Lifetime Value'],
        data: [600]
      },
      jobCompletionRate: {
        labels: ['Completed', 'Pending', 'Cancelled'],
        data: [20, 5, 2]
      }
    };
  };
