// src/components/ProfileTabs/DowngradeSubscription.js
import React, { useState, useEffect } from 'react';
import axiosInstance from '../../utils/axios';

const DowngradeSubscription = () => {
  const [prices, setPrices] = useState([]);
  const [selectedPriceId, setSelectedPriceId] = useState('');
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState({ type: '', text: '' });

  useEffect(() => {
    const fetchPrices = async () => {
      try {
        const response = await axiosInstance.get('/api/payment/prices');
        setPrices(response.data);
      } catch (error) {
        console.error('Error fetching prices:', error);
        setMessage({
          type: 'error',
          text: error.response?.data?.error || 'Failed to fetch prices.',
        });
      }
    };
    fetchPrices();
  }, []);

  const handleDowngrade = async () => {
    if (!selectedPriceId) {
      setMessage({ type: 'error', text: 'Please select a plan to downgrade.' });
      return;
    }

    setLoading(true);
    setMessage({ type: '', text: '' });

    try {
      const response = await axiosInstance.post('/api/payment/downgrade', {
        newPriceId: selectedPriceId,
      });
      setMessage({ type: 'success', text: 'Subscription downgraded successfully!' });
      // Optionally, refresh user data or redirect
    } catch (error) {
      console.error('Error downgrading subscription:', error);
      setMessage({
        type: 'error',
        text: error.response?.data?.error || 'Failed to downgrade subscription.',
      });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="space-y-4">
      <h3 className="text-xl font-semibold">Downgrade Subscription</h3>
      {message.text && (
        <div
          className={`px-4 py-2 rounded ${
            message.type === 'success' ? 'bg-green-500 text-white' : 'bg-red-500 text-white'
          }`}
        >
          {message.text}
        </div>
      )}
      <div>
        <label htmlFor="plan" className="block text-sm font-medium">
          Select a lower-tier plan:
        </label>
        <select
          id="plan"
          value={selectedPriceId}
          onChange={(e) => setSelectedPriceId(e.target.value)}
          className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary focus:ring focus:ring-primary focus:ring-opacity-50"
        >
          <option value="">-- Select a Plan --</option>
          {prices.map((price) => (
            <option key={price.id} value={price.id}>
              {price.nickname} - ${price.unit_amount / 100} {price.currency.toUpperCase()}/month
            </option>
          ))}
        </select>
      </div>
      <button
        onClick={handleDowngrade}
        disabled={loading || !selectedPriceId}
        className="w-full bg-secondary text-white px-4 py-2 rounded-md text-sm font-medium hover:bg-opacity-90"
      >
        {loading ? 'Downgrading...' : 'Downgrade Plan'}
      </button>
    </div>
  );
};

export default DowngradeSubscription;
