import React, { useState } from 'react';
import axios from 'axios';
import InputMask from 'react-input-mask';
import { GoogleMap, LoadScript } from '@react-google-maps/api';
import GooglePlacesAutocomplete from 'react-google-places-autocomplete';
import { PlusIcon, ArrowUpOnSquareIcon, InformationCircleIcon } from '@heroicons/react/24/outline';
import Header from '../components/Header';

const Customers = () => {
  const [customer, setCustomer] = useState({
    name: '',
    email: '',
    phone: '',
    address: {
      street: '',
      city: '',
      state: '',
      zipCode: ''
    },
    yardSize: '',
    notes: '',
    status: 'Active'
  });
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [loading, setLoading] = useState(false);

  const handleAddressChange = (address) => {
    const addressComponents = address.label.split(', ');
    const street = addressComponents[0];
    const city = addressComponents[1];
    const stateFull = addressComponents[2];
    const state = stateAbbreviations[stateFull] || stateFull; // Convert to abbreviation if possible
    const zipCode = addressComponents[3] || '';

    setCustomer((prev) => ({
      ...prev,
      address: {
        street,
        city,
        state,
        zipCode
      }
    }));
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (name.includes('.')) {
      const [parent, child] = name.split('.');
      setCustomer((prev) => ({
        ...prev,
        [parent]: {
          ...prev[parent],
          [child]: value
        }
      }));
    } else {
      setCustomer((prev) => ({ ...prev, [name]: value }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    setLoading(true);

    try {
      await axios.post('http://localhost:5000/api/customers', customer, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setSuccess('Customer added successfully!');
      setCustomer({
        name: '',
        email: '',
        phone: '',
        address: { street: '', city: '', state: '', zipCode: '' },
        yardSize: '',
        notes: '',
        status: 'Active'
      });
    } catch (error) {
      console.error('Error creating customer:', error);
      setError('Failed to add customer. Please try again.');
    }
    setLoading(false);
  };

  return (
    <div className="min-h-screen bg-background text-text">
      <Header />
      <main className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        <LoadScript googleMapsApiKey={process.env.REACT_APP_GOOGLE_MAPS_API_KEY} libraries={['places']}>
          <div className="px-4 py-6 sm:px-0">
            <div className="flex justify-between items-center mb-6">
              <h1 className="text-3xl font-semibold">Add Customers</h1>
            </div>

            {error && <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded relative mb-4">{error}</div>}
            {success && <div className="bg-green-100 border border-green-400 text-green-700 px-4 py-3 rounded relative mb-4">{success}</div>}

            <div className="bg-surface overflow-hidden shadow rounded-lg p-6 mb-6">
              <h2 className="text-xl font-semibold mb-4">Add New Customer</h2>
              <form onSubmit={handleSubmit} className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <label htmlFor="name" className="block text-sm font-medium">Name *</label>
                  <input
                    type="text"
                    id="name"
                    name="name"
                    value={customer.name}
                    onChange={handleChange}
                    className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary focus:ring focus:ring-primary focus:ring-opacity-50"
                    required
                  />
                </div>
                <div>
                  <label htmlFor="email" className="block text-sm font-medium">Email *</label>
                  <input
                    type="email"
                    id="email"
                    name="email"
                    value={customer.email}
                    onChange={handleChange}
                    className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary focus:ring focus:ring-primary focus:ring-opacity-50"
                    required
                  />
                </div>
                <div>
                  <label htmlFor="phone" className="block text-sm font-medium">Phone</label>
                  <InputMask
                    mask="(999) 999-9999"
                    value={customer.phone}
                    onChange={handleChange}
                  >
                    {(inputProps) => (
                      <input
                        {...inputProps}
                        type="tel"
                        id="phone"
                        name="phone"
                        className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary focus:ring focus:ring-primary focus:ring-opacity-50"
                      />
                    )}
                  </InputMask>
                </div>
                <div>
                  <label htmlFor="address" className="block text-sm font-medium">Address *</label>
                  <GooglePlacesAutocomplete
                    selectProps={{
                      value: customer.address.street,
                      onChange: handleAddressChange,
                      placeholder: "Search for an address...",
                    }}
                  />
                </div>
              </form>
            </div>
          </div>
        </LoadScript>
      </main>
    </div>
  );
};

export default Customers;
