// src/components/ProfileTabs/CancelSubscription.js
import React, { useState } from 'react';
import axiosInstance from '../../utils/axiosInstance'; // Use axiosInstance

const CancelSubscription = () => {
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState({ type: '', text: '' });

  const handleCancel = async () => {
    if (!window.confirm('Are you sure you want to cancel your subscription?')) {
      return;
    }

    setLoading(true);
    setMessage({ type: '', text: '' });

    try {
      const token = localStorage.getItem('token');
      const response = await axiosInstance.post(
        `${process.env.REACT_APP_API_URL}/api/payment/cancel`,
        {},
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      setMessage({ type: 'success', text: 'Subscription canceled successfully.' });
      // Optionally, redirect or refresh user data
    } catch (error) {
      console.error('Error canceling subscription:', error);
      setMessage({
        type: 'error',
        text: error.response?.data?.error || 'Failed to cancel subscription.',
      });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="space-y-4">
      <h3 className="text-xl font-semibold">Cancel Subscription</h3>
      {message.text && (
        <div
          className={`px-4 py-2 rounded ${
            message.type === 'success' ? 'bg-green-500 text-white' : 'bg-red-500 text-white'
          }`}
        >
          {message.text}
        </div>
      )}
      <button
        onClick={handleCancel}
        disabled={loading}
        className="w-full bg-red-600 text-white px-4 py-2 rounded-md text-sm font-medium hover:bg-red-700"
      >
        {loading ? 'Cancelling...' : 'Cancel Subscription'}
      </button>
    </div>
  );
};

export default CancelSubscription;
