// components/CustomerMap.js
import React, { useEffect, useRef } from 'react';
import { Loader } from '@googlemaps/js-api-loader';
/* global google */
const CustomerMap = ({ route, onOptimizeRoute }) => {
  const mapRef = useRef(null);

  useEffect(() => {
    console.log('Route passed to CustomerMap:', route);

    const loader = new Loader({
      apiKey: process.env.REACT_APP_GOOGLE_MAPS_API_KEY,
      version: "weekly",
    });

    loader.load().then(() => {
      const map = new google.maps.Map(mapRef.current, {
        center: { lat: 0, lng: 0 },
        zoom: 2,
      });

      const bounds = new google.maps.LatLngBounds();

      route.jobs.forEach((jobItem, index) => {
        const job = jobItem.job;
        console.log('Processing job:', job);
        if (job && job.customer && job.customer.address) {
          const { lat, lng } = job.customer.address;
          if (lat && lng) {
            const position = new google.maps.LatLng(lat, lng);
            const marker = new google.maps.Marker({
              position: position,
              map: map,
              title: `${job.title} - ${job.customer.name}`,
              label: (index + 1).toString(),
            });

            const infoWindow = new google.maps.InfoWindow({
              content: `
                <div>
                  <h3>${job.title}</h3>
                  <p>Customer: ${job.customer.name}</p>
                  <p>Address: ${job.customer.address.street}, ${job.customer.address.city}</p>
                  <p>Status: ${job.status}</p>
                </div>
              `
            });

            marker.addListener('click', () => {
              infoWindow.open(map, marker);
            });

            bounds.extend(position);
          } else {
            console.log('Job missing lat/lng:', job);
          }
        } else {
          console.log('Job missing required data:', job);
        }
      });

      if (!bounds.isEmpty()) {
        map.fitBounds(bounds);
      } else {
        map.setCenter({ lat: 0, lng: 0 });
        map.setZoom(2);
      }
    });
  }, [route]);

  return (
    <div>
      <div ref={mapRef} style={{ width: '100%', height: '400px' }}></div>
      {onOptimizeRoute && (
        <button 
          onClick={() => onOptimizeRoute(route._id)}
          className="mt-2 bg-primary text-white px-4 py-2 rounded hover:bg-opacity-90"
        >
          Optimize Route
        </button>
      )}
    </div>
  );
};
